{
    "openapi": "3.0.3",
    "info": {
        "title": "OrangeHRM Open Source : REST api docs",
        "version": "1.3.0",
        "x-base-path": "/api/v1"
    },
    "servers": [
        {
            "url": "{schema}://{your-orangehrm-host}/{basePath}",
            "variables": {
                "schema": {
                    "enum": [
                        "https",
                        "http"
                    ],
                    "default": "https"
                },
                "your-orangehrm-host": {
                    "default": "your-orangehrm-host"
                },
                "basePath": {
                    "default": "api/v1"
                }
            }
        },
        {
            "url": "{schema}://{your-orangehrm-host}",
            "variables": {
                "schema": {
                    "enum": [
                        "https",
                        "http"
                    ],
                    "default": "https"
                },
                "your-orangehrm-host": {
                    "default": "your-orangehrm-host"
                }
            }
        }
    ],
    "paths": {
        "/employees": {
            "get": {
                "tags": [
                    "User"
                ],
                "summary": "Get Accessible Employees",
                "operationId": "EmployeesApiAction::handleGetRequest",
                "parameters": [
                    {
                        "name": "actionName",
                        "in": "query",
                        "description": "Action name. e.g. assign_leave, view_leave_list",
                        "required": false,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "properties",
                        "in": "query",
                        "description": "Employee properties array. e.g. employeeId, firstName, lastName, termination_id. /api/v1/employees?properties[]=firstName&properties[]=lastName&properties[]=termination_id",
                        "required": false,
                        "schema": {
                            "type": "array",
                            "items": {
                                "type": "string"
                            }
                        }
                    },
                    {
                        "name": "pastEmployee",
                        "in": "query",
                        "description": "Specify whether with past employee. Default false",
                        "required": false,
                        "schema": {
                            "type": "boolean"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Successful operation",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/Employees"
                                }
                            }
                        }
                    },
                    "400": {
                        "description": "No Bound User",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/NoBoundUserError"
                                }
                            }
                        }
                    },
                    "404": {
                        "description": "No Records Found",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/RecordNotFoundException"
                                }
                            }
                        }
                    }
                }
            }
        },
