            "name": "token",
            "schema": {
              "type": "string"
            }
          },
          {
            "description": "The URL to return to after the logout was completed.",
            "in": "query",
            "name": "return_to",
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "204": {
            "$ref": "#/components/responses/emptyResponse"
