{
  "openapi": "3.0.0",
  "info": {
    "contact": {
      "email": "contact@skycoin.net",
      "name": "Skycoin project",
      "url": "https://skycoin.net"
    },
    "description": "Skycoin is a next-generation cryptocurrency.",
    "title": "Skycoin REST API.",
    "version": "0.27.0"
  },
  "paths": {
    "/api/v2/metrics": {
      "get": {
        "responses": {
          "200": {
            "description": "Metrics",
            "content": {
              "text/plain": {
                "schema": {
                  "type": "string"
                }
              }
            }
          }
        }
      }
    },
    "/api/v1/addresscount": {
      "get": {
        "operationId": "addressCount",
        "responses": {
          "200": {
            "description": "This endpoint Returns the total number of unique address that have coins.",
            "$ref": "#/components/responses/addressCountResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "summary": "Returns the total number of unique address that have coins."
      }
    },
    "/api/v1/block": {
      "get": {
        "operationId": "block",
        "parameters": [
          {
            "description": "get block by hash",
            "in": "query",
            "name": "hash",
            "schema": {
              "type": "string"
            }
          },
          {
            "description": "get block by sequence number",
            "in": "query",
            "name": "seq",
            "schema": {
              "type": "integer"
            }
          }
        ],
        "responses": {
          "200": {
            "$ref": "#/components/responses/blockSchemaArray"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "summary": "Returns the balance of one or more addresses, both confirmed and predicted. The predicted balance is the confirmed balance minus the pending spends."
      }
    },
    "/api/v1/blocks": {
      "get": {
        "operationId": "blocks",
        "parameters": [
          {
            "description": "start seq",
            "in": "query",
            "name": "start",
            "schema": {
              "type": "integer"
            }
          },
          {
            "description": "end seq",
            "in": "query",
            "name": "end",
            "schema": {
              "type": "integer"
            }
          },
          {
            "description": "comma-separated list of block seqs",
            "in": "query",
            "name": "seq",
            "schema": {
              "items": {
                "type": "integer"
              },
              "type": "array"
            }
          }
        ],
        "responses": {
          "200": {
            "$ref": "#/components/responses/blocksResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "summary": "Returns the balance of one or more addresses, both confirmed and predicted. The predicted balance is the confirmed balance minus the pending spends."
      }
    },
    "/api/v1/balance": {
      "get": {
        "operationId": "balanceGet",
        "parameters": [
          {
            "description": "command separated list of addresses",
            "in": "query",
            "name": "addrs",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "$ref": "#/components/responses/balanceResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "summary": "Returns the balance of one or more addresses, both confirmed and predicted. The predicted balance is the confirmed balance minus the pending spends."
      },
      "post": {
        "operationId": "balancePost",
        "parameters": [
          {
            "description": "command separated list of addresses",
            "in": "query",
            "name": "addrs",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "$ref": "#/components/responses/balanceResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "security": [
          {
            "csrfAuth": []
          }
        ],
        "summary": "Returns the balance of one or more addresses, both confirmed and predicted. The predicted balance is the confirmed balance minus the pending spends."
      }
    },
    "/api/v1/blockchain/metadata": {
      "get": {
        "operationId": "blockchainMetadata",
        "responses": {
          "200": {
            "$ref": "#/components/responses/blockchainMetadataResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "summary": "Returns the blockchain metadata."
      }
    },
    "/api/v1/blockchain/progress": {
      "get": {
        "operationId": "blockchainProgress",
        "responses": {
          "200": {
            "$ref": "#/components/responses/blockchainProgressResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "summary": "Returns the blockchain sync progress."
      }
    },
    "/api/v1/coinSupply": {
      "get": {
        "description": "coinSupplyHandler returns coin distribution supply stats",
        "operationId": "coinSupply",
        "responses": {
          "200": {
            "$ref": "#/components/responses/coinSupply"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        }
      }
    },
    "/api/v1/csrf": {
      "get": {
        "operationId": "csrf",
        "responses": {
          "200": {
            "$ref": "#/components/responses/csrfResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "summary": "Creates a new CSRF token. Previous CSRF tokens are invalidated by this call."
      }
    },
    "/api/v1/health": {
      "get": {
        "operationId": "health",
        "responses": {
          "200": {
            "$ref": "#/components/responses/healthResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "summary": "Returns node health data."
      }
    },
    "/api/v1/network/connection": {
      "get": {
        "operationId": "networkConnection",
        "parameters": [
          {
            "description": "Address port",
            "in": "query",
            "name": "addr",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "$ref": "#/components/responses/networkConnectionResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "summary": "This endpoint returns a specific connection."
      }
    },
    "/api/v1/network/connection/disconnect": {
      "post": {
        "description": "This endpoint disconnects a connection by ID or address",
        "operationId": "networkConnectionsDisconnect",
        "parameters": [
          {
            "description": "Address id.",
            "in": "query",
            "name": "id",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "This endpoint"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "security": [
          {
            "csrfAuth": []
          }
        ]
      }
    },
    "/api/v1/network/connections": {
      "get": {
        "operationId": "networkConnections",
        "parameters": [
          {
            "description": "Connection status.",
            "in": "query",
            "name": "states",
            "schema": {
              "type": "string",
              "enum": [
                "pending",
                "connected",
                "introduced"
              ]
            }
          },
          {
            "description": "Direction of the connection.",
            "in": "query",
            "name": "direction",
            "schema": {
              "type": "string",
              "enum": [
                "connected",
                "introduced"
              ]
            }
          }
        ],
        "responses": {
          "200": {
            "$ref": "#/components/responses/networkConnectionsResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "security": [
          {
            "csrfAuth": []
          }
        ],
        "summary": "This endpoint returns all outgoings connections."
      }
    },
    "/api/v1/network/connections/exchange": {
      "get": {
        "description": "This endpoint returns all connections found through peer exchange",
        "operationId": "networkConnectionsExchange",
        "responses": {
          "200": {
            "$ref": "#/components/responses/networkConnectionsExchangeResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        }
      }
    },
    "/api/v1/network/connections/trust": {
      "get": {
        "operationId": "networkConnectionsTrust",
        "responses": {
          "200": {
            "$ref": "#/components/responses/networkConnectionsTrustResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "summary": "trustConnectionsHandler returns all trusted connections.\\n They are not necessarily connected to. In the default configuration, these will be a subset of the default hardcoded bootstrap addresses."
      }
    },
    "/api/v1/network/defaultConnections": {
      "get": {
        "operationId": "defaultConnections",
        "responses": {
          "200": {
            "$ref": "#/components/responses/defaultConnectionsResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "summary": "defaultConnectionsHandler returns the list of default hardcoded bootstrap addresses.\\n They are not necessarily connected to."
      }
    },
    "/api/v1/outputs": {
      "get": {
        "operationId": "outputsGet",
        "parameters": [
          {
            "in": "query",
            "name": "address",
            "schema": {
              "type": "array",
              "items": {
                "type": "string"
              }
            }
          },
          {
            "in": "query",
            "name": "hash",
            "schema": {
              "type": "array",
              "items": {
                "type": "string"
              }
            }
          }
        ],
        "responses": {
          "200": {
            "$ref": "#/components/responses/outputsResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "summary": "If neither addrs nor hashes are specificed, return all unspent outputs. If only one filter is specified, then return outputs match the filter. Both filters cannot be specified."
      },
      "post": {
        "operationId": "outputsPost",
        "parameters": [
          {
            "in": "query",
            "name": "address",
            "schema": {
              "type": "string"
            }
          },
          {
            "in": "query",
            "name": "hash",
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "$ref": "#/components/responses/outputsResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "security": [
          {
            "csrfAuth": []
          }
        ],
        "summary": "If neither addrs nor hashes are specificed, return all unspent outputs. If only one filter is specified, then return outputs match the filter. Both filters cannot be specified."
      }
    },
    "/api/v1/resendUnconfirmedTxns": {
      "post": {
        "description": "Broadcasts all unconfirmed transactions from the unconfirmed transaction pool",
        "operationId": "resendUnconfirmedTxns",
        "responses": {
          "200": {
            "$ref": "#/components/responses/resendUnconfirmedTxnsResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "security": [
          {
            "csrfAuth": []
          }
        ]
      }
    },
    "/api/v1/richlist": {
      "get": {
        "operationId": "richlist",
        "parameters": [
          {
            "description": "include distribution addresses or not, default value false",
            "in": "query",
            "name": "include-distribution",
            "schema": {
              "type": "boolean"
            }
          },
          {
            "description": "include distribution addresses or not, default value false",
            "in": "query",
            "name": "n",
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "$ref": "#/components/responses/richlistResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "summary": "Returns the top skycoin holders."
      }
    },
    "/api/v1/transaction": {
      "get": {
        "description": "Returns a transaction identified by its txid hash with just id",
        "operationId": "transaction",
        "parameters": [
          {
            "description": "transaction Id",
            "in": "query",
            "name": "txid",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "$ref": "#/components/responses/transactionResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        }
      }
    },
    "/api/v1/transactions": {
      "get": {
        "operationId": "transactionsGet",
        "parameters": [
          {
            "description": "command separated list of addresses",
            "in": "query",
            "name": "addrs",
            "schema": {
              "type": "string"
            }
          },
          {
            "description": "Whether the transactions should be confirmed [optional, must be 0 or 1; if not provided, returns all]",
            "in": "query",
            "name": "confirmed",
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "$ref": "#/components/responses/transactionsResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "summary": "Returns transactions that match the filters."
      },
      "post": {
        "operationId": "transactionsPost",
        "parameters": [
          {
            "description": "command separated list of addresses",
            "in": "query",
            "name": "addrs",
            "schema": {
              "type": "string"
            }
          },
          {
            "description": "Whether the transactions should be confirmed [optional, must be 0 or 1; if not provided, returns all]",
            "in": "query",
            "name": "confirmed",
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "$ref": "#/components/responses/transactionsResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "security": [
          {
            "csrfAuth": []
          }
        ],
        "summary": "Returns transactions that match the filters."
      }
    },
    "/api/v1/uxout": {
      "get": {
        "operationId": "uxout",
        "parameters": [
          {
            "description": "uxid to filter by",
            "in": "query",
            "name": "uxid",
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "$ref": "#/components/responses/uxoutResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "summary": "Returns an unspent output by ID."
      }
    },
    "/api/v1/version": {
      "get": {
        "description": "versionHandler returns the application version info",
        "operationId": "version",
        "responses": {
          "200": {
            "$ref": "#/components/responses/buildInfo"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        }
      }
    },
    "/api/v1/wallet": {
      "get": {
        "operationId": "wallet",
        "parameters": [
          {
            "description": "tags to filter by",
            "in": "query",
            "name": "id",
            "required": true,
            "x-go-name": "Id",
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "$ref": "#/components/responses/walletResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "summary": "Returns a wallet by id."
      }
    },
    "/api/v1/wallet/balance": {
      "get": {
        "operationId": "walletBalance",
        "parameters": [
          {
            "description": "tags to filter by",
            "in": "query",
            "name": "id",
            "required": true,
            "x-go-name": "Id",
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "$ref": "#/components/responses/walletBalanceResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "summary": "Returns the wallet's balance, both confirmed and predicted.  The predicted balance is the confirmed balance minus the pending spends."
      }
    },
    "/api/v1/wallet/create": {
      "post": {
        "description": "Loads wallet from seed, will scan ahead N address and\nload addresses till the last one that have coins.",
        "operationId": "walletCreate",
        "parameters": [
          {
            "description": "Wallet seed.",
            "in": "header",
            "name": "seed",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "description": "Wallet label.",
            "in": "header",
            "name": "label",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "description": "The number of addresses to scan ahead for balances.",
            "in": "header",
            "name": "scan",
            "schema": {
              "type": "integer",
              "minimum": 1
            }
          },
          {
            "description": "Encrypt wallet.",
            "in": "header",
            "name": "encrypt",
            "schema": {
              "type": "boolean"
            }
          },
          {
            "description": "Wallet Password",
            "in": "header",
            "name": "password",
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "$ref": "#/components/responses/walletCreateResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "security": [
          {
            "csrfAuth": []
          }
        ]
      }
    },
    "/api/v1/wallet/decrypt": {
      "post": {
        "operationId": "walletDecrypt",
        "parameters": [
          {
            "description": "Wallet id.",
            "in": "header",
            "name": "id",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "description": "Wallet password.",
            "in": "header",
            "name": "password",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "$ref": "#/components/responses/walletDecryptResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "security": [
          {
            "csrfAuth": []
          }
        ],
        "summary": "Decrypts wallet."
      }
    },
    "/api/v1/wallet/encrypt": {
      "post": {
        "operationId": "walletEncrypt",
        "parameters": [
          {
            "description": "Wallet id.",
            "in": "header",
            "name": "id",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "description": "Wallet password.",
            "in": "header",
            "name": "password",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "$ref": "#/components/responses/walletEncryptResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "security": [
          {
            "csrfAuth": []
          }
        ],
        "summary": "Encrypt wallet."
      }
    },
    "/api/v1/wallet/newAddress": {
      "post": {
        "description": "Generates new addresses",
        "operationId": "walletNewAddress",
        "parameters": [
          {
            "description": "Wallet Id",
            "in": "query",
            "name": "id",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "description": "The number you want to generate",
            "in": "query",
            "name": "num",
            "schema": {
              "type": "string"
            }
          },
          {
            "description": "Wallet Password",
            "in": "query",
            "name": "password",
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "$ref": "#/components/responses/walletNewAddresses"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "security": [
          {
            "csrfAuth": []
          }
        ]
      }
    },
    "/api/v1/wallet/newSeed": {
      "get": {
        "description": "Returns the wallet directory path",
        "operationId": "walletNewSeed",
        "parameters": [
          {
            "description": "Entropy bitSize.",
            "in": "query",
            "name": "entropy",
            "schema": {
              "type": "string",
              "enum": [
                128,
                256
              ]
            }
          }
        ],
        "responses": {
          "200": {
            "$ref": "#/components/responses/walletNewSeed"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        }
      }
    },
    "/api/v1/wallet/seed": {
      "post": {
        "operationId": "walletSeed",
        "parameters": [
          {
            "description": "Wallet Id.",
            "in": "query",
            "name": "id",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "description": "Wallet password.",
            "in": "query",
            "name": "password",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "$ref": "#/components/responses/walletSeed"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "security": [
          {
            "csrfAuth": []
          }
        ],
        "summary": "This endpoint only works for encrypted wallets. If the wallet is unencrypted, The seed will be not returned."
      }
    },
    "/api/v1/wallet/transaction": {
      "post": {
        "summary": "Creates a signed transaction",
        "operationId": "walletTransaction",
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/walletTransactionRequest"
              }
            }
          },
          "required": true
        },
        "responses": {
          "200": {
            "$ref": "#/components/responses/walletTransactionResponse"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        },
        "security": [
          {
            "csrfAuth": []
          }
        ]
      }
    },
    "/api/v1/wallet/transactions": {
      "get": {
        "operationId": "walletTransactions",
        "parameters": [
          {
            "description": "Wallet Id.",
            "in": "query",
            "name": "id",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "$ref": "#/components/responses/walletTransactionsResponses"
          },
          "default": {
            "$ref": "#/components/responses/genericError"
          }
        }
      }
    },
    "/api/v1/wallet/unload": {
      "post": {
        "operationId": "walletUnload",
