{
	"openapi": "3.0.1",
	"servers": [
		{
			"description": "API para Cadastro de Calendário Contábil - Produtos TOTVS",
			"url": "{{host}}/api/ctb/v1",
			"variables": {
				"serverUrl": {
					"default": "localhost"
				},
				"serverHttpPort": {
					"default": "8080"
				}
			}
		}
	],
	"info": {
		"description": "API para Cadastro de Calendário Contábil - Produtos TOTVS",
		"version": "1.000",
		"title": "Calendário Contábil",
		"contact": {
			"name": "T-Talk",
			"url": "https://api.totvs.com.br",
			"email": "comiteintegracao@totvs.com.br"
		},
		"x-totvs": {
			"messageDocumentation": {
				"name": "Calendário Contábil",
				"description": "Cadastro de Calendário Contábil",
				"segment": "Serviços"
			},
			"productInformation": [
				{
					"product": "Protheus",
					"contact": "squad.control@totvs.com.br",
					"description": "Cadastro de Calendário Contábil",
					"adapter": "CTBI010O.prw"
				}
			]
		}
	},
	"paths": {
		"/AccountingCalendar": {
			"get": {
				"tags": [
					"AccountingCalendar"
				],
				"summary": "Retorna Cadastro do Calendário Contábil",
				"x-totvs": {
					"productInformation": [
						{
							"product": "Protheus",
							"available": true,
							"note": "Este verbo está disponível com os parâmetros Authorization, Order, Page, PageSize e Fields",
							"minimalVersion": "12.1.21"
						}
					]
				},
				"description": "Retorna Cadastro do Calendário Contábil",
				"operationId": "getAccountingCalendar",
				"parameters": [
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Order"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Page"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/PageSize"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Fields"
					}
				],
				"responses": {
					"200": {
						"description": "Operaçãoo realizada com sucesso!",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/AccountingCalendar_1_001.json#/definitions/PagedItems"
								}
							}
						}
					},
					"400": {
						"description": "erro no momento da requisição!",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					},
					"404": {
						"description": "Nenhum Registro encontrado",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			},
			"post": {
				"tags": [
					"AccountingCalendar"
				],
				"summary": "Incluir um Calendário Contábil",
				"description": "Incluir um Calendário Contábil conforme dados enviados na requisição.",
				"operationId": "postAccountingCalendar",
				"x-totvs": {
					"productInformation": [
						{
							"product": "Protheus",
							"available": true,
							"note": "Este verbo esta disponivel com os parametros Authorization e Fields",
							"minimalVersion": "12.1.21"
						}
					]
				},
				"parameters": [
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Fields"
					}
				],
				"requestBody": {
					"content": {
						"application/json": {
							"schema": {
								"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/AccountingCalendar_1_001.json#/definitions/BusinessContentType"
							}
						}
					},
					"description": "Calendário Contábil a ser incluido"
				},
				"responses": {
					"200": {
						"description": "Operação realizada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/AccountingCalendar_1_001.json#/definitions/PagedItems"
								}
							}
						}
					},
					"400": {
						"description": "erro no momento da Inclusão",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			}
		},
		"/AccountingCalendar/{InternalId}": {
			"get": {
				"tags": [
					"InternalId"
				],
				"summary": "Retorna um Calendário Contábil específico de acordo com a chave de pesquisa",
				"description": "Retorna a Calendário Contábil de acordo com o ID de pesquisa",
				"operationId": "getInternalId",
				"x-totvs": {
					"productInformation": [
						{
							"product": "Protheus",
							"available": true,
							"note": "o InternalId é composto por filial + Código do Calendário + Ano de Exercicío + Periodo",
							"minimalVersion": "12.1.21"
						}
					]
				},
				"parameters": [
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Order"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Page"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/PageSize"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Fields"
					},
					{
						"$ref": "#/components/parameters/InternalId"
					}
				],
				"responses": {
					"200": {
						"description": "Operação realizada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/AccountingCalendar_1_001.json#/definitions/BusinessContentType"
								}
							}
						}
					},
					"404": {
						"description": "Calendário Contábil não localizado na base",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			},
			"put": {
				"tags": [
					"InternalId"
				],
				"summary": "Altera um Calendário Contábil",
				"description": "Atualiza o Calendário Contábil conforme dados enviados na requisição",
				"operationId": "putInternalId",
				"x-totvs": {
					"productInformation": [
						{
							"product": "Protheus",
							"available": true,
							"note": "Este verbo esta disponivel com os parametros Authorization, InternalId e Fields",
							"minimalVersion": "12.1.21"
						}
					]
				},
				"parameters": [
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Fields"
					},
					{
						"$ref": "#/components/parameters/InternalId"
					}
				],
				"requestBody": {
					"content": {
						"application/json": {
							"schema": {
								"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/AccountingCalendar_1_001.json#/definitions/BusinessContentType"
							}
						}
					},
					"description": "Calendário a ser alterado"
				},
				"responses": {
					"200": {
						"description": "Operação  realizada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/AccountingCalendar_1_001.json#/definitions/BusinessContentType"
								}
							}
						}
					},
					"400": {
						"description": "erro no momento da Alteração",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					},
					"404": {
						"description": "Calendário Contábil não localizado",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			},
			"delete": {
				"tags": [
					"InternalId"
				],
				"summary": "Exclui um Calendário Contábil",
				"description": "Exclui um Calendário Contábil conforme dados enviados na requisição.",
				"operationId": "deleteInternalId",
				"x-totvs": {
					"productInformation": [
						{
							"product": "Protheus",
							"available": true,
							"note": "Este verbo esta disponível com os parametros Authorization e Fields",
							"minimalVersion": "12.1.21"
						}
					]
				},
				"parameters": [
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					},
					{
						"$ref": "#/components/parameters/InternalId"
					}
				],
				"responses": {
					"200": {
						"description": "Operação realizada com sucesso",
						"content": {
							"application/json": {}
						}
					},
					"400": {
						"description": "erro no momento da exclusão",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					},
					"404": {
						"description": "Calendário Contábil não localizado",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			}
		}
	},
	"components": {
		"parameters": {
			"InternalId": {
				"name": "InternalId",
				"in": "path",
				"description": "Identificador único do Calendário Contábil",
				"required": true,
				"example": "D MG 01 219201901",
				"schema": {
					"type": "string"
				},
				"x-totvs": {
					"productInformation": [
						{
							"product": "Protheus",
							"available": true,
							"note": "Informar os campos Filial (CTG.CTG_FILIAL), código do calendário contábil (CTG.CTG_CALEND), Ano de exercício do calendário (CTG.CTG_EXERC) e o Periodo (CTG.CTG_PERIOD)",
							"minimalVersion": "12.1.21"
						}
					]
				}
			}
		},
		"schemas": {}
	}
}
