{
	"openapi": "3.0.1",
	"servers": [
		{
			"description": "API para a entidade de campos possíveis de pesquisa na Biblioteca",
			"url": "{{host}}/api/educational/v1",
			"variables": {
				"serverUrl": {
					"default": "localhost"
				},
				"serverHttpPort": {
					"default": "8051"
				}
			}
		}
	],
	"info": {
		"description": "API para a entidade de campos possíveis de pesquisa na Biblioteca",
		"version": "1.000",
		"title": "Campos de pesquisa bibliotecária",
		"contact": {
			"name": "T-Talk",
			"url": "API.Totvs.com.br",
			"email": "comiteintegracao@totvs.com.br"
		},
		"x-totvs": {
			"messageDocumentation": {
				"name": "SearchField",
				"description": "Campos passíveis de filtro na pesquisa pública - Biblioteca",
				"segment": "Educacional"
			},
			"productInformation": [
				{
					"product": "RM",
					"contact": "TribeEdu.Squad.EduPortalMobile@totvs.com.br",
					"description": "Campos passíveis de filtro na pesquisa pública - Biblioteca"
				}
			]
		}
	},
	"paths": {
		"/SearchField/{InternalId}": {
			"get": {
				"tags": [
					"SearchField"
				],
				"summary": "Campos de filtro biblioteca",
				"description": "Retorna os filtros possíveis na pesquisa pública parametrizado no Biblioteca",
				"operationId": "getOne",
				"x-totvs": {
					"productInformation": [
						{
							"product": "RM",
							"available": true,
							"note": "Este verbo esta disponível com todos os parâmetros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"parameters": [
					{
						"$ref": "#/components/parameters/InternalId"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Fields"
					}
				],
				"responses": {
					"200": {
						"description": "Operação realizada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/SearchField_1_000.json#/definitions/SearchFieldType"
								}
							}
						}
					},
					"404": {
						"description": "Registro não encontrado",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			}
		}
	},
	"components": {
		"parameters": {
			"InternalId": {
				"name": "InternalId",
				"in": "path",
				"required": true,
				"description": "InternalId da parametrização da Biblioteca",
				"schema": {
					"type": "string"
				}
			}
		},
		"schemas": {}
	}
}
