                  "type": "array",
                  "items": { "type": "string" },
                  "description": "Resource list"
                },
                "tags": {
                  "type": "array",
                  "items": { "type": "string" },
                  "description": "Tag string"
                },
                "assetType": {
                  "type": "array",
                  "items": { "type": "string" },
                  "description": "Asset type"
                },
                "assetName": {
                  "type": "array",
