            "in": "query",
            "name": "groupBy",
            "schema": {
              "type": "string",
              "description": "Aggregate scan results by group of [assetType, assetName, resourceList]",
              "enum": ["assetType", "assetName", "resourceList"]
            }
          },
          {
            "in": "query",
            "name": "page",
            "style": "deepObject",
            "explode": true,
            "description": "Pagination parameters",
            "schema": {
              "type": "object",
              "title": "JsonApiModelPage",
              "properties": {
                "size": { "type": "integer", "example": 10 },
                "number": { "type": "integer", "example": 2 }
