            }
          },
          {
            "in": "query",
            "name": "sort",
            "schema": {
              "description": "Sorting parameters. The sort order is ascending unless the field is prefixed with minus (-), in which case it is descending",
              "type": "string",
              "example": "-assetName"
            }
          }
        ],
