            "description": "OK",
            "content": {
              "application/vnd.api+json": {
                "schema": {
                  "title": "JsonApiModelScansResponse",
                  "type": "object",
                  "required": ["meta", "data"],
                  "properties": {
                    "meta": {
                      "type": "object",
                      "properties": {
                        "pass": {
                          "description": "Total number of passed scans",
                          "type": "integer",
                          "format": "int64",
                          "example": 100
                        },
                        "fail": {
