                          "type": "integer",
                          "format": "int64",
                          "example": 10
                        },
                        "all": {
                          "description": "Total number of scans in the table view",
                          "type": "integer",
                          "format": "int64",
                          "example": 100
                        },
                        "matchedPoliciesSummary": {
                          "$ref": "#/components/schemas/IacMatchedPoliciesSummary"
                        }
                      }
                    },
                    "data": {
                      "type": "array",
                      "items": {
                        "$ref": "#/components/schemas/JsonApiModelScanTableData"
                      }
                    },
                    "links": {
                      "type": "object",
                      "properties": {
                        "self": { "type": "string", "format": "url" },
                        "first": { "type": "string", "format": "url" },
                        "prev": { "type": "string", "format": "url" },
                        "next": { "type": "string", "format": "url" },
                        "last": { "type": "string", "format": "url" }
                      }
                    }
                  }
                }
              }
            }
          },
          "400": { "$ref": "#/components/responses/ErrorResponse" },
          "500": { "$ref": "#/components/responses/ErrorResponse" }
        },
        "x-codeSamples": [
          {
            "lang": "Python + Requests",
            "source": "import requests\n\nurl = \"https://api.prismacloud.io/iac/v2/scans\"\n\nquerystring = {\"filter\":\"SOME_OBJECT_VALUE\",\"groupBy\":\"SOME_STRING_VALUE\",\"page\":\"SOME_OBJECT_VALUE\",\"sort\":\"-assetName\"}\n\nheaders = {\"x-redlock-auth\": \"REPLACE_KEY_VALUE\"}\n\nresponse = requests.request(\"GET\", url, headers=headers, params=querystring)\n\nprint(response.text)"
          },
