                      "AzureDevOps",
                      "AWSCodePipeline",
                      "BitbucketCloud",
                      "BitbucketServer",
                      "CircleCI",
                      "GitHub",
                      "GitLab-CICD",
                      "GitLab-SCM",
                      "IaC-API",
                      "IntelliJ",
                      "Jenkins",
                      "twistcli",
                      "VSCode"
                    ],
                    "example": "AzureDevOps"
                  },
                  "tags": {
                    "description": "Tags assigned to the asset or the scan job. Both tag key and value have 255 character limit. Tags are searchable in Prisma Cloud.",
                    "type": "object",
                    "additionalProperties": { "type": "string" }
                  },
                  "scanAttributes": {
                    "allOf": [
                      {
                        "description": "Any key/value data you want to associate with a scan. Scan attributes are not searchable in Prisma Cloud."
