{
    "openapi": "3.0.0",
    "info": {
        "title": "YetiForce API for Webservice App. Type: Portal",
        "termsOfService": "https://yetiforce.com/",
        "contact": {
            "name": "Devs API Team",
            "url": "https://yetiforce.com/",
            "email": "devs@yetiforce.com"
        },
        "license": {
            "name": "YetiForce Public License v3",
            "url": "https://yetiforce.com/en/yetiforce/license"
        },
        "version": "0.1"
    },
    "paths": {
        "/webservice/Files": {
            "put": {
                "tags": [
                    "BaseAction"
                ],
                "summary": "Download files from the system",
                "operationId": "Api\\Portal\\BaseAction\\Files::put",
                "parameters": [
                    {
                        "name": "X-ENCRYPTED",
                        "in": "header",
                        "required": true,
                        "schema": {
                            "$ref": "#/components/schemas/X-ENCRYPTED"
                        }
                    }
                ],
                "requestBody": {
                    "description": "Action parameters to download the file",
                    "required": true,
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/BaseAction_Files_Request"
                            }
                        },
                        "application/xml": {
                            "schema": {
                                "$ref": "#/components/schemas/BaseAction_Files_Request"
                            }
                        }
                    }
                },
                "responses": {
                    "200": {
                        "description": "File content, mediaType is dynamic depending on the type of file being downloaded",
                        "content": {
                            "application/octet-stream": {
                                "schema": {
                                    "type": "string",
                                    "format": "binary"
                                }
                            }
                        }
                    },
                    "403": {
                        "description": "No permissions",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/Exception"
                                }
                            },
                            "application/xml": {
                                "schema": {
                                    "$ref": "#/components/schemas/Exception"
                                }
                            }
                        }
                    },
                    "404": {
                        "description": "File not found",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/Exception"
                                }
                            },
                            "application/xml": {
                                "schema": {
                                    "$ref": "#/components/schemas/Exception"
                                }
                            }
                        }
                    },
                    "405": {
                        "description": "Invalid method",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/Exception"
                                }
                            },
                            "application/xml": {
                                "schema": {
                                    "$ref": "#/components/schemas/Exception"
                                }
                            }
                        }
                    },
                    "406": {
                        "description": "Not Acceptable",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/Exception"
                                }
                            },
                            "application/xml": {
                                "schema": {
                                    "$ref": "#/components/schemas/Exception"
                                }
                            }
                        }
                    }
                },
                "security": [
                    {
                        "basicAuth": "",
                        "ApiKeyAuth": "",
                        "token": ""
                    }
                ]
            }
        },
        "/webservice/Install": {
            "put": {
                "tags": [
                    "BaseAction"
                ],
                "summary": "Install the system",
                "operationId": "Api\\Portal\\BaseAction\\Install::put",
                "parameters": [
                    {
                        "name": "X-ENCRYPTED",
                        "in": "header",
                        "required": true,
                        "schema": {
                            "$ref": "#/components/schemas/X-ENCRYPTED"
                        }
                    }
                ],
                "requestBody": {
                    "description": "Base action install request body",
                    "required": false
                },
                "responses": {
                    "200": {
                        "description": "Base action details",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/BaseActionInstallResponseBody"
                                }
                            },
                            "application/xml": {
                                "schema": {
                                    "$ref": "#/components/schemas/BaseActionInstallResponseBody"
                                }
                            }
                        }
                    }
                },
                "security": [
                    {
                        "basicAuth": "",
                        "ApiKeyAuth": ""
                    }
                ]
            }
        },
        "/webservice/Menu": {
            "get": {
                "tags": [
                    "BaseAction"
                ],
                "summary": "Get menu into the system",
                "operationId": "Api\\Portal\\BaseAction\\Menu::get",
                "parameters": [
                    {
                        "name": "X-ENCRYPTED",
                        "in": "header",
                        "required": true,
                        "schema": {
                            "$ref": "#/components/schemas/X-ENCRYPTED"
                        }
                    }
                ],
                "requestBody": {
                    "description": "Request body does not occur",
                    "required": false
                },
                "responses": {
                    "200": {
                        "description": "Menu details",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/BaseActionMenuResponseBody"
                                }
                            },
                            "application/xml": {
                                "schema": {
                                    "$ref": "#/components/schemas/BaseActionMenuResponseBody"
                                }
                            }
                        }
                    }
                },
                "security": [
                    {
                        "basicAuth": "",
                        "ApiKeyAuth": "",
                        "token": ""
                    }
                ]
            }
        },
        "/webservice/Modules": {
            "get": {
                "tags": [
                    "BaseAction"
                ],
                "summary": "Get the module list action, along with their translated action",
                "operationId": "Api\\Portal\\BaseAction\\Modules::get",
                "parameters": [
                    {
                        "name": "X-ENCRYPTED",
                        "in": "header",
                        "required": true,
                        "schema": {
                            "$ref": "#/components/schemas/X-ENCRYPTED"
                        }
                    }
                ],
                "requestBody": {
                    "description": "The content of the request is empty",
                    "required": false
                },
                "responses": {
                    "200": {
                        "description": "List of active modules",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/BaseActionModulesResponseBody"
                                }
                            },
                            "application/xml": {
                                "schema": {
                                    "$ref": "#/components/schemas/BaseActionModulesResponseBody"
                                }
                            }
                        }
                    },
                    "401": {
                        "description": "No sent token OR Invalid token"
                    },
                    "403": {
                        "description": "No permissions for module"
                    }
                },
                "security": [
                    {
                        "basicAuth": "",
                        "ApiKeyAuth": "",
                        "token": ""
                    }
                ]
            }
        },
        "/webservice/{moduleName}/Fields": {
            "get": {
                "tags": [
                    "BaseModule"
                ],
                "summary": "Get data about fields",
                "operationId": "Api\\Portal\\BaseModule\\Fields::get",
                "parameters": [
                    {
                        "name": "moduleName",
                        "in": "path",
                        "description": "Module name",
                        "required": true,
                        "schema": {
                            "type": "string"
                        },
                        "example": "Contacts"
                    },
                    {
                        "name": "X-ENCRYPTED",
                        "in": "header",
                        "required": true,
                        "schema": {
                            "$ref": "#/components/schemas/X-ENCRYPTED"
                        }
                    }
                ],
                "requestBody": {
                    "description": "Request body does not occur",
                    "required": false
                },
                "responses": {
                    "200": {
                        "description": "Fields details",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/BaseModule_Fields_ResponseBody"
                                }
                            },
                            "application/xml": {
                                "schema": {
                                    "$ref": "#/components/schemas/BaseModule_Fields_ResponseBody"
                                }
                            }
                        }
                    }
                },
                "security": [
                    {
                        "basicAuth": "",
                        "ApiKeyAuth": "",
                        "token": ""
                    }
                ]
            }
        },
        "/webservice/{moduleName}/Record/{recordId}": {
            "get": {
                "tags": [
                    "BaseModule"
                ],
                "summary": "Get data for the record",
                "operationId": "Api\\Portal\\BaseModule\\Record::get",
                "parameters": [
                    {
                        "name": "moduleName",
                        "in": "path",
                        "description": "Module name",
                        "required": true,
                        "schema": {
                            "type": "string"
                        },
                        "example": "Contacts"
                    },
                    {
                        "name": "recordId",
                        "in": "path",
                        "description": "Record id",
                        "required": true,
                        "schema": {
                            "type": "integer"
                        },
                        "example": 116
                    },
                    {
                        "name": "X-ENCRYPTED",
                        "in": "header",
                        "required": true,
                        "schema": {
                            "$ref": "#/components/schemas/X-ENCRYPTED"
                        }
                    },
                    {
                        "name": "x-raw-data",
                        "in": "header",
                        "description": "Gets raw data",
                        "required": false,
                        "schema": {
                            "type": "integer",
                            "enum": [
                                0,
                                1
                            ]
                        },
                        "example": 1
                    },
                    {
                        "name": "x-parent-id",
                        "in": "header",
                        "description": "Gets parent id",
                        "required": false,
                        "schema": {
                            "type": "integer"
                        }
                    }
                ],
                "requestBody": {
                    "description": "The content of the request is empty.",
                    "required": false
                },
                "responses": {
                    "200": {
                        "description": "Gets data for the record",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/BaseModule_Get_Record_Response"
                                }
                            },
                            "application/xml": {
                                "schema": {
                                    "$ref": "#/components/schemas/BaseModule_Get_Record_Response"
                                }
                            }
                        }
                    },
                    "403": {
                        "description": "No permissions to remove record OR No permissions to view record OR No permissions to edit record"
                    },
                    "404": {
                        "description": "Record doesn't exist"
                    }
                },
                "security": [
                    {
                        "basicAuth": "",
                        "ApiKeyAuth": "",
                        "token": ""
                    }
                ]
            },
            "put": {
                "tags": [
                    "BaseModule"
                ],
                "summary": "Edit record",
                "operationId": "Api\\Portal\\BaseModule\\Record::put",
                "parameters": [
                    {
                        "name": "moduleName",
                        "in": "path",
                        "description": "Module name",
                        "required": true,
                        "schema": {
                            "type": "string"
                        },
                        "example": "Contacts"
                    },
                    {
                        "name": "recordId",
                        "in": "path",
                        "description": "Record id",
                        "required": true,
                        "schema": {
                            "type": "integer"
                        },
                        "example": 116
                    },
                    {
                        "name": "X-ENCRYPTED",
                        "in": "header",
                        "required": true,
                        "schema": {
                            "$ref": "#/components/schemas/X-ENCRYPTED"
                        }
                    }
                ],
                "requestBody": {
                    "description": "Contents of the request contains an associative array with the data record.",
                    "required": true,
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/BaseModule_Record_Request"
                            }
                        },
                        "application/xml": {
                            "schema": {
                                "$ref": "#/components/schemas/BaseModule_Record_Request"
                            }
                        }
                    }
                },
                "responses": {
                    "200": {
                        "description": "Contents of the response contains only id",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/BaseModule_Put_Record_Response"
                                }
                            },
                            "application/xml": {
                                "schema": {
                                    "$ref": "#/components/schemas/BaseModule_Put_Record_Response"
                                }
                            }
                        }
                    }
                },
                "security": [
                    {
                        "basicAuth": "",
                        "ApiKeyAuth": "",
                        "token": ""
                    }
                ]
            },
            "delete": {
                "tags": [
                    "BaseModule"
                ],
                "summary": "Delete record (move to the trash)",
                "operationId": "Api\\Portal\\BaseModule\\Record::delete",
                "parameters": [
                    {
                        "name": "moduleName",
                        "in": "path",
                        "description": "Module name",
                        "required": true,
                        "schema": {
                            "type": "string"
                        },
                        "example": "Contacts"
                    },
                    {
                        "name": "recordId",
                        "in": "path",
                        "description": "Record id",
                        "required": true,
                        "schema": {
                            "type": "integer"
                        },
                        "example": 116
                    },
                    {
                        "name": "X-ENCRYPTED",
                        "in": "header",
                        "required": true,
                        "schema": {
                            "$ref": "#/components/schemas/X-ENCRYPTED"
                        }
                    }
                ],
                "requestBody": {
                    "description": "The content of the request is empty.",
                    "required": false
                },
                "responses": {
                    "200": {
                        "description": "List of records moved to the trash",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/BaseModule_Delete_Record_Response"
                                }
                            },
                            "application/xml": {
                                "schema": {
                                    "$ref": "#/components/schemas/BaseModule_Delete_Record_Response"
                                }
                            }
                        }
                    }
                },
                "security": [
                    {
                        "basicAuth": "",
                        "ApiKeyAuth": "",
                        "token": ""
                    }
                ]
            }
        },
        "/webservice/{moduleName}/Record": {
            "post": {
                "tags": [
                    "BaseModule"
                ],
                "summary": "Create record",
                "operationId": "Api\\Portal\\BaseModule\\Record::post",
                "parameters": [
                    {
                        "name": "moduleName",
                        "in": "path",
                        "description": "Module name",
                        "required": true,
                        "schema": {
                            "type": "string"
                        },
                        "example": "Contacts"
                    },
                    {
                        "name": "X-ENCRYPTED",
                        "in": "header",
                        "required": true,
                        "schema": {
                            "$ref": "#/components/schemas/X-ENCRYPTED"
                        }
                    }
                ],
                "requestBody": {
                    "description": "Contents of the request contains an associative array with the data record.",
                    "required": true,
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/BaseModule_Record_Request"
                            }
                        },
                        "application/xml": {
                            "schema": {
                                "$ref": "#/components/schemas/BaseModule_Record_Request"
                            }
                        }
                    }
                },
                "responses": {
                    "200": {
                        "description": "Contents of the response contains only id",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/BaseModule_Post_Record_Response"
                                }
                            },
                            "application/xml": {
                                "schema": {
                                    "$ref": "#/components/schemas/BaseModule_Post_Record_Response"
                                }
                            }
                        }
                    }
                },
                "security": [
                    {
                        "basicAuth": "",
                        "ApiKeyAuth": "",
                        "token": ""
                    }
                ]
            }
        },
        "/webservice/{moduleName}/RecordHistory/{recordId}": {
            "get": {
                "tags": [
                    "BaseModule"
                ],
                "summary": "Get record history",
                "operationId": "Api\\Portal\\BaseModule\\RecordHistory::get",
                "parameters": [
                    {
                        "name": "moduleName",
                        "in": "path",
                        "description": "Module name",
                        "required": true,
                        "schema": {
                            "type": "string"
                        },
                        "example": "Contacts"
                    },
                    {
                        "name": "recordId",
                        "in": "path",
                        "description": "Record id",
                        "required": true,
                        "schema": {
                            "type": "integer"
                        },
                        "example": 116
                    },
                    {
                        "name": "x-row-limit",
                        "in": "header",
                        "description": "Get rows limit, default: 1000",
                        "required": false,
                        "schema": {
                            "type": "integer"
                        },
                        "example": 1000
                    },
                    {
                        "name": "x-row-offset",
                        "in": "header",
                        "description": "Offset, default: 0",
                        "required": false,
                        "schema": {
                            "type": "integer"
                        },
                        "example": 0
                    },
                    {
                        "name": "x-start-with",
                        "in": "header",
                        "description": "Show history from given ID",
                        "required": false,
                        "schema": {
                            "type": "integer"
                        },
                        "example": 5972
                    },
                    {
                        "name": "X-ENCRYPTED",
                        "in": "header",
                        "required": true,
                        "schema": {
                            "$ref": "#/components/schemas/X-ENCRYPTED"
                        }
                    }
                ],
                "requestBody": {
                    "description": "Request body does not occur",
                    "required": false
                },
                "responses": {
                    "200": {
                        "description": "Recent activities detail",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/BaseModule_RecordHistory_ResponseBody"
                                }
                            },
                            "application/xml": {
                                "schema": {
                                    "$ref": "#/components/schemas/BaseModule_RecordHistory_ResponseBody"
                                }
                            }
                        }
                    }
                },
                "security": [
                    {
                        "basicAuth": "",
                        "ApiKeyAuth": "",
                        "token": ""
                    }
                ]
            }
        },
        "/webservice/{moduleName}/RecordRelatedList/{recordId}/{relatedModuleName}": {
            "get": {
                "tags": [
                    "BaseModule"
                ],
                "summary": "Get the related list of records",
                "operationId": "Api\\Portal\\BaseModule\\RecordRelatedList::get",
                "parameters": [
                    {
                        "name": "moduleName",
                        "in": "path",
                        "description": "Module name",
                        "required": true,
                        "schema": {
                            "type": "string"
                        },
                        "example": "Contacts"
                    },
                    {
                        "name": "recordId",
                        "in": "path",
                        "description": "Record id",
                        "required": true,
                        "schema": {
                            "type": "integer"
                        },
                        "example": 116
                    },
                    {
                        "name": "relatedModuleName",
                        "in": "path",
                        "description": "Related module name",
                        "required": true,
                        "schema": {
                            "type": "string"
                        },
                        "example": "Contacts"
                    },
                    {
                        "name": "x-raw-data",
                        "in": "header",
                        "description": "Get rows limit, default: 0",
                        "required": false,
                        "schema": {
                            "type": "integer",
                            "enum": [
                                0,
                                1
                            ]
                        },
                        "example": 1
                    },
                    {
                        "name": "x-row-limit",
                        "in": "header",
                        "description": "Get rows limit, default: 1000",
                        "required": false,
                        "schema": {
                            "type": "integer"
                        },
                        "example": 1000
                    },
                    {
                        "name": "x-row-offset",
                        "in": "header",
                        "description": "Offset, default: 0",
                        "required": false,
                        "schema": {
                            "type": "integer"
                        },
                        "example": 0
                    },
                    {
                        "name": "x-fields",
                        "in": "header",
                        "description": "JSON array in the list of fields to be returned in response",
                        "required": false,
                        "example": [],
                        "content": {
                            "application/json": {
                                "mediaType": "application/json",
                                "schema": {
                                    "type": "array",
                                    "items": {
                                        "type": "string"
                                    }
                                }
                            }
                        }
                    },
                    {
                        "name": "x-condition",
                        "in": "header",
                        "description": "Conditions [Json format]",
                        "required": false,
                        "content": {
                            "application/json": {
                                "mediaType": "application/json",
                                "schema": {
                                    "description": "Conditions details",
                                    "properties": {
                                        "fieldName": {
                                            "description": "Field name",
                                            "type": "string",
                                            "example": "lastname"
                                        },
                                        "value": {
                                            "description": "Search value",
                                            "type": "string",
                                            "example": "Kowalski"
                                        },
                                        "operator": {
                                            "description": "Field operator",
                                            "type": "string",
                                            "example": "e"
                                        },
                                        "group": {
                                            "description": "Condition group if true is AND",
                                            "type": "boolean",
                                            "example": true
                                        }
                                    },
                                    "type": "object"
                                }
                            }
                        }
                    }
                ],
                "requestBody": {
                    "description": "The content of the request is empty",
                    "required": false
                },
                "responses": {
                    "200": {
                        "description": "List of consents",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/BaseModule_RecordRelatedList_ResponseBody"
                                }
                            },
                            "application/xml": {
                                "schema": {
                                    "$ref": "#/components/schemas/BaseModule_RecordRelatedList_ResponseBody"
                                }
                            }
                        }
                    },
                    "400": {
                        "description": "Incorrect json syntax",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/Exception"
                                }
                            },
                            "application/xml": {
                                "schema": {
                                    "$ref": "#/components/schemas/Exception"
                                }
                            }
                        }
                    },
                    "401": {
                        "description": "No sent token, Invalid token, Token has expired",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/Exception"
                                }
                            },
                            "application/xml": {
                                "schema": {
                                    "$ref": "#/components/schemas/Exception"
                                }
                            }
                        }
                    },
                    "403": {
                        "description": "No permissions for module",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/Exception"
                                }
                            },
                            "application/xml": {
                                "schema": {
                                    "$ref": "#/components/schemas/Exception"
                                }
                            }
                        }
                    },
                    "405": {
                        "description": "Invalid method",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/Exception"
                                }
                            },
                            "application/xml": {
                                "schema": {
                                    "$ref": "#/components/schemas/Exception"
                                }
                            }
                        }
                    }
                },
                "security": [
                    {
                        "basicAuth": "",
                        "ApiKeyAuth": "",
                        "token": ""
                    }
                ]
            }
        },
        "/webservice/{moduleName}/RecordsList": {
            "get": {
                "tags": [
                    "BaseModule"
                ],
                "summary": "Get the list of records",
                "operationId": "Api\\Portal\\BaseModule\\RecordsList::get",
                "parameters": [
                    {
                        "name": "moduleName",
                        "in": "path",
                        "description": "Module name",
                        "required": true,
                        "schema": {
                            "type": "string"
                        },
                        "example": "Contacts"
                    },
                    {
                        "name": "x-raw-data",
                        "in": "header",
                        "description": "Get rows limit, default: 0",
                        "required": false,
                        "schema": {
                            "type": "integer",
                            "enum": [
                                0,
                                1
                            ]
                        },
                        "example": 1
                    },
                    {
                        "name": "x-row-limit",
                        "in": "header",
                        "description": "Get rows limit, default: 1000",
                        "required": false,
                        "schema": {
                            "type": "integer"
                        },
                        "example": 1000
                    },
                    {
                        "name": "x-row-offset",
                        "in": "header",
                        "description": "Offset, default: 0",
