{
	"openapi": "3.0.1",
	"servers": [
		{
			"description": "API para a entidade publicação, (Publication) para produtos TOTVS",
			"url": "{{host}}/api/educational/v1",
			"variables": {
				"serverUrl": {
					"default": "localhost"
				},
				"serverHttpPort": {
					"default": "8051"
				}
			}
		}
	],
	"info": {
		"description": "API para a entidade publicação da biblioteca",
		"version": "1.000",
		"title": "Publicação",
		"contact": {
			"name": "T-Talk",
			"url": "API.Totvs.com.br",
			"email": "comiteintegracao@totvs.com.br"
		},
		"x-totvs": {
			"messageDocumentation": {
				"name": "Publication",
				"description": "Publicações da biblioteca",
				"segment": "Educacional"
			},
			"productInformation": [
				{
					"product": "RM",
					"contact": "TribeEdu.Squad.EduPortalMobile@totvs.com.br",
					"description": "Entidade de publicação biblioteca"
				}
			]
		}
	},
	"paths": {
		"/Publications": {
			"get": {
				"tags": [
					"Publications"
				],
				"summary": "Retorna todas as publicações de acordo com o filtro passado",
				"x-totvs": {
					"productInformation": [
						{
							"product": "RM",
							"available": true,
							"note": "Este verbo esta disponível com todos os parâmetros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"description": "Retorna todas as publicações de acordo com o filtro passado",
				"operationId": "getAll",
				"parameters": [
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Order"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Page"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/PageSize"
					}
				],
				"responses": {
					"200": {
						"description": "Operação realizada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/Publication_1_000.json#/definitions/PagedPublication"
								}
							}
						}
					},
					"400": {
						"description": "erro no momento da listagem das publicações",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			}
		},
		"/Publications/{PublicationInternalId}": {
			"get": {
				"tags": [
					"Publications"
				],
				"summary": "Retorna detalhes da publicação",
				"description": "Retorna detalhes da publicação",
				"operationId": "getId",
				"x-totvs": {
					"productInformation": [
						{
							"product": "RM",
							"available": true,
							"note": "Este verbo esta disponível com todos os parâmetros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"parameters": [
					{
						"$ref": "#/components/parameters/PublicationInternalId"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					}
				],
				"responses": {
					"200": {
						"description": "Operação realizada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/Publication_1_000.json#/definitions/Publication"
								}
							}
						}
					},
					"404": {
						"description": "Publicação não localizada na base",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			}
		}
	},
	"components": {
		"parameters": {
			"PublicationInternalId": {
				"name": "PublicationInternalId",
				"in": "path",
				"required": true,
				"description": "InternalId da publicação",
				"schema": {
					"type": "string"
				}
			}
		},
		"schemas": {}
	}
}
