                          "format": "JPEG",
                          "sequence": false,
                          "height": 500,
                          "width": 500,
                          "geo_location": null,
                          "datetime_original": null,
                          "dpi": [
                            144,
                            144
                          ]
                        },
                        "is_image": true,
                        "is_ready": true,
                        "mime_type": "application/octet-stream",
