    },
    "/files/{uuid}/storage/": {
      "put": {
        "summary": "Store file",
        "description": "Store a single file by UUID.",
        "tags": [
          "File"
        ],
        "operationId": "storeFile",
        "parameters": [
          {
            "in": "header",
            "name": "accept",
            "schema": {
              "description": "Version header.",
              "example": "application/vnd.uploadcare-v0.6+json"
            },
            "required": true
          },
          {
            "in": "path",
            "name": "uuid",
            "description": "File UUID.",
            "required": true,
            "schema": {
              "type": "string",
              "format": "uuid",
              "example": "21975c81-7f57-4c7a-aef9-acfe28779f78"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "File stored. File info in JSON.",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "datetime_removed": {
                      "type": "string",
                      "format": "date-time",
                      "nullable": true,
                      "description": "Date and time when a file was removed, if any."
                    },
                    "datetime_stored": {
                      "type": "string",
                      "format": "date-time",
                      "nullable": true,
                      "description": "Date and time of the last store request, if any."
                    },
                    "datetime_uploaded": {
                      "type": "string",
                      "format": "date-time",
                      "description": "Date and time when a file was uploaded."
                    },
                    "image_info": {
                      "type": "object",
                      "description": "Image metadata.",
                      "nullable": true,
                      "properties": {
                        "color_mode": {
                          "type": "string",
                          "description": "Image color mode.",
                          "enum": [
                            "RGB",
                            "RGBA",
                            "RGBa",
                            "RGBX",
                            "L",
                            "LA",
                            "La",
                            "P",
                            "PA",
                            "CMYK",
                            "YCbCr",
                            "HSV",
                            "LAB"
                          ],
                          "example": "RGBA"
                        },
                        "orientation": {
                          "nullable": true,
                          "type": "integer",
                          "minimum": 0,
                          "maximum": 8,
                          "description": "Image orientation from EXIF.",
                          "example": 6
                        },
                        "format": {
                          "type": "string",
                          "description": "Image format.",
                          "example": "JPEG"
                        },
                        "sequence": {
                          "type": "boolean",
                          "default": "Is image if sequence image(GIF for example).",
                          "example": false
                        },
                        "height": {
                          "type": "integer",
                          "description": "Image height in pixels.",
                          "example": 2352
                        },
                        "width": {
                          "type": "integer",
                          "description": "Image width in pixels.",
                          "example": 2935
                        },
                        "geo_location": {
                          "description": "Geo-location of image from EXIF.",
                          "type": "object",
                          "nullable": true,
                          "properties": {
                            "latitude": {
                              "type": "float",
                              "description": "Location latitude.",
                              "example": -1.1884555555555556
                            },
                            "longitude": {
                              "type": "float",
                              "description": "Location longitude.",
                              "example": 52.66996666666667
                            }
                          }
                        },
                        "datetime_original": {
                          "nullable": true,
                          "type": "string",
                          "description": "Image date and time from EXIF.",
                          "format": "date-time",
                          "example": "2018-09-13T16:23:40.000Z"
                        },
                        "dpi": {
