paths:
  tmp0:
    post:
      description: Enrolls/creates a new computer in JAMF
      parameters:
      - description: The ID of the computer
        example: '1'
        in: body
        name: ID
        required: true
        schema: {type: string}
      - description: Name of the computer
        example: Lauras MacBook Pro
        in: query
        name: Name
        required: false
        schema: {type: string}
      - description: The serial number of the computer
        example: C02Q7KHTGFWF
        in: query
        name: Serial Number
        required: false
        schema: {type: string}
      - description: The unique identifier of the computer
        example: 55900BDC-347C-58B1-D249-F32244B11D30
        in: query
        name: UDID
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp0'}
          description: Successful request
      summary: JAMF Create Computer
  tmp1:
    post:
      description: Creates a new policy in JAMF
      parameters:
      - description: The ID number of the policy
        example: '1'
        in: query
        name: ID
        required: true
        schema: {type: string}
      - description: The name of the policy
        example: Disk Encryption
        in: query
        name: Name
        required: true
        schema: {type: string}
      - description: Whether the policy is enabled
        example: ''
        in: query
        name: Enabled
        required: false
        schema: {type: boolean}
      - description: How often the policy is executed
        example: Once every week
        in: query
        name: Frequency
        required: false
        schema: {type: string}
      - description: Whether the policy is triggered at the recurring check-in frequency
        example: ''
        in: query
        name: Trigger Check-in
        required: false
        schema: {type: boolean}
      - description: Whether the policy is triggered right after a computer is enrolled
        example: ''
        in: query
        name: Trigger Enrollment Complete
        required: false
        schema: {type: boolean}
      - description: Whether the policy is triggered when a user logs into a computer
        example: ''
        in: query
        name: Trigger Login
        required: false
        schema: {type: boolean}
      - description: Whether the policy is triggered when a use logs out of a computer
        example: ''
        in: query
        name: Trigger Logout
        required: false
        schema: {type: boolean}
      - description: Whether the policy is triggered when a computer's network state
          changes
        example: ''
        in: query
        name: Trigger Network State Changed
        required: false
        schema: {type: boolean}
      - description: Whether the policy triggers when a computer starts up
        example: ''
        in: query
        name: Trigger Startup
        required: false
        schema: {type: boolean}
      - description: Another event that causes a policy to trigger
        example: beaconStateChange
        in: query
        name: Trigger Other
        required: false
        schema: {type: string}
      - description: The drive on which to run the policy
        example: /Volumes/Restore/
        in: query
        name: Target Drive
        required: false
        schema: {type: string}
      - description: Network connection to require to run the policy
        example: Ethernet
        in: query
        name: Network Requirements
        required: false
        schema: {type: string}
      - description: Whether the policy is only run for the user assigned to the computer
          in Jamf Pro
        example: ''
        in: query
        name: Location User Only
        required: false
        schema: {type: boolean}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp1'}
          description: Successful request
      summary: JAMF Create Policy
  tmp2:
    post:
      description: Deletes a computer from JAMF
