paths:
  tmp0:
    post:
      description: Gets alerts given a report ID
      parameters:
      - description: Report ID to get alerts from. If -1 it will get all alerts
        example: ''
        in: body
        name: Report ID
        required: true
        schema: {type: string}
      - description: Limit on the number of alerts to return. If -1 it will be unlimited
        example: ''
        in: body
        name: Result Limit
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp0'}
          description: Successful request
      summary: Evident.io Get Alerts from Report
  tmp1:
    post:
      description: Gets Evident Signatures
      parameters:
      - description: Number of signatures to get, maximum. If -1 then all will be
          returned
        example: ''
        in: body
        name: Result Limit
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp1'}
          description: Successful request
      summary: Evident.io Get Signatures
  tmp2:
    post:
      description: Lists information about all external accounts
      parameters: []
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp2'}
          description: Successful request
      summary: Evident.io list all external accounts
  tmp3:
    post:
      description: Searches for alerts with given criteria
      parameters:
      - description: Query to search for. Valid keys 'signature_name_cont, region_id_eq,
          external_account_team_id_in, external_account_id_eq, region_id_in, external_account_team_id_eq,
          signature_identifier_cont, status_in, external_account_id_in, resource_or_tag_cont,
          not_suppressed, cloud_trail_events_present, signature_service_id_in, suppressed,
          risk_level_eq, status_eq'
        example: risk_level_eq:high, not_suppresed:true
        in: query
        name: Query
        required: true
        schema: {type: string}
      - description: Limit on the number of alerts to return. If -1 it will be unlimited
        example: ''
        in: body
        name: Result Limit
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp3'}
          description: Successful request
      summary: Evident.io Search Alerts from Report
