info:
  contact: {email: frikky@shuffler.io, name: '@frikkylikeme', url: 'https://twitter.com/frikkylikeme'}
  description: Automated generation of Fortinet Fortisiem
  title: Fortinet Fortisiem
  version: 1.0.0
  x-logo: data:image/png;base64,
openapi: 3.0.2
paths:
  tmp0:
    post:
      description: Pull devices from FortiSIEM
      parameters: []
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp0'}
          description: Successful request
      summary: Fortinet FortiSIEM Get Devices
  tmp1:
    post:
      description: Pull in incidents and event data from FortiSIEM
      parameters:
      - description: Query for incidents in the last X minutes
        example: ''
        in: query
        name: Minutes Ago
        required: false
        schema: {type: string}
      - description: Timeout for task (in minutes). Default to 10
        example: ''
        in: body
        name: Timeout (Minutes)
        required: false
        schema: {type: string}
      - description: Gets only Query ID, if set to 'False' the task will complete
          the final request automatically. Defaults to 'True'
        example: ''
        in: query
        name: Query ID
        required: false
        schema: {type: boolean}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp1'}
          description: Successful request
      summary: Fortinet FortiSIEM Get Incidents/Events
  tmp2:
    post:
      description: Used for querying all incidents based on an incomplete polling
        of incidents from the Query ID returned
      parameters:
      - description: Query ID, obtained from 'Get Incidents/Events' timeout and used
          to finish query for incidents.
        example: ''
        in: query
        name: Query ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp2'}
          description: Successful request
      summary: Fortinet FortiSIEM Get Incidents/Events by Query ID
  tmp3:
    post:
      description: Run XML Query in FortiSIEM
      parameters:
      - description: XML File attachment or string to send to FortiSiem as a query.
        example: ''
        in: query
        name: XML File to send
        required: false
        schema: {type: string}
      - description: 'The XML may contain {key} style flags that will be patched in,
          IE, if the xml has a tag {ip} for an ip address, this field should have
          value `{''ip'': ''1.2.3.4''}`'
        example: ''
        in: query
        name: Object of dynamic parameters to pass to the XML
        required: false
        schema: {type: string}
      - description: Just return raw JSON,defaults to false.
        example: ''
        in: query
        name: Return Raw JSON instead
        required: false
        schema: {type: boolean}
      - description: Gets only Query ID, if set to 'False' the task will complete
          the final request automatically. Defaults to 'True'
        example: ''
        in: query
        name: Query ID
        required: false
        schema: {type: boolean}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp3'}
          description: Successful request
      summary: Fortinet FortiSIEM Generic XML Query
tags:
- {name: SIEM & Log Management}
