{
	"openapi": "3.0.3",
	"info": {
		"title": "CDR Energy API",
		"description": "Consumer Data Right end points and payloads for the Energy sector",
		"version": "1.16.0"
	},
	"components": {
		"schemas": {
            "EnergyPlanListResponse": {
                "type": "object",
                "required": [
                    "data",
                    "links",
                    "meta"
                ],
                "properties": {
                    "data": {
                        "type": "object",
                        "required": [
                            "plans"
                        ],
                        "properties": {
                            "plans": {
                                "description": "Array of plans",
                                "type": "array",
                                "items": {
                                    "$ref": "#/components/schemas/EnergyPlan"
                                }
                            }
                        }
                    },
                    "links": {
                        "$ref": "#/components/schemas/LinksPaginated"
                    },
                    "meta": {
                        "$ref": "#/components/schemas/MetaPaginated"
                    }
                }
            },
            "EnergyPlanResponse": {
                "type": "object",
                "required": [
                    "data",
                    "links",
                    "meta"
                ],
                "properties": {
                    "data": {
                        "$ref": "#/components/schemas/EnergyPlanDetail"
                    },
                    "links": {
                        "$ref": "#/components/schemas/Links"
                    },
                    "meta": {
                        "$ref": "#/components/schemas/Meta"
                    }
                }
            },
            "EnergyServicePointListResponse": {
                "type": "object",
                "required": [
                    "data",
                    "links",
                    "meta"
                ],
                "properties": {
                    "data": {
                        "type": "object",
                        "required": [
                            "servicePoints"
                        ],
                        "properties": {
                            "servicePoints": {
                                "type": "array",
                                "items": {
                                    "$ref": "#/components/schemas/EnergyServicePoint"
                                }
                            }
                        }
                    },
                    "links": {
                        "$ref": "#/components/schemas/LinksPaginated"
                    },
                    "meta": {
                        "$ref": "#/components/schemas/MetaPaginated"
                    }
                }
            },
            "EnergyServicePointDetailResponse": {
                "type": "object",
                "required": [
                    "data",
                    "links",
                    "meta"
                ],
                "properties": {
                    "data": {
                        "$ref": "#/components/schemas/EnergyServicePointDetail"
                    },
                    "links": {
                        "$ref": "#/components/schemas/Links"
                    },
                    "meta": {
                        "$ref": "#/components/schemas/Meta"
                    }
                }
            },
            "EnergyUsageListResponse": {
                "type": "object",
                "required": [
                    "data",
                    "links",
                    "meta"
                ],
                "properties": {
                    "data": {
                        "type": "object",
                        "required": [
                            "reads"
                        ],
                        "properties": {
                            "reads": {
                                "description": "Array of meter reads",
                                "type": "array",
                                "items": {
                                    "$ref": "#/components/schemas/EnergyUsageRead"
                                }
                            }
                        }
                    },
                    "links": {
                        "$ref": "#/components/schemas/LinksPaginated"
                    },
                    "meta": {
                        "$ref": "#/components/schemas/MetaPaginated"
                    }
                }
            },
            "EnergyDerListResponse": {
                "type": "object",
                "required": [
                    "data",
                    "links",
                    "meta"
                ],
                "properties": {
                    "data": {
                        "type": "object",
                        "required": [
                            "derRecords"
                        ],
                        "properties": {
                            "derRecords": {
                                "description": "Array of meter reads",
                                "type": "array",
                                "items": {
                                    "$ref": "#/components/schemas/EnergyDerRecord"
                                }
                            }
                        }
                    },
                    "links": {
                        "$ref": "#/components/schemas/LinksPaginated"
                    },
                    "meta": {
                        "$ref": "#/components/schemas/MetaPaginated"
                    }
                }
            },
            "EnergyDerDetailResponse": {
                "type": "object",
                "required": [
                    "data",
                    "links",
                    "meta"
                ],
                "properties": {
                    "data": {
                        "$ref": "#/components/schemas/EnergyDerRecord"
                    },
                    "links": {
                        "$ref": "#/components/schemas/Links"
                    },
                    "meta": {
                        "$ref": "#/components/schemas/Meta"
                    }
                }
            },
            "EnergyAccountListResponse": {
                "type": "object",
                "required": [
                    "data",
                    "links",
                    "meta"
                ],
                "properties": {
                    "data": {
                        "type": "object",
                        "required": [
                            "accounts"
                        ],
                        "properties": {
                            "accounts": {
                                "description": "Array of accounts",
                                "type": "array",
                                "items": {
                                    "$ref": "#/components/schemas/EnergyAccount"
                                }
                            }
                        }
                    },
                    "links": {
                        "$ref": "#/components/schemas/LinksPaginated"
                    },
                    "meta": {
                        "$ref": "#/components/schemas/MetaPaginated"
                    }
                }
            },
            "EnergyAccountDetailResponse": {
                "type": "object",
                "required": [
                    "data",
                    "links",
                    "meta"
                ],
                "properties": {
                    "data": {
                        "$ref": "#/components/schemas/EnergyAccountDetail"
                    },
                    "links": {
                        "$ref": "#/components/schemas/Links"
                    },
                    "meta": {
                        "$ref": "#/components/schemas/Meta"
                    }
                }
            },
            "EnergyPaymentScheduleResponse": {
                "type": "object",
                "required": [
                    "data",
                    "links",
                    "meta"
                ],
                "properties": {
                    "data": {
                        "$ref": "#/components/schemas/EnergyPaymentSchedule"
                    },
                    "links": {
                        "$ref": "#/components/schemas/Links"
                    },
                    "meta": {
                        "$ref": "#/components/schemas/Meta"
                    }
                }
            },
            "EnergyConcessionsResponse": {
                "type": "object",
                "required": [
                    "data",
                    "links",
                    "meta"
                ],
                "properties": {
                    "data": {
                        "type": "object",
                        "required": [
                            "concessions"
                        ],
                        "properties": {
                            "concessions": {
                                "description": "Array may be empty if no concessions exist",
                                "type": "array",
                                "items": {
                                    "$ref": "#/components/schemas/EnergyConcession"
                                }
                            }
                        }
                    },
                    "links": {
                        "$ref": "#/components/schemas/Links"
                    },
                    "meta": {
                        "$ref": "#/components/schemas/Meta"
                    }
                }
            },
            "EnergyBalanceListResponse": {
                "type": "object",
                "required": [
                    "data",
                    "links",
                    "meta"
                ],
                "properties": {
                    "data": {
                        "type": "object",
                        "required": [
                            "balances"
                        ],
                        "properties": {
                            "balances": {
                                "description": "Array of account balances",
                                "type": "array",
                                "items": {
                                    "type": "object",
                                    "required": [
                                        "accountId",
                                        "balance"
                                    ],
                                    "properties": {
                                        "accountId": {
                                            "description": "The ID of the account",
                                            "type": "string"
                                        },
                                        "balance": {
                                            "description": "The current balance of the account.  A positive value indicates that amount is owing to be paid.  A negative value indicates that the account is in credit",
                                            "type": "string",
                                            "x-cds-type": "AmountString"
                                        }
                                    }
                                }
                            }
                        }
                    },
                    "links": {
                        "$ref": "#/components/schemas/LinksPaginated"
                    },
                    "meta": {
                        "$ref": "#/components/schemas/MetaPaginated"
                    }
                }
            },
            "EnergyBalanceResponse": {
                "type": "object",
                "required": [
                    "data",
                    "links",
                    "meta"
                ],
                "properties": {
                    "data": {
                        "type": "object",
                        "required": [
                            "balance"
                        ],
                        "properties": {
                            "balance": {
                                "description": "The current balance of the account.  A positive value indicates that amount is owing to be paid.  A negative value indicates that the account is in credit",
                                "type": "string",
                                "x-cds-type": "AmountString"
                            }
                        }

                    },
                    "links": {
                        "$ref": "#/components/schemas/Links"
                    },
                    "meta": {
                        "$ref": "#/components/schemas/Meta"
                    }
                }
            },
            "EnergyInvoiceListResponse": {
                "type": "object",
                "required": [
                    "data",
                    "links",
                    "meta"
                ],
                "properties": {
                    "data": {
                        "type": "object",
                        "required": [
                            "invoices"
                        ],
                        "properties": {
                            "invoices": {
                                "description": "Array of invoices sorted by date in descending order",
                                "type": "array",
                                "items": {
                                    "$ref": "#/components/schemas/EnergyInvoice"
                                }
                            }
                        }
                    },
                    "links": {
                        "$ref": "#/components/schemas/LinksPaginated"
                    },
                    "meta": {
                        "$ref": "#/components/schemas/MetaPaginated"
                    }
                }
            },
            "EnergyBillingListResponse": {
                "type": "object",
                "required": [
                    "data",
                    "links",
                    "meta"
                ],
                "properties": {
                    "data": {
                        "type": "object",
                        "required": [
                            "transactions"
                        ],
                        "properties": {
                            "transactions": {
                                "description": "Array of transactions sorted by date and time in descending order",
                                "type": "array",
                                "items": {
                                    "$ref": "#/components/schemas/EnergyBillingTransaction"
                                }
                            }
                        }
                    },
                    "links": {
                        "$ref": "#/components/schemas/LinksPaginated"
                    },
                    "meta": {
                        "$ref": "#/components/schemas/MetaPaginated"
                    }
                }
            },
            "ErrorListResponse": {
                "type": "object",
                "required": [
                    "errors"
                ],
                "x-conditional": [ "meta" ],
                "properties": {
                    "errors": {
                        "type": "array",
                        "items": {
                            "type": "object",
                            "required": [
                                "code",
                                "title",
                                "detail"
                            ],
                            "properties": {
                                "code": {
                                    "type": "string",
                                    "description": "The code of the error encountered. Where the error is specific to the respondent, an application-specific error code, expressed as a string value. If the error is application-specific, the URN code that the specific error extends must be provided in the meta object. Otherwise, the value is the error code URN."
                                },
                                "title": {
                                    "type": "string",
                                    "description": "A short, human-readable summary of the problem that MUST NOT change from occurrence to occurrence of the problem represented by the error code."
                                },
                                "detail": {
                                    "type": "string",
                                    "description": "A human-readable explanation specific to this occurrence of the problem."
                                },
                                "meta": {
                                    "type" : "object",
                                    "x-conditional" : [ "urn" ],
                                    "description" : "Additional data for customised error codes",
                                    "properties" : {
                                       "urn": {
                                          "type": "string",
                                          "description": "The CDR error code URN which the application-specific error code extends. Mandatory if the error `code` is an application-specific error rather than a standardised error code."
                                       }
                                    }
                                }
                            }
                        }
                    }
                }
            },
            "EnergyPlan": {
                "type": "object",
                "required": [
                    "planId",
                    "lastUpdated",
                    "type",
                    "fuelType",
                    "brand",
                    "brandName"
                ],
                "properties": {
                    "planId": {
                        "type": "string",
                        "x-cds-type": "ASCIIString",
                        "description": "The ID of the specific plan"
                    },
                    "effectiveFrom": {
                        "type": "string",
                        "x-cds-type": "DateTimeString",
                        "description": "The date and time from which this plan is effective (ie. is available for origination). Used to enable the articulation of products to the regime before they are available for customers to originate"
                    },
                    "effectiveTo": {
                        "type": "string",
                        "x-cds-type": "DateTimeString",
                        "description": "The date and time at which this plan will be retired and will no longer be offered. Used to enable the managed deprecation of plans"
                    },
                    "lastUpdated": {
                        "type": "string",
                        "x-cds-type": "DateTimeString",
                        "description": "The last date and time that the information for this plan was changed (or the creation date for the plan if it has never been altered)"
                    },
                    "displayName": {
                        "type": "string",
                        "description": "The display name of the plan"
                    },
                    "description": {
                        "type": "string",
                        "description": "A description of the plan"
                    },
                    "type": {
                        "type": "string",
                        "description": "The type of the plan",
                        "enum": ["STANDING",
                            "MARKET",
                            "REGULATED"
                        ]
                    },
                    "fuelType": {
                        "type": "string",
                        "description": "The fuel types covered by the plan",
                        "enum": [
                            "ELECTRICITY",
                            "GAS",
                            "DUAL"
                        ]
                    },
                    "brand": {
                        "type": "string",
                        "x-cds-type": "ASCIIString",
                        "description": "The ID of the brand under which this plan is offered"
                    },
                    "brandName": {
                        "type": "string",
                        "description": "The display name of the brand under which this plan is offered"
                    },
                    "applicationUri": {
                        "type": "string",
                        "x-cds-type": "URIString",
                        "description": "A link to an application web page where this plan can be applied for"
                    },
                    "additionalInformation": {
                        "type": "object",
                        "description": "Object that contains links to additional information on specific topics",
                        "properties": {
                            "overviewUri": {
                                "type": "string",
                                "x-cds-type": "URIString",
                                "description": "A link to a general overview of the plan"
                            },
                            "termsUri": {
                                "type": "string",
                                "x-cds-type": "URIString",
                                "description": "A link to terms and conditions for the plan"
                            },
                            "eligibilityUri": {
                                "type": "string",
                                "x-cds-type": "URIString",
                                "description": "A link to detail on eligibility criteria for the plan"
                            },
                            "pricingUri": {
                                "type": "string",
                                "x-cds-type": "URIString",
                                "description": "A link to detail on pricing for the plan"
                            },
                            "bundleUri": {
                                "type": "string",
                                "x-cds-type": "URIString",
                                "description": "A link to detail on bundles that this plan can be a part of"
                            }
                        }
                    },
                    "customerType": {
                        "type": "string",
                        "description": "The type of customer that the plan is offered to.  If absent then the plan is available to all customers",
                        "enum": ["RESIDENTIAL",
                            "BUSINESS"
                        ]
                    },
                    "geography": {
                        "type": "object",
                        "description": "Describes the geographical area that the plan is available for.  If absent then it is assumed the plan is not geographically limited",
                        "properties": {
                            "excludedPostcodes": {
                                "description": "Array of valid Australian post codes that are specifically excluded from the plan.  Each element is a single four digit postcode (e.g. 3000) or a range of postcodes defined by two four digit postcodes and a hyphen (e.g. 3000-3999)",
                                "type": "array",
                                "items": {
                                    "type": "string"
                                }
                            },
                            "includedPostcodes": {
                                "description": "Array of valid Australian post codes that are included from the plan.  If absent defaults to all non-excluded post codes.  Each element is a single four digit postcode (e.g. 3000) or a range of postcodes defined by two four digit postcodes and a hyphen (e.g. 3000-3999)",
                                "type": "array",
                                "items": {
                                    "type": "string"
                                }
                            }
                        }
                    }
                }
            },
            "EnergyPlanDetail": {
                "allOf": [
                    {
                        "$ref": "#/components/schemas/EnergyPlan"
                    },
                    {
                        "type": "object",
                        "x-conditional": [
                            "gasContract",
                            "electricityContract"
                        ],
                        "properties": {
                            "meteringCharges": {
                                "description": "Charges for metering included in the plan",
                                "type": "array",
                                "items": {
                                    "type": "object",
                                    "required": ["displayName", "minimumValue"],
                                    "properties": {
                                        "displayName": {
                                            "type": "string",
                                            "description": "Display name of the charge"
                                        },
                                        "description": {
                                            "type": "string",
                                            "description": "Description of the charge"
                                        },
                                        "minimumValue": {
                                            "type": "string",
                                            "x-cds-type": "AmountString",
                                            "description": "Minimum value of the charge if the charge is a range or the absolute value of the charge if no range is specified"
                                        },
                                        "maximumValue": {
                                            "type": "string",
                                            "x-cds-type": "AmountString",
                                            "description": "The upper limit of the charge if the charge could occur in a range"
                                        },
                                        "period": {
                                            "type": "string",
                                            "x-cds-type": "ExternalRef",
                                            "description": "The charges that occur on a schedule indicates the frequency. Formatted according to [ISO 8601 Durations](https://en.wikipedia.org/wiki/ISO_8601#Durations) (excludes recurrence syntax)"
                                        }
                                    }
                                }
                            },
                            "gasContract": {
                                "description": "The details of the terms for the supply of electricity under this plan.  Is mandatory if fuelType is set to GAS or DUAL",
                                "$ref": "#/components/schemas/EnergyPlanContractFull"
                            },
                            "electricityContract": {
                                "description": "The details of the terms for the supply of electricity under this plan.  Is mandatory if fuelType is set to ELECTRICITY or DUAL",
                                "$ref": "#/components/schemas/EnergyPlanContractFull"
                            }
                        }
                    }
                ]
            },
            "EnergyPlanContract": {
                "type": "object",
                "required": [
                    "pricingModel",
                    "isFixed",
                    "paymentOption",
                    "tariffPeriod"
                ],
                "x-conditional": [
                    "timeZone",
                    "variation",
                    "controlledLoad"
                ],
                "properties": {
                    "additionalFeeInformation": {
                        "type": "string",
                        "description": "Free text field containing additional information of the fees for this contract"
                    },
                    "pricingModel": {
                        "type": "string",
                        "description": "The pricing model for the contract.  Contracts for gas must use SINGLE_RATE.  Note that the detail for the enumeration values are:<ul><li>**SINGLE_RATE** - all energy usage is charged at a single unit rate no matter when it is consumed. Multiple unit rates may exist that correspond to varying volumes of usage i.e. a ‘block’ or ‘step’ tariff (first 50kWh @ X cents, next 50kWh at Y cents etc.</li><li>**SINGLE_RATE_CONT_LOAD** - as above, but with an additional, separate unit rate charged for all energy usage from a controlled load i.e. separately metered appliance like hot water service, pool pump etc.</li><li>**TIME_OF_USE** - energy usage is charged at unit rates that vary dependent on time of day and day of week that the energy is consumed</li><li>**TIME_OF_USE_CONT_LOAD** - as above, but with an additional, separate unit rate charged for all energy usage from a controlled load i.e. separately metered appliance like hot water service, pool pump etc.</li><li>**FLEXIBLE** - energy usage is charged at unit rates that vary based on external factors</li><li>**FLEXIBLE_CONT_LOAD** - as above, but with an additional, separate unit rate charged for all energy usage from a controlled load i.e. separately metered appliance like hot water service, pool pump etc.</li><li>**QUOTA** - all energy usage is charged at a single fixed rate, up to a specified usage quota/allowance. All excess usage beyond the allowance is then charged at a single unit rate (may not be the best way to explain it but it is essentially a ‘subscription’ or telco style product i.e. $50/month for up to 150kWh included usage</li></ul>",
                        "enum": ["SINGLE_RATE",
                            "SINGLE_RATE_CONT_LOAD",
                            "TIME_OF_USE",
                            "TIME_OF_USE_CONT_LOAD",
                            "FLEXIBLE",
                            "FLEXIBLE_CONT_LOAD",
                            "QUOTA"
                        ]
                    },
                    "timeZone": {
                        "type": "string",
                        "description": "Required if pricingModel is set to TIME_OF_USE.  Defines the time zone to use for calculation of the time of use thresholds",
                        "enum": ["LOCAL",
                            "AEST"
                        ]
                    },
                    "isFixed": {
                        "type": "boolean",
                        "description": "Flag indicating whether prices are fixed or variable"
                    },
                    "variation": {
                        "type": "string",
                        "description": "Free text description of price variation policy and conditions for the contract.  Mandatory if isFixed is true"
                    },
                    "onExpiryDescription": {
                        "type": "string",
                        "description": "Free text field that describes what will occur on or prior to expiry of the fixed contract term or benefit period"
                    },
                    "paymentOption": {
                        "description": "Payment options for this contract",
                        "type": "array",
                        "items": {
                            "type": "string",
                            "enum": ["PAPER_BILL",
                                "CREDIT_CARD",
                                "DIRECT_DEBIT",
                                "BPAY",
                                "OTHER"
                            ]
                        }
                    },
                    "intrinsicGreenPower": {
                        "description": "Describes intrinsic green power for the plan.  If present then the plan includes a percentage of green power in the base plan. Should not be present for gas contracts",
                        "type": "object",
                        "required": ["greenPercentage"],
                        "properties": {
                            "greenPercentage": {
                                "type": "string",
                                "x-cds-type": "RateString",
                                "description": "Percentage of green power intrinsically included in the plan"
                            }
                        }
                    },
                    "controlledLoad": {
                        "$ref": "#/components/schemas/EnergyPlanControlledLoad"
                    },
                    "incentives": {
                        "$ref": "#/components/schemas/EnergyPlanIncentives"
                    },
                    "discounts": {
                        "$ref": "#/components/schemas/EnergyPlanDiscounts"
                    },
                    "greenPowerCharges": {
                        "$ref": "#/components/schemas/EnergyPlanGreenPowerCharges"
                    },
                    "eligibility": {
                        "$ref": "#/components/schemas/EnergyPlanEligibility"
                    },
                    "fees": {
                        "$ref": "#/components/schemas/EnergyPlanFees"
                    },
                    "solarFeedInTariff": {
                        "$ref": "#/components/schemas/EnergyPlanSolarFeedInTariff"
                    },
                    "tariffPeriod": {
                        "$ref": "#/components/schemas/EnergyPlanTariffPeriod"
                    }
                }
            },
            "EnergyPlanContractFull": {
                "allOf": [
                    {
                        "$ref": "#/components/schemas/EnergyPlanContract"
                    },
                    {
                        "type": "object",
                        "required": [
                            "billFrequency"
                        ],
                        "x-conditional": [
                            "benefitPeriod",
                            "variation",
                            "coolingOffDays"
                        ],
                        "properties": {
                            "termType": {
                                "type": "string",
                                "description": "The term for the contract.  If absent assumes no specified term",
                                "enum": ["1_YEAR",
                                    "2_YEAR",
                                    "3_YEAR",
                                    "4_YEAR",
                                    "5_YEAR",
                                    "ONGOING",
                                    "OTHER"
                                ]
                            },
                            "benefitPeriod": {
                                "type": "string",
                                "description": "Description of the benefit period.  Should only be present if termType has the value ONGOING"
                            },
                            "terms": {
                                "type": "string",
                                "description": "Free text description of the terms for the contract"
                            },
                            "meterTypes": {
                                "description": "An array of the meter types that this contract is available for",
                                "type": "array",
                                "items": {
                                    "type": "string"
                                }
                            },
                            "coolingOffDays": {
                                "type": "string",
                                "x-cds-type": "PositiveInteger",
                                "description": "Number of days in the cooling off period for the contract.  Mandatory for plans with type of MARKET "
                            },
                            "billFrequency": {
                                "description": "An array of the available billing schedules for this contract. Formatted according to [ISO 8601 Durations](https://en.wikipedia.org/wiki/ISO_8601#Durations) (excludes recurrence syntax)",
                                "type": "array",
                                "items": {
                                    "type": "string"
                                }
                            }
                        }
                    }
                ]
            },
            "EnergyPlanControlledLoad": {
                "type": "object",
                "description": "Required if pricing model is SINGLE_RATE_CONT_LOAD or TIME_OF_USE_CONT_LOAD or FLEXIBLE_CONT_LOAD",
                "required": ["displayName", "dailyCharge", "period", "rates"],
                "properties": {
                    "displayName": {
                        "type": "string",
                        "description": "A display name for the controlled load tier"
                    },
                    "description": {
                        "type": "string",
                        "description": "A description of the controlled load tier"
                    },
                    "dailyCharge": {
                        "type": "string",
                        "x-cds-type": "AmountString",
                        "description": "The daily supply charge (exclusive of GST) for this controlled load tier"
                    },
                    "period": {
