          "id": {
            "type": "string",
            "format": "uuid",
            "description": "UUID of the POI that is going to be moved",
            "example": "0c5b01d8-8dde-4d9f-be20-0865766bae6e"
          },
          "attributes": {
            "type": "object",
            "properties": {
              "latitude": {
                "type": "number",
                "format": "float",
                "example": 49.013,
                "minimum": -85,
                "maximum": 85,
                "description": "Latitude in degrees"
              },
              "longitude": {
                "type": "number",
                "format": "float",
                "example": 8.425,
                "minimum": -180,
                "maximum": 180,
                "description": "Longitude in degrees"
              }
            }
          }
        }
      },
      "Fuel": {
        "type": "string",
