      "CommonOpeningHours": {
        "type": "object",
        "properties": {
          "timezone": {
            "type": "string",
            "description": "As defined by ISO 8601, the timezone",
            "example": "+01:00"
          },
          "rules": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "action": {
                  "type": "string",
                  "default": "open",
                  "enum": [
                    "open",
                    "close"
                  ]
                },
                "days": {
                  "type": "array",
                  "items": {
                    "type": "string",
                    "enum": [
                      "monday",
                      "tuesday",
                      "wednesday",
                      "thursday",
                      "friday",
                      "saturday",
                      "sunday"
                    ]
                  }
                },
                "timespans": {
                  "type": "array",
                  "items": {
                    "type": "object",
                    "properties": {
                      "from": {
                        "description": "relative to the specified time zone (local time)",
                        "type": "string",
                        "example": "07:30"
                      },
                      "to": {
                        "description": "relative to the specified time zone (local time)",
                        "type": "string",
                        "example": "20:30"
                      }
                    }
                  }
                }
              }
            }
          }
        },
        "example": {
          "timezone": "+01:00",
          "rules": [
            {
              "days": [
                "monday",
                "tuesday",
                "wednesday",
                "thursday",
                "friday"
              ],
              "hours": [
                {
                  "from": "06:00",
                  "to": "23:00"
                }
              ]
            },
            {
              "days": [
                "saturday"
              ],
              "hours": [
                {
                  "from": "07:00",
                  "to": "23:00"
                }
              ]
            },
            {
              "days": [
                "sunday"
              ],
              "hours": [
                {
                  "from": "07:00",
                  "to": "22:00"
                }
              ]
            }
          ]
        }
