  "paths": {
    "/contributors": {
      "get": {
        "tags": [
          "Contributors"
        ],
        "summary": "Returns list of Contributors",
        "operationId": "getAllContributors",
        "description": "This end point returns list of kind contributors of CodingGarden Community App in long term active support order.",
        "responses": {
          "200": {
            "description": "An array of contributor objects",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "type": "object",
                    "properties": {
                      "type": {
                        "type": "string",
                        "example": "contributor"
                      },
                      "id": {
                        "type": "string",
                        "example": "w3cj"
                      },
                      "attributes": {
                        "type": "object",
                        "properties": {
                          "username": {
                            "type": "string",
                            "example": "w3cj"
                          },
                          "name": {
                            "type": "string",
                            "example": "CJ"
                          },
                          "image": {
                            "type": "string",
                            "example": "https://avatars1.githubusercontent.com/u/14241866"
                          },
                          "countryCode": {
                            "type": "string",
                            "example": "USA"
                          },
                          "active": {
                            "type": "boolean",
                            "example": true
                          },
                          "joined": {
                            "type": "string",
                            "example": "2018-12-16"
                          },
                          "teamIds": {
                            "type": "array",
                            "items": {
                              "type": "number"
                            },
                            "example": [
                              0,
                              1,
                              2,
                              3,
                              4
                            ]
                          }
                        }
                      }
                    }
                  }
                }
              }
            }
          },
          "500": {
            "description": "Error: Internal Server Error",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "message": {
                      "type": "string"
                    },
                    "status": {
                      "type": "number"
                    },
                    "stack": {
                      "type": "string"
                    }
                  },
                  "example": {
                    "message": "Request to <URL> failed",
                    "status": 500,
                    "stack": "FetchError: request to <URL> failed"
                  }
                }
              }
            }
          }
        }
      }
    },
    "/contributors/{id}": {
      "get": {
        "tags": [
          "Contributors"
        ],
        "summary": "Returns one contributor object",
        "operationId": "getSingleContributorById",
        "description": "This end point returns single contributor of CodingGarden Community App based on `id`",
        "parameters": [
          {
            "name": "id",
            "in": "path",
            "required": true,
            "description": "Contributor `id`. It is same as GitHub `username`",
            "schema": {
              "type": "string",
              "example": "w3cj"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "A contributor object",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "type": "object",
                    "properties": {
                      "type": {
                        "type": "string",
                        "example": "contributor"
                      },
                      "id": {
                        "type": "string",
                        "example": "w3cj"
                      },
                      "attributes": {
                        "type": "object",
                        "properties": {
                          "username": {
                            "type": "string",
                            "example": "w3cj"
                          },
                          "name": {
                            "type": "string",
                            "example": "CJ"
                          },
                          "image": {
                            "type": "string",
                            "example": "https://avatars1.githubusercontent.com/u/14241866"
                          },
                          "countryCode": {
                            "type": "string",
                            "example": "USA"
                          },
                          "active": {
                            "type": "boolean",
                            "example": true
                          },
                          "joined": {
                            "type": "string",
                            "example": "2018-12-16"
                          },
                          "teamIds": {
                            "type": "array",
                            "items": {
                              "type": "number"
                            },
                            "example": [
                              0,
                              1,
                              2,
                              3,
                              4
                            ]
                          }
                        }
                      }
                    }
                  }
                }
              }
            }
          },
          "404": {
            "description": "Contributor not found.",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "message": {
                      "type": "string"
                    },
                    "status": {
                      "type": "number"
                    },
                    "stack": {
                      "type": "string"
                    }
                  }
                },
                "example": {
                  "message": "There is no contributor with the ID that you requested",
                  "status": 404,
                  "stack": "RangeError: There is no contributor with the ID that you requested."
                }
              }
            }
          },
          "500": {
            "description": "Error: Internal Server Error",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "message": {
                      "type": "string"
                    },
                    "status": {
                      "type": "number"
                    },
                    "stack": {
                      "type": "string"
                    }
                  },
                  "example": {
                    "message": "Request to <URL> failed",
                    "status": 500,
                    "stack": "FetchError: request to <URL> failed"
                  }
                }
              }
            }
          }
        }
      }
    },
