      "Link": {
        "description": "A pagination header link. This header works together with the `perPage` and `nextPageToken` query string\nparameters.\n",
        "type": "string",
        "format": "uri"
      },
      "PerPage": {
        "description": "This parameter helps to control the amount of data returned to the client through pagination.\nIn the case of EPCIS events, `perPage` specifies the maximum number of events in a response to the client.\nIt does not mandate that the server reaches this limit. For example, if the server sees that some EPCIS events are very\nlarge, the server can decide to return fewer events to avoid creating an error because the response body is too\nlarge.\n\nAs long as there are more resources to retrieve, the `Link` header contains the URL of the next page and\nthe attribute `rel=\"next\"`. The last page is indicated by the absence of the `rel=\"next\"`. Depending on the\nimplementation, there can be a global upper limit for the `perPage` value that the client cannot overwrite,\nwhich should be stated in the documentation.\n",
        "type": "integer",
        "default": 30
      },
      "NextPageToken": {
        "description": "Parameter that points to the next list of results when requesting a list of events that is larger than the value\nspecified with the `perPage` parameter. Some technologies refer to this as scrolling.\nA `nextPageToken` may be invalidated through timeout. It is up to the specific implementation to provide\nthe mechanisms for invalidating tokens.\n",
        "type": "string",
        "example": "3A1550673874978%2Ci%3AU6D7DENAKwM2gQRRwGrataeq"
      },
      "Upgrade": {
        "type": "string",
        "description": "The `Upgrade` and `Connection` headers are needed to open a Websocket for queries.",
        "default": "websocket",
        "example": "websocket"
      },
      "Connection": {
        "example": "Upgrade",
        "description": "The `Upgrade` and `Connection` headers are needed to open a Websocket for queries.",
        "type": "string",
        "default": "upgrade"
      },
      "EPCISEventTypes": {
        "type": "string",
        "description": "The five types of EPCIS events.",
        "example": [
          "ObjectEvent",
          "AggregationEvent",
          "AssociationEvent",
          "TransformationEvent",
          "TransactionEvent"
        ],
        "enum": [
          "ObjectEvent",
          "AggregationEvent",
          "AssociationEvent",
          "TransformationEvent",
          "TransactionEvent"
        ]
      },
      "AbstractQuerySubscription": {
        "example": {
          "callbackUrl": "https://client.example.com/queryCallback",
          "secret": "0435ojtojt02"
        },
        "type": "object",
        "description": "This schema contains shared features of EPCIS query subscriptions. If the schema is used in a response, the\n`secret` should not be fully revealed.\n",
        "required": [
          "callbackUrl",
          "secret"
        ],
        "properties": {
          "callbackUrl": {
            "description": "Callback to receive EPCIS resources and errors.",
            "type": "string",
            "format": "uri",
            "example": "https://client.example.com/queryCallback"
          },
          "secret": {
            "example": "0435ojtojt02",
            "type": "string",
            "description": "Secret generated by the client for the server."
          },
          "initialRecordTime": {
            "$ref": "#/components/schemas/GS1-Query-InitialRecordTime"
          }
        }
      },
      "QueryStreamSubscription": {
        "description": "If no query schedule is specified, the client must explicitly set `stream` to `true`. This restriction is to\nprevent clients from accidentally subscribing to EPCIS event streams.\n\nExample:\n\n```\nPOST /queries/MyQuery/subscriptions\n{\n  \"callbackUrl\": \"https://client.example.com/queryCallback\",\n  \"secret\": \"0435ojtojt02\",\n  \"stream\": true\n}\n```\n",
        "type": "object",
        "example": {
          "stream": true
        },
        "required": [
          "stream"
        ],
        "properties": {
          "stream": {
            "$ref": "#/components/schemas/GS1-Query-Stream"
          }
        }
      },
      "QueryScheduleSubscription": {
        "description": "A scheduled query subscription is a time-based query execution scheduler. EPCIS 2.0 scheduled queries are scheduled\nin the same manner as cron jobs.\n\nFor example, this query subscription is scheduled to trigger every morning at 1.05am. By setting\n`reportIfEmpty` to `true`, the client's callback URL will be called even if there are no new events that match\nthe query.\n\n```\nPOST /queries/MyQuery/subscriptions\n{\n  \"callbackUrl\": \"https://client.example.com/queryCallback\",\n  \"secret\": \"0435ojtojt02\",\n  \"reportIfEmpty\": true,\n  \"schedule\": {\n    \"hour\":\"1\",\n    \"minute\": \"5\"\n  }\n}\n```\n",
        "type": "object",
        "example": {
          "schedule": {
            "hour": "1",
            "minute": "5"
          }
        },
        "required": [
          "schedule"
        ],
        "properties": {
          "reportIfEmpty": {
            "$ref": "#/components/schemas/GS1-Query-ReportIfEmpty"
          },
          "schedule": {
            "type": "object",
            "properties": {
              "second": {
                "$ref": "#/components/schemas/GS1-Query-Second"
              },
              "minute": {
                "$ref": "#/components/schemas/GS1-Query-Minute"
              },
              "hour": {
                "$ref": "#/components/schemas/GS1-Query-Hour"
              },
              "dayOfMonth": {
                "$ref": "#/components/schemas/GS1-Query-DayOfMonth"
              },
              "month": {
                "$ref": "#/components/schemas/GS1-Query-Month"
              },
              "dayOfWeek": {
                "$ref": "#/components/schemas/GS1-Query-DayOfWeek"
              }
            }
          }
        }
      },
      "GS1-Query-InitialRecordTime": {
        "description": "An optional header to specify a time used to constrain what resources are considered when processing a query\nsubscription. This value only applies the first time a subscriber receives events. If omitted, the default\nvalue is the current time.\n",
        "type": "string",
        "format": "date-time",
        "example": "2020-04-04T20:33:31.116-06:00"
      },
      "GS1-Query-Min-Record-Time": {
        "example": "2020-04-04T20:33:31.116-06:00",
        "description": "An optional header to specify the smallest possible record time for EPCIS events in a\nquery subscription.\n",
        "type": "string",
        "format": "date-time"
      },
      "GS1-Query-ReportIfEmpty": {
        "example": false,
        "type": "boolean",
        "default": false,
        "description": "Flag to specify subscribed clients should be notified if the query result is empty."
      },
      "GS1-Query-Second": {
        "description": "Specifies that the query time must have a matching seconds value. The range for this parameter is 0\nthrough 59, inclusive.\n",
        "example": 0,
        "type": "string",
        "pattern": "(\\g<element>,)*\\g<element>|(?<element>(\\g<number>|\\g<range>))|(?<range>(\\[\\g<number>\\-\\g<number>\\]))|(?<number>([0-59]))"
      },
      "GS1-Query-Minute": {
        "description": "Specifies that the query time must have a matching minute value. The range for this parameter is 0 through 59,\ninclusive.\n",
        "example": 5,
        "type": "string",
        "pattern": "(\\g<element>,)*\\g<element>|(?<element>(\\g<number>|\\g<range>))|(?<range>(\\[\\g<number>\\-\\g<number>\\]))|(?<number>([0-59]))"
      },
      "GS1-Query-Hour": {
        "description": "Specifies that the query time must have a matching hour value. The range for this parameter is 0 through 23,\ninclusive, with 0 denoting the hour that begins at midnight, and 23 denoting the hour that ends at midnight.\n",
        "example": 1,
        "type": "string",
        "pattern": "(\\g<element>,)*\\g<element>|(?<element>(\\g<number>|\\g<range>))|(?<range>(\\[\\g<number>\\-\\g<number>\\]))|(?<number>([0-23]))"
      },
      "GS1-Query-DayOfMonth": {
        "description": "Specifies that the query time must have a matching day of month value. The range for this parameter is 1 through\n31, inclusive.\n",
        "example": 1,
        "type": "string",
        "pattern": "(\\g<element>,)*\\g<element>|(?<element>(\\g<number>|\\g<range>))|(?<range>(\\[\\g<number>\\-\\g<number>\\]))|(?<number>([1-31]))"
      },
      "GS1-Query-Month": {
        "description": "Specifies that the query time must have a matching month value. The range for this parameter is 1 through 12,\ninclusive.\n",
        "example": 1,
        "type": "string",
        "pattern": "(\\g<element>,)*\\g<element>|(?<element>(\\g<number>|\\g<range>))|(?<range>(\\[\\g<number>\\-\\g<number>\\]))|(?<number>([1-12]))"
      },
      "GS1-Query-DayOfWeek": {
        "description": "Specifies that the query time must have a matching day of week value. The range for this parameter is 1 through\n7, inclusive, with 1 denoting Monday, 2 denoting Tuesday, and so forth, up to 7 denoting Sunday.\n",
        "example": 1,
        "type": "string",
        "pattern": "(\\g<element>,)*\\g<element>|(?<element>(\\g<number>|\\g<range>))|(?<range>(\\[\\g<number>\\-\\g<number>\\]))|(?<number>([1-7]))"
      },
      "GS1-Query-Stream": {
        "type": "boolean",
        "description": "If now schedule is specified and `stream` is `true`, the standing query SHALL report immediately whenever a\ncaptured EPCIS event matches the query criteria.\n",
        "default": true,
        "enum": [
          true
        ]
      },
      "GS1-EPCIS-Version": {
        "description": "The EPCIS version.",
        "type": "string",
        "pattern": "^\\d+(\\.\\d+)?$",
        "example": "2.0"
      },
      "GS1-EPCIS-Min": {
        "description": "The lowest EPCIS version supported.",
