        "type": "string",
        "pattern": "^\\d+(\\.\\d+)?$"
      },
      "GS1-CBV-Version": {
        "example": "2.0",
        "description": "The core business vocabulary version.",
        "type": "string",
        "pattern": "^\\d+(\\.\\d+)?$"
      },
      "GS1-EPC-Format": {
        "example": "Always_GS1_Digital_Link",
        "description": "Request or response header to indicate whether EPCs are expressed as GS1 Digital Link URIs or as EPC URNs.\nIf both server and client set `GS1-EPC-Format` to `No_Preference`, the default value is `Always_GS1_Digital_Link`:\n\n- No_Preference: The server chooses the representation.\n- Always_DL: URIs are returned as GS1 Digital Link.\n- Always_EPC_URN: URIs are returned as URN.\n- Never_Translates: The original format is kept.\n",
        "type": "string",
        "enum": [
          "No_Preference",
          "Always_GS1_Digital_Link",
          "Always_EPC_URN",
          "Never_Translates"
        ]
      },
      "GS1-CBV-XML-Format": {
        "example": "Always_Web_URI",
        "description": "When requesting XML content-type only, users can use this header to request\nreceiving events with CBV values in either URN or Web URI format.\nThis option is not available for JSON/LD.\n\n- No_Preference: The server chooses the representation.\n- Always_Web_URI: CBV values are returned as Web URI.\n- Always_URN: CBV values are returned as URNs.\n- Never_Translates: The original format is kept.\n",
        "type": "string",
        "enum": [
          "No_Preference",
          "Always_Web_URI",
          "Always_URN",
          "Never_Translates"
        ]
      },
      "GS1-EPCIS-Extensions": {
        "description": "Specific EPCIS extensions supported (e.g., for FIT).",
        "example": {
          "example-epc-ext": "http://example.com/epcis/"
        },
        "type": "object"
      },
      "GS1-Vendor-Version": {
        "example": "example-version-1.0",
        "description": "A versioning scheme that can be freely chosen by the vendor.",
        "anyOf": [
          {
            "type": "string"
          },
          {
            "type": "object"
          }
        ]
      },
      "GS1-CBV-Extensions": {
        "description": "An optional header to specify the vocabulary for the top-level endpoints.",
        "example": {
          "ex": "http://example.com/vendor1",
          "vendor": "<https://example.org/defs#"
        },
        "type": "object"
      },
      "GS1-EPCIS-Capture-Limit": {
        "example": 500,
        "description": "The maximum number of EPCIS events that can be captured per call.",
        "type": "string",
        "pattern": "^(\\d+|\\*)$"
      },
      "GS1-EPCIS-Capture-File-Size-Limit": {
        "example": 1024,
        "description": "The maximum event document length in octets (8-bit bytes).",
        "type": "string",
        "pattern": "^(\\d+|\\*)$"
      },
      "GS1-Capture-Error-Behaviour": {
        "description": "A header to control how the capture interface will behave in case of an error:\n\n- `rollback`: \"All or nothing\". Either the capture job is entirely successful or all EPCIS events are rejected.\n- `proceed`: \"Greedy capture\". The capture interface tries to capture as many EPCIS events as possible, even if there are errors.\n\nThe default behaviour is `rollback`, as in EPCIS 1.2.\n",
        "example": "rollback",
        "type": "string",
        "default": "rollback",
        "enum": [
          "rollback",
          "proceed"
        ]
      },
      "GS1-Next-Page-Token-Expires": {
        "example": "2017-07-21T17:32:28Z",
        "description": "The expiry time for nextPageToken. This header is optional.",
        "type": "string",
        "format": "date-time"
      }
