{
    "openapi": "3.0.0",
    "info": {
        "title": "Promotions & Taxes API",
        "description": "The Promotions & Taxes API allows you to manage and retrieve all promotions, coupons and tax rules from your VTEX store.",
        "contact": {},
        "version": "1.0"
    },
    "servers": [
        {
            "url": "https://{accountName}.{environment}.com.br",
            "variables": {
                "accountName": {
                    "description": "Name of the VTEX account. Used as part of the URL",
                    "default": "{accountName}"
                },
                "environment": {
                    "description": "Environment to use. Used as part of the URL.",
                    "default": "{environment}"
                }
            }
        }
    ],
    "paths": {
        "/api/rnb/pvt/multiple-coupons": {
            "post": {
                "tags": [
                    "Coupons"
                ],
                "summary": "Create multiple coupons",
                "description": "Creates multiple coupons with different coupon codes. This endpoint has a throttling of 60 requests per minute.",
                "parameters": [
                    {
                        "name": "Content-Type",
                        "in": "header",
                        "description": "Type of the content being sent",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    },
                    {
                        "name": "Accept",
                        "in": "header",
                        "description": "HTTP Client Negotiation Accept Header. Indicates the types of responses the client can understand ",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "example": "application/json"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "OK",
                        "content": {
                            "application/json": {
                                "example": [
                                    "test"
                                ],
                                "schema": {
                                    "type": "array",
                                    "description": "Array with the coupon code's of all coupons that were created.",
                                    "items": {
                                        "type": "string",
                                        "title": "items",
                                        "description": "Coupon code.",
                                        "example": [
                                            "test"
                                        ]
                                    }
                                }
                            }
                        }
                    }
                },
                "requestBody": {
                    "content": {
                        "application/json": {
                            "schema": {
                                "type": "array",
                                "title": "",
                                "default": [],
                                "items": {
                                    "required": [
                                        "quantity",
                                        "couponConfiguration",
                                        "utmSource",
                                        "utmCampaign",
                                        "couponCode",
                                        "maxItemsPerClient",
                                        "expirationIntervalPerUse"
                                    ],
                                    "properties": {
                                        "quantity": {
                                            "type": "integer",
                                            "title": "quantity",
                                            "description": "Quantity of coupons created. The value must be a minimum of `1` and at maximum `1000`. If the amount is `1`, the coupon code will be the value of the `couponCode` parameter. If the value is greater than `1`, the coupon code will receive a random suffix.",
                                            "default": 1
                                        },
                                        "couponConfiguration": {
                                            "type": "object",
                                            "title": "couponConfiguration",
                                            "description": "Object that contains all coupon configuration",
                                            "default": {
                                                "utmSource": "coupon3",
                                                "utmCampaign": "",
                                                "couponCode": "test",
                                                "maxItemsPerClient": 10,
                                                "expirationIntervalPerUse": "00:00:00"
                                            },
                                            "required": [
                                                "utmSource",
                                                "utmCampaign",
                                                "couponCode",
                                                "maxItemsPerClient",
                                                "expirationIntervalPerUse"
                                            ],
                                            "properties": {
                                                "utmSource": {
                                                    "type": "string",
                                                    "title": "utmSource",
                                                    "description": "utmSource code",
                                                    "default": "coupon3"
                                                },
                                                "utmCampaign": {
                                                    "type": "string",
                                                    "title": "utmCampaign",
                                                    "description": "utmCampaign code",
                                                    "default": ""
                                                },
                                                "couponCode": {
                                                    "type": "string",
                                                    "title": "couponCode",
                                                    "description": "Coupon code",
                                                    "default": "test"
                                                },
                                                "maxItemsPerClient": {
                                                    "type": "integer",
                                                    "title": "maxItemsPerClient",
                                                    "description": "Maximum items per client that the coupon can be applied",
                                                    "default": 10
                                                },
                                                "expirationIntervalPerUse": {
                                                    "type": "string",
                                                    "title": "expirationIntervalPerUse",
                                                    "description": "Coupon expiration interval per use",
                                                    "default": "00:00:00"
                                                }
                                            }
                                        }
                                    }
                                }
                            }
                        }
                    }
                }
            }
        },
