{
	"openapi": "3.0.1",
	"servers": [
		{
			"description": "API para o cadastro do documento de frete no SIGAGFE - TOTVS",
			"url": "{{host}}/api/gfe/v1",
			"variables": {
				"serverUrl": {
					"default": "localhost"
				},
				"serverHttpPort": {
					"default": "8080"
				}
			}
		}
	],
	"info": {
		"description": "API para o cadastro do documento de frete no SIGAGFE - TOTVS",
		"version": "1.000",
		"title": "FreightDocuments",
		"contact": {
			"name": "T-Talk",
			"url": "API.Totvs.com.br",
			"email": "comiteintegracao@totvs.com.br"
		},
		"x-totvs": {
			"messageDocumentation": {
				"name": "FreightDocuments",
				"description": "Cadastro de documento de frete.",
				"segment": "Gestão de Frete Embarcador"
			},
			"productInformation": [
				{
					"product": "Protheus",
					"contact": "comiteintegracao@totvs.com.br",
					"description": "Cadastro de documento de frete.",
					"adapter": "gfea065api.prw",
					"helpUrl": "link aqui"
				}
			]
		}
	},
	"paths": {
		"/FreightDocuments": {
			"get": {
				"tags": [
					"FreightDocuments"
				],
				"summary": "Retorna todos os documentos de frete cadastrados.",
				"x-totvs": {
					"productInformation": [
						{
							"product": "Protheus",
							"available": true,
							"minimalVersion": "12.1.30"
						}
					]
				},
				"description": "Retorna todos os documentos de frete cadastrados.",
				"operationId": "getFreightDocuments",
				"parameters": [
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Order"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Page"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/PageSize"
					}
				],
				"responses": {
					"200": {
						"description": "Operação realizada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/FreightDocuments_1_000.json#/definitions/PagedFreightDocuments"
								}
							}
						}
					},
					"400": {
						"description": "Erro no momento da requisição",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			}
		},
		"/FreightDocuments/{InternalId}": {
			"get": {
				"tags": [
					"InternalId"
				],
				"summary": "Retorna um documento de frete.",
				"description": "Retorna o documento de frete requerido.",
				"operationId": "getFreightDocumentsID",
				"x-totvs": {
					"productInformation": [
						{
							"product": "Protheus",
							"available": true,
							"minimalVersion": "12.1.30"
						}
					]
				},
				"parameters": [
					{
						"$ref": "#/components/parameters/InternalId"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					}
				],
				"responses": {
					"200": {
						"description": "Operação realizada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/FreightDocuments_1_000.json#/definitions/FreightDocumentsType"
								}
							}
						}
					},
					"404": {
						"description": "Erro no momento da requisição",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			},
			"put": {
				"tags": [
					"InternalId"
				],
				"summary": "Altera a situação de um documento de frete.",
				"description": "Altera a situação do documento de frete, requerido através da chave do registro (InternalId).",
				"operationId": "putFreightDocumentsID",
				"x-totvs": {
					"productInformation": [
						{
							"product": "Protheus",
							"available": true,
							"note": "Este verbo esta disponível com todos os parâmetros",
							"minimalVersion": "12.1.30"
						}
					]
				},
				"parameters": [
					{
						"$ref": "#/components/parameters/InternalId"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					}
				],
				"requestBody": {
					"description": "Altera a situação do documento de frete informado.",
					"content": {
						"application/json": {
							"schema": {
								"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/FreightDocuments_1_000.json#/definitions/FreightDocumentsType"
							}
						}
					}
				},
				"responses": {
					"200": {
						"description": "Operação realizada com sucesso.",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/FreightDocuments_1_000.json#/definitions/FreightDocumentsType"
								}
							}
						}
					},
					"400": {
						"description": "Erro no momento da operação.",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					},
					"404": {
						"description": "Conjunto não localizado",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			}
		}
	},
	"components": {
		"parameters": {
			"InternalId": {
				"name": "InternalId",
				"in": "path",
				"required": true,
				"description": "Chave do registro.",
				"schema": {
					"type": "string"
				}
			}
		},
		"schemas": {}
	}
}
