          {
            "description": "Returns IDs and names of accounts that belong to the specified account group IDs",
            "explode": true,
            "in": "query",
            "name": "accountGroupIds",
            "required": false,
            "schema": { "items": { "type": "string" }, "type": "array" }
          },
          {
            "description": "Returns IDs and names of accounts that belong to the specified cloudType and accountGroup",
            "in": "query",
            "name": "cloudType",
            "required": false,
            "schema": { "type": "string" }
          }
        ],
        "responses": {
          "200": {
            "content": {
              "application/json; charset=UTF-8": {
                "schema": {
                  "items": {
                    "$ref": "#/components/schemas/CloudAccountIdentity"
                  },
                  "type": "array"
                }
              }
            },
            "description": "successful operation"
          }
        },
        "security": [{ "x-redlock-auth": [] }],
        "summary": "List Cloud Account Names - GET",
        "tags": ["Cloud Accounts"],
        "x-codeSamples": [
          {
            "lang": "Python + Requests",
            "source": "import requests\n\nurl = \"https://api.prismacloud.io/cloud/name\"\n\nquerystring = {\"onlyActive\":\"SOME_BOOLEAN_VALUE\",\"accountGroupIds\":\"SOME_ARRAY_VALUE\",\"cloudType\":\"SOME_STRING_VALUE\"}\n\nheaders = {\"x-redlock-auth\": \"REPLACE_KEY_VALUE\"}\n\nresponse = requests.request(\"GET\", url, headers=headers, params=querystring)\n\nprint(response.text)"
          },
          {
            "lang": "Shell + Curl",
            "source": "curl --request GET \\\n  --url 'https://api.prismacloud.io/cloud/name?onlyActive=SOME_BOOLEAN_VALUE&accountGroupIds=SOME_ARRAY_VALUE&cloudType=SOME_STRING_VALUE' \\\n  --header 'x-redlock-auth: REPLACE_KEY_VALUE'"
          }
        ]
      },
      "post": {
        "description": "Returns a list of cloud account IDs and names.",
        "operationId": "get-cloud-account-names-by-post",
        "parameters": [
          {
            "description": "Returns IDs and names of active accounts only. Default is false.",
            "in": "query",
            "name": "onlyActive",
            "required": false,
            "schema": { "type": "boolean" }
          },
          {
            "description": "Returns IDs and names of accounts that belong to the specified cloudType and accountGroup",
            "in": "query",
            "name": "cloudType",
            "required": false,
            "schema": { "type": "string" }
          }
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/AccountListRequestModel"
              }
            }
          },
          "description": "Cloud Account Groups",
          "required": true
        },
        "responses": {
          "200": {
            "content": {
              "application/json; charset=UTF-8": {
                "schema": {
                  "items": {
                    "$ref": "#/components/schemas/CloudAccountIdentity"
                  },
                  "type": "array"
                }
              }
            },
            "description": "successful operation"
          }
        },
        "security": [{ "x-redlock-auth": [] }],
        "summary": "List Cloud Account Names - POST",
        "tags": ["Cloud Accounts"],
        "x-codeSamples": [
          {
            "lang": "Python + Requests",
            "source": "import requests\n\nurl = \"https://api.prismacloud.io/cloud/name\"\n\nquerystring = {\"onlyActive\":\"SOME_BOOLEAN_VALUE\",\"cloudType\":\"SOME_STRING_VALUE\"}\n\npayload = {\n    \"accountGroupIds\": [\"string\"],\n    \"accountIds\": [\"string\"],\n    \"aggregate\": True,\n    \"cloudType\": \"string\",\n    \"csvHeaderRequired\": True,\n    \"filterKey\": \"string\",\n    \"filterValue\": \"string\",\n    \"groupBy\": \"string\",\n    \"limit\": 0,\n    \"timeRange\": {\n        \"relativeTimeType\": \"BACKWARD\",\n        \"type\": \"relative\",\n        \"value\": {\n            \"amount\": 0,\n            \"unit\": \"minute\"\n        }\n    }\n}\nheaders = {\n    \"content-type\": \"application/json\",\n    \"x-redlock-auth\": \"REPLACE_KEY_VALUE\"\n}\n\nresponse = requests.request(\"POST\", url, json=payload, headers=headers, params=querystring)\n\nprint(response.text)"
          },
          {
            "lang": "Shell + Curl",
            "source": "curl --request POST \\\n  --url 'https://api.prismacloud.io/cloud/name?onlyActive=SOME_BOOLEAN_VALUE&cloudType=SOME_STRING_VALUE' \\\n  --header 'content-type: application/json' \\\n  --header 'x-redlock-auth: REPLACE_KEY_VALUE' \\\n  --data '{\"accountGroupIds\":[\"string\"],\"accountIds\":[\"string\"],\"aggregate\":true,\"cloudType\":\"string\",\"csvHeaderRequired\":true,\"filterKey\":\"string\",\"filterValue\":\"string\",\"groupBy\":\"string\",\"limit\":0,\"timeRange\":{\"relativeTimeType\":\"BACKWARD\",\"type\":\"relative\",\"value\":{\"amount\":0,\"unit\":\"minute\"}}}'"
          }
        ]
      }
    },
    "/cloud/{cloud_type}/{id}/project": {
      "get": {
        "description": "Lists all cloud accounts onboarded onto the Prisma Cloud platform as children of an organization.",
