          },
          {
            "description": "Account ID",
            "in": "path",
            "name": "id",
            "required": true,
            "schema": { "pattern": ".+", "type": "string" }
          },
          {
            "description": "true = Exclude account group details. Default is false.",
            "in": "query",
            "name": "excludeAccountGroupDetails",
            "required": false,
            "schema": { "type": "boolean" }
          }
        ],
        "responses": {
          "200": {
            "content": {
              "application/json; charset=UTF-8": {
                "schema": {
                  "items": {
                    "$ref": "#/components/schemas/CloudAccountViewModel"
                  },
                  "type": "array"
                }
              }
            },
            "description": "successful operation"
          },
          "400": { "description": "not_found / not_an_org" },
          "500": { "description": "internal_error" }
        },
        "security": [{ "x-redlock-auth": [] }],
        "summary": "List Cloud Org Accounts",
        "tags": ["Cloud Accounts"],
        "x-codeSamples": [
