    "/organizations/{orgId}/audit":{
      "get":{
        "tags":[
          "Audit"
        ],
        "summary":"Retrieve audit logs for the environment",
        "description":"User must have the ENVIRONMENT_AUDIT[READ] or ORGANIZATION_AUDIT[READ] permission to use this service",
        "operationId":"getAudits",
        "parameters":[
          {
            "name":"environment",
            "in":"query",
            "schema":{
              "type":"string"
            }
          },
          {
            "name":"api",
            "in":"query",
            "schema":{
              "type":"string"
            }
          },
          {
            "name":"application",
            "in":"query",
            "schema":{
              "type":"string"
            }
          },
          {
            "name":"type",
            "in":"query",
            "description":"filter on the type of audit",
            "schema":{
              "$ref":"#/components/schemas/AuditType"
            }
          },
          {
            "name":"event",
            "in":"query",
            "description":"filter by the name of an event.",
            "schema":{
              "type":"string"
            },
            "example":"APPLICATION_UPDATED, API_CREATED, METADATA_DELETED, ..."
          },
          {
            "name":"from",
            "in":"query",
            "description":"Timestamp used to define the start date of the time window to query",
            "schema":{
              "type":"integer",
              "format":"int64"
            }
          },
          {
            "name":"to",
            "in":"query",
            "description":"Timestamp used to define the end date of the time window to query",
            "schema":{
              "type":"integer",
              "format":"int64"
            }
          },
          {
            "name":"size",
            "in":"query",
            "description":"Number of elements per page",
            "schema":{
              "type":"integer",
              "format":"int32",
              "default":20
            }
          },
          {
            "name":"page",
            "in":"query",
            "schema":{
              "type":"integer",
              "format":"int32",
              "default":1
            }
          },
          {
            "name":"orgId",
            "in":"path",
            "description":"The ID of the Organization",
            "required":true,
            "schema":{
              "type":"string",
              "default":"DEFAULT"
            }
          }
        ],
        "responses":{
          "200":{
            "description":"List of audits",
            "content":{
              "application/json":{
                "schema":{
                  "$ref":"#/components/schemas/AuditEntityMetadataPage"
                }
              }
            }
          },
          "500":{
            "description":"Internal server error"
          }
        }
      }
    },
