file.content:
{
  "openapi": "3.0.2",
  "info": {
    "title": "Entity API",
    "version": "1.0.0",
    "description": "Flexible data layer for epilot Entities.\n\nUse this API configure and access your business objects like Contacts, Opportunities and Products.\n\n[Feature Documentation](https://docs.epilot.io/docs/entities/flexible-entities)\n"
  },
  "tags": [
    {
      "name": "Schemas",
      "description": "Model Entities"
    },
    {
      "name": "Entities",
      "description": "CRUD Access for Entities"
    },
    {
      "name": "Activity",
      "description": "Entity Events"
    }
  ],
  "security": [
    {
      "EpilotAuth": []
    },
    {
      "EpilotOrg": []
    }
  ],
  "paths": {
    "/v1/entity/schemas": {
      "get": {
        "operationId": "listSchemas",
        "summary": "listSchemas",
        "description": "Get the latest version of local schema",
        "parameters": [
          {
            "in": "query",
            "name": "unpublished",
            "description": "Includes the unpublished version of unpublished schema",
            "schema": {
              "type": "boolean",
              "default": false
            }
          }
        ],
        "tags": [
          "Schemas"
        ],
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "results": {
                      "type": "array",
                      "items": {
                        "$ref": "#/components/schemas/EntitySchemaItem"
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    },
    "/v1/entity/schemas/blueprints": {
      "get": {
        "operationId": "listSchemaBlueprints",
        "summary": "listSchemaBlueprints",
        "description": "List canonical versions of all available schemas",
        "tags": [
          "Schemas"
        ],
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "results": {
                      "type": "array",
                      "items": {
                        "$ref": "#/components/schemas/EntitySchemaItem"
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    },
    "/v1/entity/schemas/{slug}/versions": {
      "get": {
        "operationId": "getSchemaVersions",
        "summary": "getSchemaVersions",
        "description": "Get all versions of this schema ordered by the latest versions including drafts.",
        "tags": [
          "Schemas"
        ],
        "parameters": [
          {
            "in": "path",
            "name": "slug",
            "required": true,
            "schema": {
              "$ref": "#/components/schemas/EntitySlug"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "versions": {
                      "type": "array",
                      "items": {
                        "$ref": "#/components/schemas/EntitySchemaItem"
                      }
                    },
                    "drafts": {
                      "type": "array",
                      "items": {
                        "$ref": "#/components/schemas/EntitySchemaItem"
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    },
    "/v1/entity/schemas/{slug}": {
      "get": {
        "operationId": "getSchema",
        "summary": "getSchema",
        "description": "By default gets the latest version of the Schema and to get the specific version of schema pass the id.",
        "tags": [
          "Schemas"
        ],
        "parameters": [
          {
            "in": "path",
            "name": "slug",
            "required": true,
            "schema": {
              "$ref": "#/components/schemas/EntitySlug"
            }
          },
          {
            "in": "query",
            "name": "id",
            "schema": {
              "$ref": "#/components/schemas/SchemaId"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/EntitySchemaItem"
                }
              }
            }
          }
        }
      },
      "put": {
        "operationId": "createNewSchemaVersion",
        "summary": "createNewSchemaVersion",
        "description": "Create new version of the schema and default draft is false.",
        "tags": [
          "Schemas"
        ],
        "parameters": [
          {
            "in": "path",
            "name": "slug",
            "required": true,
            "schema": {
              "$ref": "#/components/schemas/EntitySlug"
            }
          },
          {
            "in": "query",
            "name": "draft",
            "schema": {
              "default": false,
              "type": "boolean"
            }
          }
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/EntitySchema"
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/EntitySchemaItem"
                }
              }
            }
          }
        }
      },
      "delete": {
        "operationId": "deleteSchemaById",
        "summary": "deleteSchemaById",
        "description": "Delete schema by Id",
        "tags": [
          "Schemas"
        ],
        "parameters": [
          {
            "in": "path",
            "name": "slug",
            "required": true,
            "schema": {
              "$ref": "#/components/schemas/EntitySlug"
            }
          },
          {
            "in": "query",
            "name": "id",
            "required": true,
            "schema": {
              "$ref": "#/components/schemas/SchemaId"
            }
          }
        ],
        "responses": {
          "204": {
            "description": "Success"
          }
        }
      }
    },
    "/v1/entity:search": {
      "post": {
        "operationId": "searchEntities",
        "summary": "searchEntities",
        "description": "Search for entities. Supports ordering and pagination. Lucene query syntax supported for complex querying.\n\nPassing comma-separated `x-epilot-org-id` is supported for cross-org entity search.\n\n## Relations\n\nWhen `hydrate=true`, relation attributes are replaced in-place with nested entity values.\n\nExample:\n```json\n{\n  \"_id\": \"123\",\n  \"name\": \"parent\",\n  \"_tags\": [\"parent\"],\n  \"contacts\": {\n    \"$relation\": [\n      { \"entity_id\": \"456\", \"_tags\": [\"primary\"] },\n      { \"entity_id\": \"789\", \"_tags\": [\"secondary\"] },\n    ]\n  }\n}\n```\n\nBecomes:\n```json\n{\n  \"_id\": \"123\",\n  \"name\": \"parent\",\n  \"_tags\": [\"parent\"],\n  \"contacts\": [\n    {\n      \"$relation\": { \"entity_id\": \"456\", \"_tags\": [\"primary\"] },\n      \"_id\": \"456\",\n      \"name\": \"child 1\",\n      \"_tags\": [\"child\"]\n    },\n    {\n      \"$relation\": { \"entity_id\": \"789\", \"_tags\": [\"secondary\"] },\n      \"_id\": \"789\",\n      \"name\": \"child 2\",\n      \"_tags\": [\"child\"]\n    }\n  ]\n}\n```\n",
        "tags": [
          "Entities"
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/EntitySearchParams"
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/EntitySearchResults"
                }
              },
              "text/csv": {
                "schema": {
                  "type": "string"
                }
              }
            }
          }
        }
      }
    },
    "/v1/entity/{slug}": {
      "post": {
        "operationId": "createEntity",
        "summary": "createEntity",
        "description": "Creates a new entity using a key.\n\n## Activity\n\nIf no `activity_id` query parameter is provided, implicitly creates Activity of type `EntityCreated`\n\n## Relations\n\nTo create a relation, store a property object that defines a `$relation` array.\n\nExample:\n\n```json\n{\n  \"contacts\": {\n    \"$relation\": [\n      { \"entity_id\": \"3fa85f64-5717-4562-b3fc-2c963f66afa6\" }\n    ]\n  }\n}\n```\n\nThe items in `$relation` support two properties:\n- `entity_id` - The ID of the entity to link\n- `_tags` - Tags or labels for the relation (optional)\n",
        "tags": [
          "Entities"
        ],
        "parameters": [
          {
            "in": "path",
            "name": "slug",
            "description": "Entity Schema",
            "required": true,
            "schema": {
              "$ref": "#/components/schemas/EntitySlug"
            }
          },
          {
            "in": "query",
            "name": "activity_id",
            "description": "Activity to include in event feed",
            "required": false,
            "schema": {
              "$ref": "#/components/schemas/ActivityId"
            }
          }
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/Entity"
              },
              "examples": {
                "contact": {
                  "$ref": "#/components/examples/ContactEntity"
                },
                "account": {
                  "$ref": "#/components/examples/AccountEntity"
                },
                "product": {
                  "$ref": "#/components/examples/ProductEntity"
                },
                "price": {
                  "$ref": "#/components/examples/PriceEntity"
                },
                "tax": {
                  "$ref": "#/components/examples/TaxEntity"
                },
                "order": {
                  "$ref": "#/components/examples/OrderEntity"
                },
                "opportunity": {
                  "$ref": "#/components/examples/OpportunityEntity"
                },
                "email template": {
                  "$ref": "#/components/examples/EmailTemplateEntity"
                },
                "file": {
                  "$ref": "#/components/examples/FileEntity"
                },
                "workflow step": {
                  "$ref": "#/components/examples/WorkflowStepEntity"
                },
                "submission": {
                  "$ref": "#/components/examples/SubmissionEntity"
                }
              }
            }
          }
        },
        "responses": {
          "201": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/EntityItem"
                }
              }
            }
          }
        }
      }
    },
    "/v1/entity/{slug}:upsert": {
      "patch": {
        "operationId": "upsertEntity",
        "summary": "upsertEntity",
        "description": "Create or update an entity using `unique_key`\n\n- If no entities are matched, a new entity is created.\n- If exactly one entity is matched, a `PATCH`-style update is applied to the existing entity.\n- If more than one entity is matched a `409` Error is returned\n\n## Activity\n\nIf no `activity_id` query parameter is provided, implicitly creates Activity of type `EntityCreated` or `EntityUpdated`\n",
        "tags": [
          "Entities"
        ],
        "parameters": [
          {
            "in": "path",
            "name": "slug",
            "description": "Entity Schema",
            "required": true,
            "schema": {
              "$ref": "#/components/schemas/EntitySlug"
            }
          },
          {
            "in": "query",
            "name": "activity_id",
            "description": "Activity to include in event feed",
            "required": false,
            "schema": {
              "$ref": "#/components/schemas/ActivityId"
            }
          },
          {
            "in": "query",
            "name": "dry_run",
            "description": "Dry Run mode = return matched entities but don't update them.",
            "required": false,
            "schema": {
              "type": "boolean",
              "default": false
            }
          }
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "unique_key": {
                    "type": "array",
                    "items": {
                      "type": "string",
                      "example": "email.0.email"
                    },
                    "example": [
                      "_id"
                    ]
                  },
                  "entity": {
                    "$ref": "#/components/schemas/Entity"
                  }
                },
                "required": [
                  "unique_key",
                  "entity"
                ]
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Entity was updated",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/EntityItem"
                }
              }
            }
          },
          "201": {
            "description": "Entity was created",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/EntityItem"
                }
              }
            }
          },
          "409": {
            "description": "Conflict: multiple entities were matched with `unique_key`"
          }
        }
      }
    },
    "/v1/entity/{slug}/{id}": {
      "get": {
        "operationId": "getEntity",
        "summary": "getEntity",
        "description": "Gets Entity and relations by id.\n\n## Relations\n\nWhen `hydrate=true`, relation attributes are replaced in-place with nested entity values.\n\nExample:\n```json\n{\n  \"_id\": \"123\",\n  \"name\": \"parent\",\n  \"_tags\": [\"parent\"],\n  \"contacts\": {\n    \"$relation\": [\n      { \"entity_id\": \"456\", \"_tags\": [\"primary\"] },\n      { \"entity_id\": \"789\", \"_tags\": [\"secondary\"] },\n    ]\n  }\n}\n```\n\nBecomes:\n```json\n{\n  \"_id\": \"123\",\n  \"name\": \"parent\",\n  \"_tags\": [\"parent\"],\n  \"contacts\": [\n    {\n      \"$relation\": { \"entity_id\": \"456\", \"_tags\": [\"primary\"] },\n      \"_id\": \"456\",\n      \"name\": \"child 1\",\n      \"_tags\": [\"child\"]\n    },\n    {\n      \"$relation\": { \"entity_id\": \"789\", \"_tags\": [\"secondary\"] },\n      \"_id\": \"789\",\n      \"name\": \"child 2\",\n      \"_tags\": [\"child\"]\n    }\n  ]\n}\n```\n",
        "tags": [
          "Entities"
        ],
        "parameters": [
          {
            "in": "path",
            "name": "slug",
            "description": "Entity Type",
            "required": true,
            "schema": {
              "$ref": "#/components/schemas/EntitySlug"
            }
          },
          {
            "in": "path",
            "name": "id",
            "description": "Entity id",
            "required": true,
            "schema": {
              "$ref": "#/components/schemas/EntityId"
            }
          },
          {
            "in": "query",
            "name": "hydrate",
            "description": "When true, enables entity hydration to resolve nested $relation references in-place.",
            "schema": {
              "default": false,
              "type": "boolean"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "entity": {
                      "$ref": "#/components/schemas/EntityItem"
                    },
                    "relations": {
                      "type": "array",
                      "items": {
                        "$ref": "#/components/schemas/EntityItem"
                      }
                    }
                  }
                }
              }
            }
          }
        }
      },
      "put": {
        "operationId": "updateEntity",
        "summary": "updateEntity",
        "description": "Updates an Entity\n\n## Activity\n\nIf no `activity_id` query parameter is provided, implicitly creates Activity of type `EntityUpdated`\n\n## Relations\n\nTo create a relation, store a property that defines a `$relation` array.\n\nExample:\n\n```json\n{\n  \"contacts\": {\n    \"$relation\": [\n      { \"entity_id\": \"3fa85f64-5717-4562-b3fc-2c963f66afa6\" }\n    ]\n  }\n}\n```\n\nThe items in `$relation` support two properties:\n- `entity_id` - The ID of the entity to link\n- `_tags` - Tags or labels for the relation (optional)\n",
        "tags": [
          "Entities"
        ],
        "parameters": [
          {
            "in": "path",
            "name": "slug",
            "description": "Entity Type",
            "required": true,
            "schema": {
              "$ref": "#/components/schemas/EntitySlug"
            }
          },
          {
            "in": "path",
            "name": "id",
            "description": "Entity id",
            "required": true,
            "schema": {
              "$ref": "#/components/schemas/EntityId"
            }
          },
          {
            "in": "query",
            "name": "activity_id",
            "description": "Activity to include in event feed",
            "required": false,
            "schema": {
              "$ref": "#/components/schemas/ActivityId"
            }
          }
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/Entity"
              },
              "example": {}
            }
          }
        },
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/EntityItem"
                }
              }
            }
          }
        }
      },
      "delete": {
        "operationId": "deleteEntity",
        "summary": "deleteEntity",
        "description": "Deletes an Entity\n\n## Activity\n\nIf no `activity_id` query parameter is provided, implicitly creates Activity of type `EntityDeleted`\n",
        "tags": [
          "Entities"
        ],
        "parameters": [
          {
            "in": "path",
            "name": "slug",
            "description": "Entity Type",
            "required": true,
            "schema": {
              "$ref": "#/components/schemas/EntitySlug"
            }
          },
          {
            "in": "path",
            "name": "id",
            "required": true,
            "schema": {
              "$ref": "#/components/schemas/EntityId"
            }
          },
          {
            "in": "query",
            "name": "activity_id",
            "description": "Activity to include in event feed",
            "required": false,
            "schema": {
              "$ref": "#/components/schemas/ActivityId"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Success"
          }
        }
      }
    },
    "/v1/entity:autocomplete": {
      "get": {
        "operationId": "autocomplete",
        "summary": "autocomplete",
        "description": "Autocomplete entity attributes\n",
        "tags": [
          "Entities"
        ],
        "parameters": [
          {
            "name": "input",
            "in": "query",
            "description": "Input to autocomplete",
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "attribute",
            "in": "query",
            "description": "Autocomplete attribute",
            "schema": {
              "type": "string",
              "example": "_tags"
            },
            "required": true
          },
          {
            "name": "slug",
            "in": "query",
            "description": "Limit results to entity schema",
            "schema": {
              "$ref": "#/components/schemas/EntitySlug"
            }
          },
          {
            "name": "size",
            "in": "query",
            "description": "Maximum number of results to return",
            "schema": {
              "type": "integer",
              "default": 10,
              "minimum": 1,
              "maximum": 250
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "hits": {
                      "type": "number",
                      "example": 1
                    },
                    "results": {
                      "type": "array",
                      "items": {
                        "anyOf": [
                          {
                            "type": "string"
                          },
                          {
                            "type": "boolean"
                          },
                          {
                            "type": "object",
                            "additionalProperties": true
                          }
                        ]
                      },
                      "example": [
                        "value"
                      ]
                    }
                  }
                }
              }
            }
          }
        }
      }
    },
    "/v1/entity/activity": {
      "post": {
        "operationId": "createActivity",
        "summary": "createActivity",
        "description": "Create an activity that can be displayed in activity feeds.\n\n- All activites are published as events on the event bus\n- Entity mutations are always part of an activity\n",
        "tags": [
          "Activity"
        ],
        "parameters": [
          {
            "in": "query",
            "name": "entities",
            "description": "Comma-separated list of entities which the activity primarily concerns",
            "style": "form",
            "explode": false,
            "schema": {
              "type": "array",
              "items": {
                "$ref": "#/components/schemas/EntityId"
              }
            }
          }
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/Activity"
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ActivityItem"
                }
              }
            }
          }
        }
      }
    },
    "/v1/entity/activity/{id}": {
      "get": {
        "operationId": "getActivity",
        "summary": "getActivity",
        "description": "Get activity by id",
        "tags": [
          "Activity"
        ],
        "parameters": [
          {
            "in": "path",
            "name": "id",
            "description": "Activity Id",
            "required": true,
            "schema": {
              "$ref": "#/components/schemas/ActivityId"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ActivityItem"
                }
              }
            }
          }
        }
      }
    },
    "/v1/entity/activity/{id}:attach": {
      "post": {
        "operationId": "attachActivity",
        "summary": "attachActivity",
        "description": "Attach existing activity to entity activity feeds",
        "tags": [
          "Activity"
        ],
        "parameters": [
          {
            "in": "path",
            "name": "id",
            "description": "Activity Id",
            "required": true,
            "schema": {
              "$ref": "#/components/schemas/ActivityId"
            }
          },
          {
            "in": "query",
            "name": "entities",
            "description": "Comma-separated list of entities which the activity primarily concerns",
            "style": "form",
            "explode": false,
            "schema": {
              "type": "array",
              "items": {
                "$ref": "#/components/schemas/EntityId"
              }
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ActivityItem"
                }
              }
            }
          }
        }
      }
    },
    "/v1/entity/{slug}/{id}/activity": {
      "get": {
        "operationId": "getEntityActivityFeed",
        "summary": "getEntityActivityFeed",
        "description": "Get activity feed for an entity\n",
        "tags": [
          "Activity"
        ],
        "parameters": [
          {
            "in": "path",
            "name": "slug",
            "description": "Entity Type",
            "required": true,
            "schema": {
              "$ref": "#/components/schemas/EntitySlug"
            }
          },
          {
            "in": "path",
            "name": "id",
            "description": "Entity id",
            "required": true,
            "schema": {
              "$ref": "#/components/schemas/EntityId"
            }
          },
          {
            "in": "query",
            "name": "after",
            "description": "Get activities after this timestamp",
            "schema": {
              "type": "string",
              "format": "date-time"
            }
          },
          {
            "in": "query",
            "name": "before",
            "description": "get activities before this timestamp",
            "schema": {
              "type": "string",
              "format": "date-time"
            }
          },
          {
            "in": "query",
            "name": "from",
            "description": "start from page",
            "schema": {
              "type": "integer",
              "minimum": 0,
