            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/ForbiddenException" }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "applicationId",
            "in": "path",
            "required": true,
            "description": "The Amazon Resource Name (ARN) of the application.",
            "schema": { "type": "string" }
          },
