          {
            "name": "meshName",
            "in": "path",
            "required": true,
            "description": "The name of the service mesh to create the gateway route in.",
            "schema": { "type": "string", "minLength": 1, "maxLength": 255 }
          },
          {
            "name": "meshOwner",
            "in": "query",
            "required": false,
            "description": "The AWS IAM account ID of the service mesh owner. If the account ID is not your own, then\n               the account that you specify must share the mesh with your account before you can create \n             the resource in the service mesh. For more information about mesh sharing, see <a href=\"https://docs.aws.amazon.com/app-mesh/latest/userguide/sharing.html\">Working with shared meshes</a>.",
            "schema": { "type": "string", "minLength": 12, "maxLength": 12 }
          },
          {
            "name": "virtualGatewayName",
            "in": "path",
            "required": true,
            "description": "The name of the virtual gateway to associate the gateway route with. If the virtual\n         gateway is in a shared mesh, then you must be the owner of the virtual gateway\n         resource.",
            "schema": { "type": "string", "minLength": 1, "maxLength": 255 }
          }
        ],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": ["gatewayRouteName", "spec"],
                "properties": {
                  "clientToken": {
                    "description": "Unique, case-sensitive identifier that you provide to ensure the idempotency of the\nrequest. Up to 36 letters, numbers, hyphens, and underscores are allowed.",
                    "type": "string"
                  },
                  "gatewayRouteName": {
                    "description": "The name to use for the gateway route.",
                    "type": "string",
                    "minLength": 1,
                    "maxLength": 255
                  },
                  "spec": {
                    "description": "An object that represents a gateway route specification. Specify one gateway route\n         type.",
                    "type": "object",
                    "properties": {
                      "grpcRoute": {
                        "allOf": [
                          { "$ref": "#/components/schemas/GrpcGatewayRoute" },
                          {
                            "description": "An object that represents the specification of a gRPC gateway route."
                          }
                        ]
                      },
                      "http2Route": {
                        "allOf": [
                          { "$ref": "#/components/schemas/HttpGatewayRoute" },
                          {
                            "description": "An object that represents the specification of an HTTP/2 gateway route."
                          }
                        ]
                      },
                      "httpRoute": {
                        "allOf": [
                          { "$ref": "#/components/schemas/HttpGatewayRoute" },
                          {
                            "description": "An object that represents the specification of an HTTP gateway route."
                          }
                        ]
                      },
                      "priority": {
                        "allOf": [
                          {
                            "$ref": "#/components/schemas/GatewayRoutePriority"
                          },
                          {
                            "description": "The ordering of the gateway routes spec."
                          }
                        ]
                      }
                    }
                  },
                  "tags": {
                    "description": "Optional metadata that you can apply to the gateway route to assist with categorization\n         and organization. Each tag consists of a key and an optional value, both of which you\n         define. Tag keys can have a maximum character length of 128 characters, and tag values can have\n            a maximum length of 256 characters.",
                    "type": "array",
                    "items": { "$ref": "#/components/schemas/TagRef" },
                    "minItems": 0,
                    "maxItems": 50
                  }
                }
              }
            }
          }
        },
