            "name": "limit",
            "in": "query",
            "required": false,
            "description": "The maximum number of results returned by <code>ListGatewayRoutes</code> in paginated\n         output. When you use this parameter, <code>ListGatewayRoutes</code> returns only\n            <code>limit</code> results in a single page along with a <code>nextToken</code> response\n         element. You can see the remaining results of the initial request by sending another\n            <code>ListGatewayRoutes</code> request with the returned <code>nextToken</code> value.\n         This value can be between 1 and 100. If you don't use this\n         parameter, <code>ListGatewayRoutes</code> returns up to 100 results and a\n            <code>nextToken</code> value if applicable.",
            "schema": { "type": "integer", "minimum": 1, "maximum": 100 }
          },
          {
            "name": "meshName",
            "in": "path",
            "required": true,
            "description": "The name of the service mesh to list gateway routes in.",
            "schema": { "type": "string", "minLength": 1, "maxLength": 255 }
          },
          {
            "name": "meshOwner",
            "in": "query",
            "required": false,
            "description": "The AWS IAM account ID of the service mesh owner. If the account ID is not your own, then it's\n               the ID of the account that shared the mesh with your account. For more information about mesh sharing, see <a href=\"https://docs.aws.amazon.com/app-mesh/latest/userguide/sharing.html\">Working with shared meshes</a>.",
            "schema": { "type": "string", "minLength": 12, "maxLength": 12 }
          },
          {
            "name": "nextToken",
            "in": "query",
            "required": false,
            "description": "The <code>nextToken</code> value returned from a previous paginated\n            <code>ListGatewayRoutes</code> request where <code>limit</code> was used and the results\n         exceeded the value of that parameter. Pagination continues from the end of the previous\n         results that returned the <code>nextToken</code> value.",
            "schema": { "type": "string" }
