                  "SecurityGroups": {
                    "description": "Up to five VPC security group IDs, of the form <code>sg-xxxxxxxx</code>. These must be for the same VPC as subnet specified.",
                    "type": "array",
                    "items": { "$ref": "#/components/schemas/SecurityGroup" },
