      },
      "RegexPatternSet": {
        "type": "object",
        "properties": {
          "Name": {
            "allOf": [
              { "$ref": "#/components/schemas/EntityName" },
              {
                "description": "The name of the set. You cannot change the name after you create the set."
              }
            ]
          },
          "Id": {
            "allOf": [
              { "$ref": "#/components/schemas/EntityId" },
              {
                "description": "A unique identifier for the set. This ID is returned in the responses to create and list commands. You provide it to operations like update and delete."
              }
            ]
          },
          "ARN": {
            "allOf": [
              { "$ref": "#/components/schemas/ResourceArn" },
              { "description": "The Amazon Resource Name (ARN) of the entity." }
            ]
          },
          "Description": {
            "allOf": [
              { "$ref": "#/components/schemas/EntityDescription" },
              {
                "description": "A description of the set that helps with identification. You cannot change the description of a set after you create it."
