      "TextTransformationPriority": { "type": "integer", "minimum": 0 },
      "TextTransformationType": {
        "type": "string",
        "enum": [
          "NONE",
          "COMPRESS_WHITE_SPACE",
          "HTML_ENTITY_DECODE",
          "LOWERCASE",
          "CMD_LINE",
          "URL_DECODE",
          "BASE64_DECODE",
          "HEX_DECODE",
          "MD5",
          "REPLACE_COMMENTS",
          "ESCAPE_SEQ_DECODE",
          "SQL_HEX_DECODE",
