                                    "minItems": 1,
                                    "maxItems": 500
                                  }
                                },
                                "example": {
                                  "associationType": "issueIdOrKeys",
                                  "values": [
                                    "ABC-123",
                                    "ABC-456"
                                  ]
                                }
                              },
                              {
                                "description": "An association type referencing service id or keys.\n",
                                "type": "object",
                                "required": [
                                  "associationType",
                                  "values"
                                ],
                                "properties": {
                                  "associationType": {
