{
	"openapi": "3.0.1",
	"info": {
		"title": "lotide API",
		"version": "0.9.0-pre"
	},
	"components": {
		"schemas": {
			"PathUserID": {
				"oneOf": [
					{"type": "string", "enum": ["~me"]},
					{"type": "integer"}
				]
			},
			"SortType": {
				"type": "string",
				"enum": ["hot", "new"]
			},
			"Content": {
				"type": "object",
				"required": ["content_text", "content_markdown", "content_html"],
				"properties": {
					"content_text": {"type": "string"},
					"content_markdown": {"type": "string"},
					"content_html": {"type": "string"}
				}
			},
			"LoginUserInfo": {
				"type": "object",
				"required": ["id", "username", "is_site_admin", "has_unread_notifications"],
				"properties": {
					"id": {"type": "integer"},
					"username": {"type": "string"},
					"is_site_admin": {"type": "boolean"},
					"has_unread_notifications": {"type": "boolean"}
				}
			},
			"LoginInfo": {
				"type": "object",
				"required": ["user", "permissions"],
				"properties": {
					"user": {
						"$ref": "#/components/schemas/LoginUserInfo"
					},
					"permissions": {
						"type": "object",
						"required": ["create_community", "create_invitation"],
						"properties": {
							"create_community": {"$ref": "#/components/schemas/PermissionInfo"},
							"create_invitation": {"$ref": "#/components/schemas/PermissionInfo"}
						}
					}
				}
			},
			"MinimalCommentInfo": {
				"type": "object",
				"required": ["id", "remote_url"],
				"properties": {
					"id": {"type": "integer"},
					"remote_url": {"type": "string", "nullable": true},
					"content_text": {"type": "string"},
					"content_html": {"type": "string"}
				}
			},
			"MinimalCommunityInfo": {
				"type": "object",
				"required": ["id", "username", "local", "host", "remote_url", "deleted"],
				"properties": {
					"id": {"type": "integer"},
					"name": {"type": "string"},
					"local": {"type": "boolean"},
					"host": {"type": "string"},
					"remote_url": {"type": "string", "nullable": true},
					"deleted": {"type": "boolean"}
				}
			},
			"MinimalPostInfo": {
				"type": "object",
				"required": ["id", "title", "remote_url", "sensitive"],
				"properties": {
					"id": {"type": "integer"},
					"title": {"type": "string"},
					"remote_url": {"type": "string", "nullable": true},
					"sensitive": {"type": "boolean"}
				}
			},
			"NullableMinimalPostInfo": {
				"type": "object",
				"nullable": true,
				"required": ["id", "title", "remote_url"],
				"properties": {
					"id": {"type": "integer"},
					"title": {"type": "string"},
					"remote_url": {"type": "string", "nullable": true},
					"sensitive": {"type": "boolean"}
				}
			},
			"MinimalUserInfo": {
				"type": "object",
				"required": ["id", "username", "local", "host", "remote_url", "is_bot"],
				"properties": {
					"id": {"type": "integer"},
					"username": {"type": "string"},
					"local": {"type": "boolean"},
					"host": {"type": "string"},
					"remote_url": {"type": "string", "nullable": true},
					"is_bot": {"type": "boolean"},
					"avatar": {
						"type": "object",
						"required": ["url"],
						"properties": {
							"url": {"type": "string"}
						}
					}
				}
			},
			"NullableMinimalUserInfo": {
				"type": "object",
				"nullable": true,
				"required": ["id", "username", "local", "host", "remote_url"],
				"properties": {
					"id": {"type": "integer"},
					"username": {"type": "string"},
					"local": {"type": "boolean"},
					"host": {"type": "string"},
					"remote_url": {"type": "string", "nullable": true},
					"avatar": {
						"type": "object",
						"required": ["url"],
						"properties": {
							"url": {"type": "string"}
						}
					}
				}
			},
			"UserInfo": {
				"allOf": [{"$ref": "#/components/schemas/MinimalUserInfo"}],
				"type": "object",
				"required": ["description"],
				"properties": {
					"description": {
						"$ref": "#/components/schemas/Content"
					},
					"your_note": {
						"type": "string",
						"nullable": true,
						"description": "Private note about this user written by you"
					},
					"suspended": {
						"type": "boolean",
						"description": "Whether the user is suspended"
					}
				}
			},
			"CommunityInfo": {
				"allOf": [{"$ref": "#/components/schemas/MinimalCommunityInfo"}],
				"type": "object",
				"required": ["description", "feeds"],
				"properties": {
					"description": {
						"$ref": "#/components/schemas/Content"
					},
					"feeds": {
						"type": "object",
						"required": ["atom"],
						"properties": {
							"atom": {
								"type": "object",
								"required": ["new"],
								"properties": {
									"new": {
										"type": "string"
									}
								}
							}
						}
					},
					"you_are_moderator": {"type": "boolean"},
					"your_follow": {
						"type": "object",
						"nullable": true,
						"required": ["accepted"],
						"properties": {
							"accepted": {
								"type": "boolean",
								"description": "Whether your follow request has been accepted by the community."
							}
						}
					}
				}
			},
			"PermissionInfo": {
				"type": "object",
				"required": ["allowed"],
				"properties": {
					"allowed": {"type": "boolean"}
				}
			},
			"SomePostInfo": {
				"type": "object",
				"required": ["id", "title", "created", "community", "score", "sticky", "remote_url", "content_text", "content_html", "content_markdown", "sensitive"],
				"properties": {
					"id": {"type": "integer"},
					"title": {"type": "string"},
					"remote_url": {"type": "string", "nullable": true},
					"href": {"type": "string"},
					"content_text": {
						"nullable": true,
						"type": "string",
						"description": "Content in text form. Should be displayed if content_html is not present."
					},
					"content_html": {
						"nullable": true,
						"type": "string",
						"description": "Content in HTML form."
					},
					"author": {
						"$ref": "#/components/schemas/MinimalUserInfo"
					},
					"created": {"type": "string", "format": "date-time"},
					"community": {
						"type": "object",
						"required": ["id", "name", "local", "host", "remote_url"],
						"properties": {
							"id": {"type": "integer"},
							"name": {"type": "string"},
							"local": {"type": "boolean"},
							"host": {"type": "string"},
							"remote_url": {"type": "string", "nullable": true}
						}
					},
					"score": {"type": "integer"},
					"sticky": {"type": "boolean"},
					"your_vote": {"$ref": "#/components/schemas/YourVote"},
					"content_markdown": {"type": "string", "nullable": true},
					"sensitive": {"type": "boolean"}
				}
			},
			"PostListPost": {
				"allOf": [
					{"$ref": "#/components/schemas/SomePostInfo"},
					{
						"type": "object",
						"required": ["replies_count_total"],
						"properties": {
							"replies_count_total": {"type": "integer"}
						}
					}
				]
			},
			"PostCommentInfo": {
				"type": "object",
				"required": ["id", "content_text", "content_markdown", "content_html", "sensitive", "author", "created", "deleted", "replies", "attachments", "local", "remote_url"],
				"properties": {
					"id": {"type": "integer"},
					"remote_url": {"type": "string", "nullable": true},
					"content_text": {"type": "string", "nullable": true},
					"content_markdown": {"type": "string", "nullable": true},
					"content_html": {"type": "string", "nullable": true},
					"sensitive": {"type": "boolean"},
					"attachments": {
						"type": "array",
						"items": {
							"type": "object",
							"required": ["url"],
							"properties": {
								"url": {"type": "string"}
							}
						}
					},
					"author": {
						"$ref": "#/components/schemas/NullableMinimalUserInfo"
					},
					"created": {"type": "string", "format": "date-time"},
					"deleted": {"type": "boolean"},
					"local": {"type": "boolean"},
					"replies": {
						"type": "object",
						"nullable": true,
						"required": ["items", "next_page"],
						"properties": {
							"items": {
								"type": "array",
								"items": {"$ref": "#/components/schemas/PostCommentInfo"}
							},
							"next_page": {
								"type": "string",
								"nullable": true
							}
						},
						"description": "If null, there are replies to this comment but are omitted due to depth limits. Otherwise, this contains a list of replies. If next_page is not null, some replies have been omitted and can be accessed with pagination."
					},
					"your_vote": {"$ref": "#/components/schemas/YourVote"}
				}
			},
			"YourVote": {
				"type": "object",
				"nullable": true,
				"description": "Will be null if you haven't voted, or an empty object if you have."
			}
		},
		"securitySchemes": {
			"bearer": {
				"type": "http",
				"description": "Token passed in Authorization header as Bearer",
				"scheme": "bearer"
			}
		}
	},
	"paths": {
		"/api/unstable/actors:lookup/{remoteID}": {
			"get": {
				"summary": "Look up a remote actor by WebFinger or ActivityPub ID",
				"parameters": [
					{
						"name": "remoteID",
						"in": "path",
						"required": true,
						"schema": {
							"type": "string"
						}
					}
				],
				"responses": {
					"200": {
						"description": "",
						"content": {
							"application/json": {
								"schema": {
									"type": "array",
									"items": {
										"type": "object",
										"required": ["id"],
										"properties": {
											"id": {"type": "integer"},
											"type": {"type": "string", "enum": ["community", "user"]}
										}
									}
								}
							}
						}
					}
				}
			}
		},
		"/api/unstable/comments/{commentID}": {
			"get": {
				"summary": "Fetch a comment",
				"parameters": [
					{
						"name": "commentID",
						"in": "path",
						"required": true,
						"schema": {"type": "integer"}
					},
					{
						"name": "include_your",
						"in": "query",
						"required": false,
						"schema": {"type": "boolean"}
					}
				],
				"responses": {
					"200": {
						"description": "",
						"content": {
							"application/json": {
								"schema": {
									"type": "object",
									"required": ["id", "content_text", "content_html", "sensitive", "author", "created", "deleted", "replies", "parent", "post", "remote_url"],
									"properties": {
										"id": {"type": "integer"},
										"remote_url": {"type": "string", "nullable": true},
										"content_text": {"type": "string", "nullable": true},
										"content_html": {"type": "string", "nullable": true},
										"sensitive": {"type": "boolean"},
										"author": {
											"$ref": "#/components/schemas/NullableMinimalUserInfo"
										},
										"created": {"type": "string", "format": "date-time"},
										"deleted": {"type": "boolean"},
										"replies": {
											"type": "object",
											"nullable": true,
											"properties": {
												"items": {"type": "array"},
												"next_page": {
													"type": "string",
													"nullable": true,
													"description": "This is actually always null since it only appears when there are no replies."
												}
											},
											"description": "This endpoint no longer includes replies. However, if there are no replies, this will be non-null and items will be an empty array."
										},
										"your_vote": {"$ref": "#/components/schemas/YourVote"},
										"parent": {
											"type": "object",
											"nullable": true,
											"required": ["id"],
											"properties": {
												"id": {"type": "integer"}
											}
										},
										"post": {"$ref": "#/components/schemas/NullableMinimalPostInfo"}
									}
								}
							}
						}
					}
				},
				"security": [{"bearer": []}]
			},
			"delete": {
				"summary": "Delete a comment",
				"parameters": [
					{
						"name": "commentID",
						"in": "path",
						"required": true,
						"schema": {"type": "integer"}
					}
				],
				"responses": {
					"204": {
						"description": "Successfully deleted."
					}
				},
				"security": [{"bearer": []}]
			}
		},
		"/api/unstable/comments/{commentID}/replies": {
			"get": {
				"summary": "List comment replies",
				"parameters": [
					{
						"name": "commentID",
						"in": "path",
						"required": true,
						"schema": {"type": "integer"}
					},
					{
						"name": "include_your",
						"in": "query",
						"required": false,
						"schema": {"type": "boolean"}
					},
					{
						"name": "depth",
						"in": "query",
						"required": false,
						"schema": {"type": "integer"},
						"description": "Number of levels to expand replies for. Defaults to 3."
					},
					{
						"name": "limit",
						"in": "query",
						"required": false,
						"schema": {"type": "integer"}
					},
					{
						"name": "sort",
						"in": "query",
						"required": false,
						"schema": {"$ref": "#/components/schemas/SortType"}
					},
					{
						"name": "page",
						"in": "query",
						"required": false,
						"schema": {"type": "string"}
					}
				],
				"responses": {
					"200": {
						"description": "",
						"content": {
							"application/json": {
								"schema": {
									"type": "object",
									"required": ["items", "next_page"],
									"properties": {
										"items": {
											"type": "array",
											"items": {
												"$ref": "#/components/schemas/PostCommentInfo"
											}
										},
										"next_page": {
											"type": "string",
											"nullable": true
										}
									}
								}
							}
						}
					}
				}
			},
			"post": {
				"summary": "Reply to a comment",
				"parameters": [
					{
						"name": "commentID",
						"in": "path",
						"required": true,
						"schema": {"type": "integer"}
					}
				],
				"requestBody": {
					"required": true,
					"content": {
						"application/json": {
							"schema": {
								"type": "object",
								"properties": {
									"content_text": {"type": "string"},
									"content_markdown": {"type": "string"},
									"attachment": {
										"type": "string",
										"description": "Must be a `local-media://` reference"
									},
									"sensitive": {"type": "boolean"}
								}
							}
						}
					}
				},
				"responses": {
					"200": {
						"description": "Successfully created reply.",
						"content": {
							"application/json": {
								"schema": {
									"type": "object",
									"required": ["id", "post"],
									"properties": {
										"id": {"type": "integer"},
										"post": {
											"type": "object",
											"required": ["id"],
											"properties": {
												"id": {"type": "integer"}
											}
										}
									}
								}
							}
						}
					}
				}
			}
		},
		"/api/unstable/comments/{commentID}/votes": {
			"get": {
				"summary": "List likers of a comment",
				"parameters": [
					{
						"name": "commentID",
						"in": "path",
						"required": true,
						"schema": {"type": "integer"}
					},
					{
						"name": "page",
						"in": "query",
						"required": true,
						"schema": {"type": "string"}
					}
				],
				"responses": {
					"200": {
						"description": "",
						"content": {
							"application/json": {
								"schema": {
									"type": "object",
									"required": ["items", "next_page"],
									"properties": {
										"items": {
											"type": "array",
											"items": {
												"type": "object",
												"required": ["user"],
												"properties": {
													"user": {"$ref": "#/components/schemas/MinimalUserInfo"}
												}
											}
										},
										"next_page": {
											"type": "string",
											"nullable": true
										}
									}
								}
							}
						}
					}
				}
			}
		},
		"/api/unstable/comments/{commentID}/your_vote": {
			"put": {
				"summary": "Like a comment",
				"parameters": [
					{
						"name": "commentID",
						"in": "path",
						"required": true,
						"schema": {"type": "integer"}
					}
				],
				"responses": {
					"204": {
						"description": "Successfully liked."
					}
				},
				"security": [{"bearer": []}]
			},
			"delete": {
				"summary": "Retract a like of a comment",
				"parameters": [
					{
						"name": "commentID",
						"in": "path",
						"required": true,
						"schema": {"type": "integer"}
					}
				],
				"responses": {
					"204": {
						"description": "Successfully unliked."
					}
				},
				"security": [{"bearer": []}]
			}
		},
		"/api/unstable/communities": {
			"get": {
				"summary": "List communities",
				"parameters": [
					{
						"name": "include_your",
						"in": "query",
						"required": false,
						"schema": {"type": "boolean"},
						"description": "If true, will include `you_are_moderator` and `your_follow` in list. Requires login."
					},
					{
						"name": "search",
						"in": "query",
						"required": false,
						"schema": {"type": "string"}
					},
					{
						"name": "your_follow.accepted",
						"in": "query",
						"required": false,
						"schema": {"type": "boolean"},
						"description": "Filter to either communities you are following or waiting to follow. Requires login."
					},
					{
						"name": "local",
						"in": "query",
						"required": false,
						"schema": {"type": "boolean"},
						"description": "Filter to either local or remote communities."
					},
					{
						"name": "limit",
						"in": "query",
						"required": false,
						"schema": {"type": "integer"}
					},
					{
						"name": "page",
						"in": "query",
						"required": false,
						"schema": {"type": "string"}
					},
					{
						"name": "sort",
						"in": "query",
						"required": false,
						"schema": {"type": "string", "enum": ["old_local", "alphabetic"]}
					}
				],
				"responses": {
					"200": {
						"description": "",
						"content": {
							"application/json": {
								"schema": {
									"type": "object",
									"required": ["items", "next_page"],
									"properties": {
										"items": {
											"type": "array",
											"items": {
												"$ref": "#/components/schemas/CommunityInfo"
											}
										},
										"next_page": {
											"type": "string",
											"nullable": true
										}
									}
								}
							}
						}
					}
				},
				"security": [{"bearer": []}]
			},
			"post": {
				"summary": "Create a community",
				"requestBody": {
					"required": true,
					"content": {
						"application/json": {
							"schema": {
								"type": "object",
								"required": ["name"],
								"properties": {
									"name": {"type": "string"}
								}
							}
						}
					}
				},
				"responses": {
					"200": {
						"description": "Successfully created.",
						"content": {
							"application/json": {
								"schema": {
									"type": "object",
									"required": ["community"],
									"properties": {
										"community": {
											"type": "object",
											"required": ["id"],
											"properties": {
												"id": {"type": "integer"}
											}
										}
									}
								}
							}
						}
					}
				},
				"security": [{"bearer": []}]
			}
		},
		"/api/unstable/communities/{communityID}": {
			"get": {
				"summary": "Fetch community info",
				"parameters": [
					{
						"name": "communityID",
						"in": "path",
						"required": true,
						"schema": {"type": "integer"}
					},
					{
						"name": "include_your",
						"in": "query",
						"required": false,
						"schema": {"type": "boolean"}
					}
				],
				"responses": {
					"200": {
						"description": "",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "#/components/schemas/CommunityInfo"
								}
							}
						}
					}
				},
				"security": [{"bearer": []}]
			},
			"patch": {
				"summary": "Edit community details",
				"parameters": [
					{
						"name": "communityID",
						"in": "path",
						"required": true,
						"schema": {"type": "integer"}
					}
				],
				"requestBody": {
					"required": true,
					"content": {
						"application/json": {
							"schema": {
								"type": "object",
								"properties": {
									"description_text": {"type": "string"},
									"description_markdown": {"type": "string"},
									"description_html": {"type": "string"}
								}
							}
						}
					}
				},
				"responses": {
					"204": {"description": "Successfully modified."}
