{
  "openapi": "3.0.0",
  "info": {
    "version": "1.0.0",
    "title": "TODO list tram example view side service API",
    "license": {
      "name": "MIT"
    }
  },
  "servers": [
    {
      "url": "http://light-tram.swagger.io/v1"
    }
  ],
  "paths": {
    "/todoviews": {
      "get": {
        "summary": "List of todo task by search",
        "operationId": "listTodos",
        "tags": [
          "todos"
        ],
        "parameters": [
          {
            "name": "searchValue",
            "in": "query",
            "description": "search critiria",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "security": [
          {
            "todo_auth": [
              "read:todo"
            ]
          }
        ],
        "responses": {
          "200": {
            "description": "List of todos",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/TodoView"
                  }
                }
              }
            }
          },
          "default": {
            "description": "unexpected error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        }
      }
    }
  },
  "components": {
    "securitySchemes": {
      "todo_auth": {
        "type": "oauth2",
        "description": "This API uses OAuth 2 with the client credential grant flow.",
        "flows": {
          "clientCredentials": {
            "tokenUrl": "https://localhost:6882/token",
            "scopes": {
              "write:todos": "modify todo in your task list",
              "read:todos": "read your todo from your task list"
            }
          }
        }
      }
    },
    "schemas": {
      "TodoView": {
        "required": [
          "id",
          "title"
        ],
        "properties": {
          "id": {
            "type": "string"
          },
          "title": {
            "type": "string"
          },
          "completed": {
            "type": "boolean"
          },
          "executionOrder": {
            "type": "integer",
            "format": "int32"
          }
        }
      },
      "Error": {
        "required": [
          "code",
          "message"
        ],
        "properties": {
          "code": {
            "type": "integer",
            "format": "int32"
          },
          "message": {
            "type": "string"
          }
        }
      }
    }
  }
}
