            "ap-southeast-1",
            "ap-southeast-2",
            "ap-east-1",
            "ap-south-1",
            "sa-east-1",
            "me-south-1"
          ],
          "default": "us-east-1"
        }
      },
      "description": "The MediaPackage multi-region endpoint"
    },
    {
      "url": "http://mediapackage.{region}.amazonaws.com.cn",
      "variables": {
