                "properties": {
                  "description": {
                    "description": "A short text description of the Channel.",
                    "type": "string"
                  },
                  "id": {
                    "description": "The ID of the Channel. The ID must be unique within the region and it\ncannot be changed after a Channel is created.\n",
                    "type": "string"
