    "/harvest_jobs": {
      "post": {
        "operationId": "CreateHarvestJob",
        "description": "Creates a new HarvestJob record.",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/CreateHarvestJobResponse"
                }
              }
            }
          },
          "480": {
            "description": "UnprocessableEntityException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/UnprocessableEntityException"
                }
              }
            }
          },
          "481": {
            "description": "InternalServerErrorException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InternalServerErrorException"
                }
              }
            }
          },
          "482": {
            "description": "ForbiddenException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/ForbiddenException" }
              }
            }
          },
          "483": {
            "description": "NotFoundException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/NotFoundException" }
              }
            }
          },
          "484": {
            "description": "ServiceUnavailableException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ServiceUnavailableException"
                }
              }
            }
          },
          "485": {
            "description": "TooManyRequestsException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/TooManyRequestsException"
                }
              }
            }
          }
        },
        "parameters": [],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": [
                  "endTime",
                  "id",
                  "originEndpointId",
                  "s3Destination",
                  "startTime"
                ],
                "properties": {
                  "endTime": {
                    "description": "The end of the time-window which will be harvested\n",
                    "type": "string"
                  },
                  "id": {
                    "description": "The ID of the HarvestJob. The ID must be unique within the region\nand it cannot be changed after the HarvestJob is submitted\n",
                    "type": "string"
                  },
                  "originEndpointId": {
                    "description": "The ID of the OriginEndpoint that the HarvestJob will harvest from.\nThis cannot be changed after the HarvestJob is submitted.\n",
                    "type": "string"
                  },
                  "s3Destination": {
                    "description": "Configuration parameters for where in an S3 bucket to place the harvested content\n",
                    "type": "object",
                    "properties": {
                      "BucketName": {
                        "allOf": [
                          { "$ref": "#/components/schemas/__string" },
                          {
                            "xml": { "name": "bucketName" },
                            "description": "The name of an S3 bucket within which harvested content will be exported\n"
                          }
                        ]
                      },
                      "ManifestKey": {
                        "allOf": [
                          { "$ref": "#/components/schemas/__string" },
                          {
                            "xml": { "name": "manifestKey" },
                            "description": "The key in the specified S3 bucket where the harvested top-level manifest will be placed.\n"
                          }
                        ]
                      },
                      "RoleArn": {
                        "allOf": [
                          { "$ref": "#/components/schemas/__string" },
                          {
                            "xml": { "name": "roleArn" },
                            "description": "The IAM role used to write to the specified S3 bucket\n"
                          }
                        ]
                      }
                    }
                  },
                  "startTime": {
                    "description": "The start of the time-window which will be harvested\n",
                    "type": "string"
                  }
                }
              }
            }
          }
        },
        "summary": "Create Harvest Job"
      },
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
        { "$ref": "#/components/parameters/X-Amz-Date" },
        { "$ref": "#/components/parameters/X-Amz-Algorithm" },
        { "$ref": "#/components/parameters/X-Amz-Credential" },
        { "$ref": "#/components/parameters/X-Amz-Security-Token" },
        { "$ref": "#/components/parameters/X-Amz-Signature" },
        { "$ref": "#/components/parameters/X-Amz-SignedHeaders" }
      ],
      "get": {
        "operationId": "ListHarvestJobs",
        "description": "Returns a collection of HarvestJob records.",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ListHarvestJobsResponse"
                }
