              }
            }
          },
          "481": {
            "description": "InternalServerErrorException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InternalServerErrorException"
                }
              }
            }
          },
          "482": {
            "description": "ForbiddenException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/ForbiddenException" }
              }
            }
          },
          "483": {
            "description": "NotFoundException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/NotFoundException" }
              }
            }
          },
          "484": {
            "description": "ServiceUnavailableException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ServiceUnavailableException"
                }
              }
            }
          },
          "485": {
            "description": "TooManyRequestsException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/TooManyRequestsException"
                }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "includeChannelId",
            "in": "query",
            "required": false,
            "description": "When specified, the request will return only HarvestJobs associated with the given Channel ID.",
            "schema": { "type": "string" }
          },
          {
            "name": "includeStatus",
            "in": "query",
            "required": false,
            "description": "When specified, the request will return only HarvestJobs in the given status.",
            "schema": { "type": "string" }
          },
          {
            "name": "maxResults",
            "in": "query",
            "required": false,
            "description": "The upper bound on the number of records to return.",
            "schema": { "type": "integer", "minimum": 1, "maximum": 1000 }
          },
          {
            "name": "nextToken",
            "in": "query",
            "required": false,
            "description": "A token used to resume pagination from the end of a previous request.",
            "schema": { "type": "string" }
          },
          {
            "name": "MaxResults",
            "in": "query",
            "schema": { "type": "string" },
            "description": "Pagination limit",
            "required": false
          },
          {
            "name": "NextToken",
            "in": "query",
            "schema": { "type": "string" },
            "description": "Pagination token",
            "required": false
          }
        ],
        "summary": "List Harvest Jobs"
      }
    },
    "/origin_endpoints": {
      "post": {
        "operationId": "CreateOriginEndpoint",
        "description": "Creates a new OriginEndpoint record.",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/CreateOriginEndpointResponse"
                }
              }
            }
          },
          "480": {
            "description": "UnprocessableEntityException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/UnprocessableEntityException"
                }
              }
            }
          },
          "481": {
            "description": "InternalServerErrorException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InternalServerErrorException"
                }
              }
            }
          },
          "482": {
            "description": "ForbiddenException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/ForbiddenException" }
              }
            }
          },
          "483": {
            "description": "NotFoundException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/NotFoundException" }
              }
            }
          },
          "484": {
            "description": "ServiceUnavailableException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ServiceUnavailableException"
                }
              }
            }
          },
          "485": {
            "description": "TooManyRequestsException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/TooManyRequestsException"
                }
              }
            }
          }
        },
        "parameters": [],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": ["channelId", "id"],
                "properties": {
                  "authorization": {
                    "description": "CDN Authorization credentials",
                    "type": "object",
                    "properties": {
                      "CdnIdentifierSecret": {
                        "allOf": [
                          { "$ref": "#/components/schemas/__string" },
                          {
                            "xml": { "name": "cdnIdentifierSecret" },
                            "description": "The Amazon Resource Name (ARN) for the secret in Secrets Manager that your Content Distribution Network (CDN) uses for authorization to access your endpoint.\n"
                          }
                        ]
                      },
                      "SecretsRoleArn": {
                        "allOf": [
                          { "$ref": "#/components/schemas/__string" },
                          {
                            "xml": { "name": "secretsRoleArn" },
                            "description": "The Amazon Resource Name (ARN) for the IAM role that allows MediaPackage to communicate with AWS Secrets Manager.\n"
                          }
                        ]
                      }
                    }
                  },
                  "channelId": {
                    "description": "The ID of the Channel that the OriginEndpoint will be associated with.\nThis cannot be changed after the OriginEndpoint is created.\n",
                    "type": "string"
                  },
                  "cmafPackage": {
                    "description": "A Common Media Application Format (CMAF) packaging configuration.",
                    "type": "object",
                    "properties": {
                      "Encryption": {
                        "allOf": [
                          { "$ref": "#/components/schemas/CmafEncryption" },
                          { "xml": { "name": "encryption" } }
                        ]
                      },
                      "HlsManifests": {
                        "allOf": [
                          {
                            "$ref": "#/components/schemas/__listOfHlsManifestCreateOrUpdateParameters"
                          },
                          {
                            "xml": { "name": "hlsManifests" },
                            "description": "A list of HLS manifest configurations"
                          }
                        ]
                      },
                      "SegmentDurationSeconds": {
                        "allOf": [
                          { "$ref": "#/components/schemas/__integer" },
                          {
                            "xml": { "name": "segmentDurationSeconds" },
                            "description": "Duration (in seconds) of each segment. Actual segments will be\nrounded to the nearest multiple of the source segment duration.\n"
                          }
                        ]
                      },
                      "SegmentPrefix": {
                        "allOf": [
                          { "$ref": "#/components/schemas/__string" },
                          {
                            "xml": { "name": "segmentPrefix" },
                            "description": "An optional custom string that is prepended to the name of each segment. If not specified, it defaults to the ChannelId."
                          }
                        ]
                      },
                      "StreamSelection": {
                        "allOf": [
                          { "$ref": "#/components/schemas/StreamSelection" },
                          { "xml": { "name": "streamSelection" } }
                        ]
                      }
                    }
                  },
                  "dashPackage": {
                    "description": "A Dynamic Adaptive Streaming over HTTP (DASH) packaging configuration.",
                    "type": "object",
                    "properties": {
                      "AdTriggers": {
                        "allOf": [
                          { "$ref": "#/components/schemas/AdTriggers" },
                          { "xml": { "name": "adTriggers" } }
                        ]
                      },
                      "AdsOnDeliveryRestrictions": {
                        "allOf": [
                          {
                            "$ref": "#/components/schemas/AdsOnDeliveryRestrictions"
                          },
                          { "xml": { "name": "adsOnDeliveryRestrictions" } }
                        ]
                      },
                      "Encryption": {
                        "allOf": [
                          { "$ref": "#/components/schemas/DashEncryption" },
                          { "xml": { "name": "encryption" } }
                        ]
                      },
                      "ManifestLayout": {
                        "allOf": [
                          { "$ref": "#/components/schemas/ManifestLayout" },
                          {
                            "xml": { "name": "manifestLayout" },
                            "description": "Determines the position of some tags in the Media Presentation Description (MPD).  When set to FULL, elements like SegmentTemplate and ContentProtection are included in each Representation.  When set to COMPACT, duplicate elements are combined and presented at the AdaptationSet level."
                          }
                        ]
                      },
                      "ManifestWindowSeconds": {
                        "allOf": [
                          { "$ref": "#/components/schemas/__integer" },
                          {
                            "xml": { "name": "manifestWindowSeconds" },
                            "description": "Time window (in seconds) contained in each manifest."
                          }
                        ]
                      },
                      "MinBufferTimeSeconds": {
                        "allOf": [
                          { "$ref": "#/components/schemas/__integer" },
                          {
                            "xml": { "name": "minBufferTimeSeconds" },
                            "description": "Minimum duration (in seconds) that a player will buffer media before starting the presentation."
                          }
                        ]
                      },
                      "MinUpdatePeriodSeconds": {
                        "allOf": [
                          { "$ref": "#/components/schemas/__integer" },
                          {
                            "xml": { "name": "minUpdatePeriodSeconds" },
                            "description": "Minimum duration (in seconds) between potential changes to the Dynamic Adaptive Streaming over HTTP (DASH) Media Presentation Description (MPD)."
                          }
                        ]
                      },
                      "PeriodTriggers": {
                        "allOf": [
                          {
                            "$ref": "#/components/schemas/__listOf__PeriodTriggersElement"
                          },
                          {
                            "xml": { "name": "periodTriggers" },
                            "description": "A list of triggers that controls when the outgoing Dynamic Adaptive Streaming over HTTP (DASH)\nMedia Presentation Description (MPD) will be partitioned into multiple periods. If empty, the content will not\nbe partitioned into more than one period. If the list contains \"ADS\", new periods will be created where\nthe Channel source contains SCTE-35 ad markers.\n"
                          }
                        ]
                      },
                      "Profile": {
                        "allOf": [
