      "ProtocolDetails": {
        "type": "object",
        "properties": {
          "PassiveIp": {
            "allOf": [
              { "$ref": "#/components/schemas/PassiveIp" },
              {
                "description": "<p> Indicates passive mode, for FTP and FTPS protocols. Enter a single dotted-quad IPv4 address, such as the external IP address of a firewall, router, or load balancer. For example: </p> <p> <code> aws transfer update-server --protocol-details PassiveIp=<i>0.0.0.0</i> </code> </p> <p>Replace <code> <i>0.0.0.0</i> </code> in the example above with the actual IP address you want to use.</p> <note> <p> If you change the <code>PassiveIp</code> value, you must stop and then restart your Transfer server for the change to take effect. For details on using Passive IP (PASV) in a NAT environment, see <a href=\"http://aws.amazon.com/blogs/storage/configuring-your-ftps-server-behind-a-firewall-or-nat-with-aws-transfer-family/\">Configuring your FTPS server behind a firewall or NAT with Amazon Web Services Transfer Family</a>. </p> </note>"
              }
            ]
          }
        },
        "description": "<p> The protocol settings that are configured for your server. </p> <note> <p> This type is only valid in the <code>UpdateServer</code> API. </p> </note>"
      },
      "DirectoryId": {
        "type": "string",
        "pattern": "^d-[0-9a-f]{10}$",
        "minLength": 12,
        "maxLength": 12
      },
      "EfsFileSystemId": {
        "type": "string",
        "pattern": "^(arn:aws[-a-z]*:elasticfilesystem:[0-9a-z-:]+:(access-point/fsap|file-system/fs)-[0-9a-f]{8,40}|fs(ap)?-[0-9a-f]{8,40})$",
        "maxLength": 128
      },
      "EfsPath": {
        "type": "string",
        "pattern": "^(\\/|(\\/(?!\\.)+[^$#<>;`|&?{}^*/\\n]+){1,4})$",
        "minLength": 1,
        "maxLength": 100
      },
      "EfsFileLocation": {
        "type": "object",
        "properties": {
          "FileSystemId": {
            "allOf": [
              { "$ref": "#/components/schemas/EfsFileSystemId" },
              {
                "description": "The ID of the file system, assigned by Amazon EFS."
              }
            ]
