    "paths": {
        "/api/v2/auth/login": {
            "post": {
                "operationId": "login",
                "summary": "Login",
                "description": "Login to Floatplane with the provided username and password, retrieving the authentication/authorization cookie from the response for subsequent requests.",
                "tags": [
                    "AuthV2"
                ],
                "security": [],
                "parameters": [],
                "requestBody": {
                    "required": true,
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/AuthLoginV2Request"
                            },
                            "example": {
                                "username": "my_username",
                                "password": "my_password",
                                "captchaToken": "..."
                            }
                        }
                    }
                },
                "responses": {
                    "200": {
                        "description": "OK - Returns the header and information about the logged-in user, including the id, username, and profile image.",
                        "headers": {
                            "Set-Cookie": {
                                "description": "Contains the cookie used in subsequent authenticated requests.",
                                "schema": {
                                    "type": "string"
                                }
                            }
                        },
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/AuthLoginV2Response"
                                },
                                "example": {
                                    "user": {
                                        "id": "0123456789abcdef01234567",
                                        "username": "my_username",
                                        "profileImage": {
                                            "width": 512,
                                            "height": 512,
                                            "path": "https://pbs.floatplane.com/profile_images/default/user12.png",
                                            "childImages": [
                                                {
                                                    "width": 250,
                                                    "height": 250,
                                                    "path": "https://pbs.floatplane.com/profile_images/default/user12_250x250.png"
                                                },
                                                {
                                                    "width": 100,
                                                    "height": 100,
                                                    "path": "https://pbs.floatplane.com/profile_images/default/user12_100x100.png"
                                                }
                                            ]
                                        }
                                    },
                                    "needs2FA": false
                                }
                            }
                        },
                        "links": {
                            "twoFactorLogin": {
                                "operationId": "checkFor2faLogin",
                                "description": "If `$request.needs2FA == true`, then to complete the login process, you will need to submit the user's inputted two-factor token to this endpoint.",
