{
  "openapi": "3.0.0",
  "info": {
    "title": "Feed API",
    "description": "<p>The <strong>Feed API</strong> lets sellers upload input files, download reports and files including their status, filter reports using URI parameters, and retrieve customer service metrics task details.</p>",
    "contact": {
      "name": "eBay Inc,"
    },
    "license": {
      "name": "eBay API License Agreement",
      "url": "https://go.developer.ebay.com/api-license-agreement"
    },
    "version": "v1.2.0"
  },
  "servers": [
    {
      "url": "https://api.ebay.com{basePath}",
      "description": "Production",
      "variables": {
        "basePath": {
          "default": "/sell/feed/v1"
        }
      }
    }
  ],
  "paths": {
    "/order_task": {
      "get": {
        "tags": [
          "order_task"
        ],
        "description": "This method returns the details and status for an array of order tasks based on a specified feed_type or scheduled_id. Specifying both feed_type and scheduled_id results in an error. Since schedules are based on feed types, you can specify a schedule (scheduled_id) that returns the needed feed_type. If specifying the feed_type, limit which order tasks are returned by specifying filters such as the creation date range or period of time using look_back_days. If specifying a scheduled_id, the schedule template (that the scheduled_id is based on) determines which order tasks are returned (see scheduled_id for additional information). Each scheduled_id applies to one feed_type.",
        "operationId": "getOrderTasks",
        "parameters": [
          {
            "name": "date_range",
            "in": "query",
            "description": "The order tasks creation date range. This range is used to filter the results. The filtered results are filtered to include only tasks with a creation date that is equal to this date or is within specified range. Only orders less than 90 days old can be retrieved. Do not use with the look_back_days parameter. Format: UTC For example: Tasks within a range yyyy-MM-ddThh:mm:ss.SSSZ..yyyy-MM-ddThh:mm:ss.SSSZ Tasks created on September 8, 2019 2019-09-08T00:00:00.000Z..2019-09-09T00:00:00.000Z",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "feed_type",
            "in": "query",
            "description": "The feed type associated with the task. The only presently supported value is LMS_ORDER_REPORT. Do not use with the scheduled_id parameter. Since schedules are based on feed types, you can specify a schedule (scheduled_id) that returns the needed feed_type.",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "limit",
            "in": "query",
            "description": "The maximum number of order tasks that can be returned on each page of the paginated response. Use this parameter in conjunction with the offset parameter to control the pagination of the output. Note: This feature employs a zero-based list, where the first item in the list has an offset of 0. For example, if offset is set to 10 and limit is set to 10, the call retrieves order tasks 11 thru 20 from the result set. If this parameter is omitted, the default value is used. Default: 10 Maximum: 500",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "look_back_days",
            "in": "query",
            "description": "The number of previous days in which to search for tasks. Do not use with the date_range parameter. If both date_range and look_back_days are omitted, this parameter's default value is used. Default: 7 Range: 1-90 (inclusive)",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "offset",
            "in": "query",
            "description": "The number of order tasks to skip in the result set before returning the first order in the paginated response. Combine offset with the limit query parameter to control the items returned in the response. For example, if you supply an offset of 0 and a limit of 10, the first page of the response contains the first 10 items from the complete list of items retrieved by the call. If offset is 10 and limit is 20, the first page of the response contains items 11-30 from the complete result set. If this query parameter is not set, the default value is used and the first page of records is returned. Default: 0",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "schedule_id",
            "in": "query",
            "description": "The schedule ID associated with the order task. A schedule periodically generates a report for the feed type specified by the schedule template (see scheduleTemplateId in createSchedule). Do not use with the feed_id parameter.",
            "required": false,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/OrderTaskCollection"
                }
              }
            }
          },
          "400": {
            "description": "Bad Request",
            "x-response-codes": {
              "errors": {
                "160004": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "You must submit either a 'feed_type' or 'schedule_id'."
                },
                "160005": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "Both 'feed_type' and 'schedule_id' were provided. Please remove one of them."
                },
                "160006": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The 'feed_type' {feedTypeValue} is invalid."
                },
                "160007": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The 'schedule_id' {scheduleID} is invalid."
                },
                "160008": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "Both 'look_back_days' and 'date_range' were provided. Please remove one of them."
                },
                "160009": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The format of the 'date_range' is invalid. The format for a date range is yyyy-MM-ddThh:mm:ss.SSSZ..yyyy-MM-ddThh:mm:ss.SSSZ."
                },
                "160010": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The 'date_range' must be less than or equal to 90 days."
                },
                "160011": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The 'look_back_days' value must be greater than zero and less than or equal to 90."
                },
                "160012": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The 'limit' value must be greater than zero and less than or equal to 500."
                },
                "160013": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The 'offset' value cannot be less than zero."
                },
                "160029": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The 'offset' value must be a multiple of the 'limit' value."
                }
              }
            }
          },
          "403": {
            "description": "Forbidden",
            "x-response-codes": {
              "errors": {
                "160002": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The authentication scope {scope} is incorrect for 'feed_type' {feedType}. Refer to documentation for details about the scopes."
                }
              }
            }
          },
          "500": {
            "description": "Internal Server Error",
            "x-response-codes": {
              "errors": {
                "160001": {
                  "domain": "API_FEED",
                  "category": "APPLICATION",
                  "description": "There was a problem with an eBay internal system or process. Contact eBay Developer Technical Support for assistance."
                }
              }
            }
          }
        },
        "security": [
          {
            "api_auth": [
              "https://api.ebay.com/oauth/api_scope/sell.fulfillment"
            ]
          }
        ]
      },
      "post": {
        "tags": [
          "order_task"
        ],
        "description": "This method creates an order download task with filter criteria for the order report. When using this method, specify the feedType, schemaVersion, and filterCriteria for the report. The method returns the location response header containing the getOrderTask call URI to retrieve the order task you just created. The URL includes the eBay-assigned task ID, which you can use to reference the order task. To retrieve the status of the task, use the getOrderTask method to retrieve a single task ID or the getOrderTasks method to retrieve multiple order task IDs. Note: The scope depends on the feed type. An error message results when an unsupported scope or feed type is specified. The following list contains this method's authorization scope and its corresponding feed type: https://api.ebay.com/oauth/api_scope/sell.fulfillment: LMS_ORDER_REPORT For details about how this method is used, see General feed types in the Selling Integration Guide. Note: At this time, the createOrderTask method only supports order creation date filters and not modified order date filters. Do not include the modifiedDateRange filter in your request payload.",
        "operationId": "createOrderTask",
        "parameters": [
          {
            "name": "X-EBAY-C-MARKETPLACE-ID",
            "in": "header",
            "description": "The ID of the eBay marketplace where the item is hosted. Note: This value is case sensitive. For example: X-EBAY-C-MARKETPLACE-ID = EBAY_US This identifies the eBay marketplace that applies to this task. See MarketplaceIdEnum.",
            "required": false,
            "schema": {
              "type": "string"
            }
          }
        ],
        "requestBody": {
          "description": "description not needed",
          "content": {
            "application/json": {
              "schema": {
                "description": "description not needed",
                "$ref": "#/components/schemas/CreateOrderTaskRequest"
              }
            }
          },
          "required": true
        },
        "responses": {
          "202": {
            "description": "Accepted"
          },
          "400": {
            "description": "Bad Request",
            "x-response-codes": {
              "errors": {
                "160017": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The 'feedType' is missing or invalid."
                },
                "160018": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The 'schemaVersion' is invalid."
                },
                "160019": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The 'filterCriteria' is invalid. {additionalInfo}"
                },
                "160027": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The X-EBAY-C-MARKETPLACE-ID header is missing or invalid."
                },
                "160030": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "This resource is not applicable for the feed type {feedTypeValue}. For more information, see the documentation for this API."
                }
              }
            }
          },
          "403": {
            "description": "Forbidden",
            "x-response-codes": {
              "errors": {
                "160002": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The authentication scope {scope} is incorrect for 'feed_type' {feedType}. Refer to documentation for details about the scopes."
                },
                "160022": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "You are not allowed to access this resource. Contact eBay Developer Technical Support for assistance."
                }
              }
            }
          },
          "409": {
            "description": "Conflict",
            "x-response-codes": {
              "errors": {
                "160024": {
                  "domain": "API_FEED",
                  "category": "BUSINESS",
                  "description": "You have reached the maximum number of feed tasks that can be queued or processed concurrently. Wait for current tasks to complete before adding tasks. For more information, see the documentation for this API."
                },
                "160025": {
                  "domain": "API_FEED",
                  "category": "BUSINESS",
                  "description": "You have exceeded the maximum number of feed tasks that can be created or processed in a given period (hour or day). Wait until the present period ends before adding tasks. For more information, see the documentation for this API."
                }
              }
            }
          },
          "500": {
            "description": "Internal Server Error",
            "x-response-codes": {
              "errors": {
                "160001": {
                  "domain": "API_FEED",
                  "category": "APPLICATION",
                  "description": "There was a problem with an eBay internal system or process. Contact eBay Developer Technical Support for assistance."
                }
              }
            }
          }
        },
        "security": [
          {
            "api_auth": [
              "https://api.ebay.com/oauth/api_scope/sell.fulfillment"
            ]
          }
        ]
      }
    },
    "/order_task/{task_id}": {
      "get": {
        "tags": [
          "order_task"
        ],
        "description": "This method retrieves the task details and status of the specified task. The input is task_id. For details about how this method is used, see Working with Order Feeds in the Selling Integration Guide.",
        "operationId": "getOrderTask",
        "parameters": [
          {
            "name": "task_id",
            "in": "path",
            "description": "The ID of the task. This ID is generated when the task was created by the createOrderTask method.",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/OrderTask"
                }
              }
            }
          },
          "400": {
            "description": "Bad Request"
          },
          "403": {
            "description": "Forbidden",
            "x-response-codes": {
              "errors": {
                "160002": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The authentication scope {scope} is incorrect for 'feed_type' {feedType}. Refer to documentation for details about the scopes."
                }
              }
            }
          },
          "404": {
            "description": "Not Found",
            "x-response-codes": {
              "errors": {
                "160003": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "Task {taskID} does not exist."
                }
              }
            }
          },
          "500": {
            "description": "Internal Server Error",
            "x-response-codes": {
              "errors": {
                "160001": {
                  "domain": "API_FEED",
                  "category": "APPLICATION",
                  "description": "There was a problem with an eBay internal system or process. Contact eBay Developer Technical Support for assistance."
                }
              }
            }
          }
        },
        "security": [
          {
            "api_auth": [
              "https://api.ebay.com/oauth/api_scope/sell.fulfillment"
            ]
          }
        ]
      }
    },
    "/schedule": {
      "get": {
        "tags": [
          "schedule"
        ],
        "description": "This method retrieves an array containing the details and status of all schedules based on the specified feed_type. Use this method to find a schedule if you do not know the schedule_id.",
        "operationId": "getSchedules",
        "parameters": [
          {
            "name": "feed_type",
            "in": "query",
            "description": "The feedType associated with the schedule.",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "limit",
            "in": "query",
            "description": "The maximum number of schedules that can be returned on each page of the paginated response. Use this parameter in conjunction with the offset parameter to control the pagination of the output. Note: This feature employs a zero-based list, where the first item in the list has an offset of 0. For example, if offset is set to 10 and limit is set to 10, the call retrieves schedules 11 thru 20 from the result set. If this parameter is omitted, the default value is used. Default: 10 Maximum: 500",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "offset",
            "in": "query",
            "description": "The number of schedules to skip in the result set before returning the first schedule in the paginated response. Combine offset with the limit query parameter to control the items returned in the response. For example, if you supply an offset of 0 and a limit of 10, the first page of the response contains the first 10 items from the complete list of items retrieved by the call. If offset is 10 and limit is 20, the first page of the response contains items 11-30 from the complete result set. If this query parameter is not set, the default value is used and the first page of records is returned. Default: 0",
            "required": false,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/UserScheduleCollection"
                }
              }
            }
          },
          "400": {
            "description": "Bad Request",
            "x-response-codes": {
              "errors": {
                "160012": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The 'limit' value must be greater than zero and less than or equal to 500."
                },
                "160013": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The 'offset' value cannot be less than zero."
                },
                "160017": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The 'feedType' is missing or invalid."
                },
                "160029": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The 'offset' value must be a multiple of the 'limit' value."
                }
              }
            }
          },
          "403": {
            "description": "Forbidden",
            "x-response-codes": {
              "errors": {
                "160002": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The authentication scope {scope} is incorrect for 'feed_type' {feedType}. Refer to documentation for details about the scopes."
                }
              }
            }
          },
          "500": {
            "description": "Internal Server Error",
            "x-response-codes": {
              "errors": {
                "160001": {
                  "domain": "API_FEED",
                  "category": "APPLICATION",
                  "description": "There was a problem with an eBay internal system or process. Contact eBay Developer Technical Support for assistance."
                }
              }
            }
          }
        },
        "security": [
          {
            "api_auth": [
              "https://api.ebay.com/oauth/api_scope/sell.inventory",
              "https://api.ebay.com/oauth/api_scope/sell.fulfillment",
              "https://api.ebay.com/oauth/api_scope/sell.marketing",
              "https://api.ebay.com/oauth/api_scope/commerce.catalog.readonly",
              "https://api.ebay.com/oauth/api_scope/sell.analytics.readonly"
            ]
          }
        ]
      },
      "post": {
        "tags": [
          "schedule"
        ],
        "description": "This method creates a schedule, which is a subscription to the specified schedule template. A schedule periodically generates a report for the feedType specified by the template. Specify the same feedType as the feedType of the associated schedule template. When creating the schedule, if available from the template, you can specify a preferred trigger hour, day of the week, or day of the month. These and other fields are conditionally available as specified by the template. Note: Make sure to include all fields required by the schedule template (scheduleTemplateId). Call the getScheduleTemplate method (or the getScheduleTemplates method), to find out which fields are required or optional. If a field is optional and a default value is provided by the template, the default value will be used if omitted from the payload.A successful call returns the location response header containing the getSchedule call URI to retrieve the schedule you just created. The URL includes the eBay-assigned schedule ID, which you can use to reference the schedule task. To retrieve the details of the create schedule task, use the getSchedule method for a single schedule ID or the getSchedules method to retrieve all schedule details for the specified feed_type. The number of schedules for each feedType is limited. Error code 160031 is returned when you have reached this maximum. Note: Except for schedules with a HALF-HOUR frequency, all schedules will ideally run at the start of each hour ('00' minutes). Actual start time may vary time may vary due to load and other factors.",
        "operationId": "createSchedule",
        "requestBody": {
          "description": "In the request payload: feedType and scheduleTemplateId are required; scheduleName is optional; preferredTriggerHour, preferredTriggerDayOfWeek, preferredTriggerDayOfMonth, scheduleStartDate, scheduleEndDate, and schemaVersion are conditional.",
          "content": {
            "application/json": {
              "schema": {
                "description": "In the request payload: feedType and scheduleTemplateId are required; scheduleName is optional; preferredTriggerHour, preferredTriggerDayOfWeek, preferredTriggerDayOfMonth, scheduleStartDate, scheduleEndDate, and schemaVersion are conditional.",
                "$ref": "#/components/schemas/CreateUserScheduleRequest"
              }
            }
          },
          "required": true
        },
        "responses": {
          "201": {
            "description": "Created",
            "headers": {
              "Location": {
                "schema": {
                  "type": "string",
                  "description": "The URL of the schedule, which includes the id."
                }
              }
            },
            "content": {
              "application/json": {
                "schema": {
                  "type": "object"
                }
              }
            }
          },
          "400": {
            "description": "Bad Request",
            "x-response-codes": {
              "errors": {
                "160017": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The 'feedType' is missing or invalid."
                },
                "160032": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The 'scheduleTemplateId' is invalid. Call the getScheduleTemplates method to get the available schedule templates."
                },
                "160033": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The 'feedType' is not associated with the 'scheduleTemplateId'. Please provide a 'feedType' that matches the 'scheduleTemplateId'. Call the getScheduleTemplates method to get the available schedule templates."
                },
                "160034": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The '{fieldName}' is invalid, missing, or not allowed."
                },
                "160036": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The '{fieldName}' is in the past."
                },
                "160037": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The 'scheduleEndDate' is before 'scheduleStartDate'."
                }
              }
            }
          },
          "403": {
            "description": "Forbidden",
            "x-response-codes": {
              "errors": {
                "160002": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The authentication scope {scope} is incorrect for 'feed_type' {feedType}. Refer to documentation for details about the scopes."
                },
                "160022": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "You are not allowed to access this resource. Contact eBay Developer Technical Support for assistance."
                }
              }
            }
          },
          "409": {
            "description": "Conflict",
            "x-response-codes": {
              "errors": {
                "160031": {
                  "domain": "API_FEED",
                  "category": "BUSINESS",
                  "description": "You have reached the maximum number of subscribed schedules for the 'feedType' {feedType}. To subscribe to another schedule, you must delete one."
                },
                "160035": {
                  "domain": "API_FEED",
                  "category": "BUSINESS",
                  "description": "Duplicate schedule (a matching schedule already exists). Use the getSchedules method to see the existing schedules."
                },
                "160040": {
                  "domain": "API_FEED",
                  "category": "BUSINESS",
                  "description": "The 'scheduleTemplateId' is inactive. You cannot create or modify a schedule with an inactive 'scheduleTemplateId'."
                }
              }
            }
          },
          "500": {
            "description": "Internal Server Error",
            "x-response-codes": {
              "errors": {
                "160001": {
                  "domain": "API_FEED",
                  "category": "APPLICATION",
                  "description": "There was a problem with an eBay internal system or process. Contact eBay Developer Technical Support for assistance."
                }
              }
            }
          }
        },
        "security": [
          {
            "api_auth": [
              "https://api.ebay.com/oauth/api_scope/sell.inventory",
              "https://api.ebay.com/oauth/api_scope/sell.fulfillment",
              "https://api.ebay.com/oauth/api_scope/sell.marketing",
              "https://api.ebay.com/oauth/api_scope/commerce.catalog.readonly",
              "https://api.ebay.com/oauth/api_scope/sell.analytics.readonly"
            ]
          }
        ]
      }
    },
    "/schedule/{schedule_id}": {
      "get": {
        "tags": [
          "schedule"
        ],
        "description": "This method retrieves schedule details and status of the specified schedule. Specify the schedule to retrieve using the schedule_id. Use the getSchedules method to find a schedule if you do not know the schedule_id.",
        "operationId": "getSchedule",
        "parameters": [
          {
            "name": "schedule_id",
            "in": "path",
            "description": "The ID of the schedule for which to retrieve the details. This ID is generated when the schedule was created by the createSchedule method.",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/UserScheduleResponse"
                }
              }
            }
          },
          "400": {
            "description": "Bad Request"
          },
          "403": {
            "description": "Forbidden",
            "x-response-codes": {
              "errors": {
                "160002": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The authentication scope {scope} is incorrect for 'feed_type' {feedType}. Refer to documentation for details about the scopes."
                }
              }
            }
          },
          "404": {
            "description": "Not Found",
            "x-response-codes": {
              "errors": {
                "160038": {
                  "domain": "API_FEED",
                  "category": "REQUEST",
                  "description": "The schedule id {schedule_id} does not exist."
                }
              }
            }
          },
          "500": {
