{
  "openapi": "3.0.1",
  "info": {
    "title": "Keycloak REST Api",
    "description": "This is a REST API reference for the Keycloak Admin",
    "version": "1"
  },
  "paths": {
    "/": {
      "get": {
        "tags": [
          "RealmsAdmin"
        ],
        "description": "/\nGet accessible realms\n\nReturns a list of accessible realms. The list is filtered based on what realms the caller is allowed to view.\n\n",
        "parameters": [
          {
            "name": "briefRepresentation",
            "in": "query",
            "schema": {
              "type": "boolean"
            }
          }
        ],
        "responses": {
          "2XX": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/RealmRepresentation"
                  }
                }
              }
            }
          }
        }
      },
      "post": {
        "tags": [
          "RealmsAdmin"
        ],
        "description": "/\nImport a realm\n\nImports a realm from a full representation of that realm.  Realm name must be unique.\n\n",
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/RealmRepresentation"
              }
            }
          }
        },
        "responses": {
          "2XX": {
            "description": "Success"
          }
        }
      }
    },
    "/{realm}": {
      "description": "/{realm}",
      "get": {
        "tags": [
          "RealmAdmin"
        ],
        "description": "/\nGet the top-level representation of the realm\n\nIt will not include nested information like User and Client representations.\n\n",
        "responses": {
          "2XX": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/RealmRepresentation"
                }
              }
            }
          }
        }
      },
      "put": {
        "tags": [
          "RealmAdmin"
        ],
        "description": "/\nUpdate the top-level information of the realm\n\nAny user, roles or client information in the representation\nwill be ignored.  This will only update top-level attributes of the realm.\n\n",
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/RealmRepresentation"
              }
            }
          }
        },
        "responses": {
          "2XX": {
            "description": "Success"
          }
        }
      },
      "delete": {
        "tags": [
          "RealmAdmin"
        ],
        "description": "/\nDelete the realm\n\n/\n",
        "responses": {
          "2XX": {
            "description": "Success"
          }
        }
      },
      "parameters": [
        {
          "name": "realm",
          "in": "path",
          "description": "realm name (not id!)",
          "required": true,
          "schema": {
            "type": "string"
          }
        }
      ]
    },
    "/{realm}/admin-events": {
      "description": "/{realm}/admin-events",
      "get": {
        "tags": [
          "RealmAdmin"
        ],
        "description": "/\nBase path for the admin REST API for one particular realm.\n\n",
        "parameters": [
          {
            "name": "operationTypes",
            "in": "query",
            "schema": {
              "type": "array",
              "items": {
                "type": "string"
              }
            }
          },
          {
            "name": "authRealm",
            "in": "query",
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "authClient",
            "in": "query",
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "authUser",
            "in": "query",
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "authIpAddress",
            "in": "query",
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "resourcePath",
            "in": "query",
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "dateFrom",
            "in": "query",
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "dateTo",
            "in": "query",
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "first",
            "in": "query",
            "schema": {
              "type": "integer",
              "format": "int32"
            }
          },
          {
            "name": "max",
            "in": "query",
            "schema": {
              "type": "integer",
              "format": "int32"
            }
          },
          {
            "name": "resourceTypes",
            "in": "query",
            "schema": {
              "type": "array",
              "items": {
                "type": "string"
              }
            }
          }
        ],
        "responses": {
          "2XX": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/AdminEventRepresentation"
                  }
                }
              }
            }
          }
        }
      },
      "delete": {
        "tags": [
          "RealmAdmin"
        ],
        "description": "/\nDelete all admin events\n\n/\n",
        "responses": {
          "2XX": {
            "description": "Success"
          }
        }
      },
      "parameters": [
        {
          "name": "realm",
          "in": "path",
          "description": "realm name (not id!)",
          "required": true,
          "schema": {
            "type": "string"
          }
        }
      ]
    },
    "/{realm}/attack-detection/brute-force/users": {
      "description": "/{realm}/attack-detection/brute-force/users",
      "delete": {
        "tags": [
          "AttackDetection"
        ],
        "description": "/\nClear any user login failures for all users\n\nThis can release temporary disabled users\n\n/\n",
        "responses": {
          "2XX": {
            "description": "Success"
