              }
            }
          }
        }
      },
      "delete": {
        "tags": [
          "AttackDetection"
        ],
        "description": "/\nClear any user login failures for the user\n\nThis can release temporary disabled user\n\n",
        "responses": {
          "2XX": {
            "description": "Success"
          }
        }
      },
      "parameters": [
        {
          "name": "realm",
          "in": "path",
          "description": "realm name (not id!)",
          "required": true,
          "schema": {
            "type": "string"
          }
        },
        {
          "name": "userId",
          "in": "path",
          "description": "",
          "required": true,
          "schema": {
            "type": "string"
          }
        }
      ]
    },
