{
  "openapi": "3.0.0",
  "info": {
    "version": "2018-04-23",
    "x-release": "v4",
    "title": "AWS MediaTailor",
    "description": "Use the AWS Elemental MediaTailor SDK to configure scalable ad insertion for your live and VOD content. With AWS Elemental MediaTailor, you can serve targeted ads to viewers while maintaining broadcast quality in over-the-top (OTT) video applications. For information about using the service, including detailed information about the settings covered in this guide, see the AWS Elemental MediaTailor User Guide.Through the SDK, you manage AWS Elemental MediaTailor configurations the same as you do through the console. For example, you specify ad insertion behavior and mapping information for the origin server and the ad decision server (ADS).",
    "x-logo": {
      "url": "https://raw.githubusercontent.com/chainjet/assets/master/integrations/aws/Arch_AWS-Elemental-MediaTailor_64.svg"
    },
    "termsOfService": "https://aws.amazon.com/service-terms/",
    "contact": {
      "name": "Mike Ralphson",
      "email": "mike.ralphson@gmail.com",
      "url": "https://github.com/mermade/aws2openapi",
      "x-twitter": "PermittedSoc"
    },
    "license": {
      "name": "Apache 2.0 License",
      "url": "http://www.apache.org/licenses/"
    },
    "x-providerName": "amazonaws.com",
    "x-serviceName": "mediatailor",
    "x-origin": [
      {
        "contentType": "application/json",
        "url": "https://raw.githubusercontent.com/aws/aws-sdk-js/master/apis/mediatailor-2018-04-23.normal.json",
        "converter": {
          "url": "https://github.com/mermade/aws2openapi",
          "version": "1.0.0"
        },
        "x-apisguru-driver": "external"
      }
    ],
    "x-apiClientRegistration": {
      "url": "https://portal.aws.amazon.com/gp/aws/developer/registration/index.html?nc2=h_ct"
    },
    "x-apisguru-categories": ["cloud"],
    "x-preferred": true
  },
  "externalDocs": {
    "description": "Amazon Web Services documentation",
    "url": "https://docs.aws.amazon.com/mediatailor/"
  },
  "servers": [
    {
      "url": "http://api.mediatailor.{region}.amazonaws.com",
      "variables": {
        "region": {
          "description": "The AWS region",
          "enum": [
            "us-east-1",
            "us-east-2",
            "us-west-1",
            "us-west-2",
            "us-gov-west-1",
            "us-gov-east-1",
            "ca-central-1",
            "eu-north-1",
            "eu-west-1",
            "eu-west-2",
            "eu-west-3",
            "eu-central-1",
            "eu-south-1",
            "af-south-1",
            "ap-northeast-1",
            "ap-northeast-2",
            "ap-northeast-3",
            "ap-southeast-1",
            "ap-southeast-2",
            "ap-east-1",
            "ap-south-1",
            "sa-east-1",
            "me-south-1"
          ],
          "default": "us-east-1"
        }
      },
      "description": "The MediaTailor multi-region endpoint"
    },
    {
      "url": "https://api.mediatailor.{region}.amazonaws.com",
      "variables": {
        "region": {
          "description": "The AWS region",
          "enum": [
            "us-east-1",
            "us-east-2",
            "us-west-1",
            "us-west-2",
            "us-gov-west-1",
            "us-gov-east-1",
            "ca-central-1",
            "eu-north-1",
            "eu-west-1",
            "eu-west-2",
            "eu-west-3",
            "eu-central-1",
            "eu-south-1",
            "af-south-1",
            "ap-northeast-1",
            "ap-northeast-2",
            "ap-northeast-3",
            "ap-southeast-1",
            "ap-southeast-2",
            "ap-east-1",
            "ap-south-1",
            "sa-east-1",
            "me-south-1"
          ],
          "default": "us-east-1"
        }
      },
      "description": "The MediaTailor multi-region endpoint"
    },
    {
      "url": "http://api.mediatailor.{region}.amazonaws.com.cn",
      "variables": {
        "region": {
          "description": "The AWS region",
          "enum": ["cn-north-1", "cn-northwest-1"],
          "default": "cn-north-1"
        }
      },
      "description": "The MediaTailor endpoint for China (Beijing) and China (Ningxia)"
    },
    {
      "url": "https://api.mediatailor.{region}.amazonaws.com.cn",
      "variables": {
        "region": {
          "description": "The AWS region",
          "enum": ["cn-north-1", "cn-northwest-1"],
          "default": "cn-north-1"
        }
      },
      "description": "The MediaTailor endpoint for China (Beijing) and China (Ningxia)"
    }
  ],
  "x-hasEquivalentPaths": true,
  "paths": {
    "/playbackConfiguration/{Name}": {
      "delete": {
        "operationId": "DeletePlaybackConfiguration",
        "description": "Deletes the playback configuration for the specified name. ",
        "responses": {
          "204": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/DeletePlaybackConfigurationResponse"
                }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "Name",
            "in": "path",
            "required": true,
            "description": "The identifier for the playback configuration.",
            "schema": { "type": "string" }
          }
        ],
        "summary": "Delete Playback Configuration"
      },
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
        { "$ref": "#/components/parameters/X-Amz-Date" },
        { "$ref": "#/components/parameters/X-Amz-Algorithm" },
        { "$ref": "#/components/parameters/X-Amz-Credential" },
        { "$ref": "#/components/parameters/X-Amz-Security-Token" },
        { "$ref": "#/components/parameters/X-Amz-Signature" },
        { "$ref": "#/components/parameters/X-Amz-SignedHeaders" }
      ],
      "get": {
        "operationId": "GetPlaybackConfiguration",
        "description": "Returns the playback configuration for the specified name. ",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/GetPlaybackConfigurationResponse"
                }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "Name",
            "in": "path",
            "required": true,
            "description": "The identifier for the playback configuration.",
            "schema": { "type": "string" }
          }
        ],
        "summary": "Get Playback Configuration"
      }
    },
    "/playbackConfigurations": {
      "get": {
        "operationId": "ListPlaybackConfigurations",
        "description": "Returns a list of the playback configurations defined in AWS Elemental MediaTailor. You can specify a maximum number of configurations to return at a time. The default maximum is 50. Results are returned in pagefuls. If MediaTailor has more configurations than the specified maximum, it provides parameters in the response that you can use to retrieve the next pageful. ",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ListPlaybackConfigurationsResponse"
                }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "MaxResults",
            "in": "query",
            "required": false,
            "description": "Maximum number of records to return. ",
            "schema": { "type": "integer", "minimum": 1, "maximum": 100 }
          },
          {
            "name": "NextToken",
            "in": "query",
            "required": false,
            "description": "Pagination token returned by the GET list request when results exceed the maximum allowed. Use the token to fetch the next page of results.",
            "schema": { "type": "string" }
          }
        ],
        "summary": "List Playback Configurations"
      },
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
        { "$ref": "#/components/parameters/X-Amz-Date" },
        { "$ref": "#/components/parameters/X-Amz-Algorithm" },
        { "$ref": "#/components/parameters/X-Amz-Credential" },
        { "$ref": "#/components/parameters/X-Amz-Security-Token" },
        { "$ref": "#/components/parameters/X-Amz-Signature" },
        { "$ref": "#/components/parameters/X-Amz-SignedHeaders" }
      ]
    },
    "/tags/{ResourceArn}": {
      "get": {
        "operationId": "ListTagsForResource",
        "description": "Returns a list of the tags assigned to the specified playback configuration resource. ",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ListTagsForResourceResponse"
                }
              }
            }
          },
          "480": {
            "description": "BadRequestException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/BadRequestException" }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "ResourceArn",
            "in": "path",
            "required": true,
            "description": "The Amazon Resource Name (ARN) for the playback configuration. You can get this from the response to any playback configuration request. ",
            "schema": { "type": "string" }
          }
        ],
        "summary": "List Tags For Resource"
      },
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
        { "$ref": "#/components/parameters/X-Amz-Date" },
        { "$ref": "#/components/parameters/X-Amz-Algorithm" },
        { "$ref": "#/components/parameters/X-Amz-Credential" },
        { "$ref": "#/components/parameters/X-Amz-Security-Token" },
        { "$ref": "#/components/parameters/X-Amz-Signature" },
        { "$ref": "#/components/parameters/X-Amz-SignedHeaders" }
      ],
      "post": {
        "operationId": "TagResource",
        "description": "Adds tags to the specified playback configuration resource. You can specify one or more tags to add. ",
        "responses": {
          "204": { "description": "Success" },
          "480": {
            "description": "BadRequestException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/BadRequestException" }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "ResourceArn",
            "in": "path",
            "required": true,
            "description": "The Amazon Resource Name (ARN) for the playback configuration. You can get this from the response to any playback configuration request. ",
            "schema": { "type": "string" }
          }
        ],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": ["tags"],
                "properties": {
                  "tags": {
                    "description": "A comma-separated list of tag key:value pairs. For example: \n {\n \"Key1\": \"Value1\",\n \"Key2\": \"Value2\"\n }\n ",
                    "type": "object",
                    "additionalProperties": {
                      "$ref": "#/components/schemas/__string"
                    }
                  }
                }
              }
            }
          }
        },
        "summary": "Tag Resource"
      }
    },
    "/playbackConfiguration": {
      "put": {
        "operationId": "PutPlaybackConfiguration",
        "description": "Adds a new playback configuration to AWS Elemental MediaTailor. ",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/PutPlaybackConfigurationResponse"
                }
              }
            }
          }
        },
        "parameters": [],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "AdDecisionServerUrl": {
                    "description": "The URL for the ad decision server (ADS). This includes the specification of static parameters and placeholders for dynamic parameters. AWS Elemental MediaTailor substitutes player-specific and session-specific parameters as needed when calling the ADS. Alternately, for testing you can provide a static VAST URL. The maximum length is 25,000 characters.",
                    "type": "string"
                  },
                  "AvailSuppression": {
                    "description": "The configuration for Avail Suppression. Ad suppression can be used to turn off ad personalization in a long manifest, or if a viewer joins mid-break.",
                    "type": "object",
                    "properties": {
                      "Mode": {
                        "allOf": [
                          { "$ref": "#/components/schemas/Mode" },
                          {
                            "description": "Sets the mode for avail suppression, also known as ad suppression. By default, ad suppression is off and all ad breaks are filled by MediaTailor with ads or slate."
                          }
                        ]
                      },
                      "Value": {
                        "allOf": [
                          { "$ref": "#/components/schemas/__string" },
                          {
                            "description": "The avail suppression value is a live edge offset time in HH:MM:SS. MediaTailor won't fill ad breaks on or behind this time in the manifest lookback window. "
                          }
                        ]
                      }
                    }
                  },
                  "Bumper": {
                    "description": "The configuration for bumpers. Bumpers are short audio or video clips that play at the start or before the end of an ad break. ",
                    "type": "object",
                    "properties": {
                      "EndUrl": {
                        "allOf": [
                          { "$ref": "#/components/schemas/__string" },
                          {
                            "description": "The URL for the end bumper asset. "
                          }
                        ]
                      },
                      "StartUrl": {
                        "allOf": [
                          { "$ref": "#/components/schemas/__string" },
                          {
                            "description": "The URL for the start bumper asset. "
                          }
                        ]
                      }
                    }
                  },
                  "CdnConfiguration": {
                    "description": "The configuration for using a content delivery network (CDN), like Amazon CloudFront, for content and ad segment management. ",
                    "type": "object",
                    "properties": {
                      "AdSegmentUrlPrefix": {
                        "allOf": [
                          { "$ref": "#/components/schemas/__string" },
                          {
                            "description": "A non-default content delivery network (CDN) to serve ad segments. By default, AWS Elemental MediaTailor uses Amazon CloudFront with default cache settings as its CDN for ad segments. To set up an alternate CDN, create a rule in your CDN for the following origin: ads.mediatailor.&lt;region>.amazonaws.com. Then specify the rule's name in this AdSegmentUrlPrefix. When AWS Elemental MediaTailor serves a manifest, it reports your CDN as the source for ad segments."
                          }
                        ]
                      },
                      "ContentSegmentUrlPrefix": {
                        "allOf": [
                          { "$ref": "#/components/schemas/__string" },
                          {
                            "description": "A content delivery network (CDN) to cache content segments, so that content requests don’t always have to go to the origin server. First, create a rule in your CDN for the content segment origin server. Then specify the rule's name in this ContentSegmentUrlPrefix. When AWS Elemental MediaTailor serves a manifest, it reports your CDN as the source for content segments."
                          }
                        ]
                      }
                    }
                  },
                  "DashConfiguration": {
                    "description": "The configuration for DASH PUT operations. ",
                    "type": "object",
                    "properties": {
                      "MpdLocation": {
                        "allOf": [
                          { "$ref": "#/components/schemas/__string" },
                          {
                            "description": "The setting that controls whether MediaTailor includes the Location tag in DASH manifests. MediaTailor populates the Location tag with the URL for manifest update requests, to be used by players that don't support sticky redirects. Disable this if you have CDN routing rules set up for accessing MediaTailor manifests, and you are either using client-side reporting or your players support sticky HTTP redirects. Valid values are DISABLED and EMT_DEFAULT. The EMT_DEFAULT setting enables the inclusion of the tag and is the default value. "
                          }
                        ]
                      },
                      "OriginManifestType": {
                        "allOf": [
                          { "$ref": "#/components/schemas/OriginManifestType" },
                          {
                            "description": "The setting that controls whether MediaTailor handles manifests from the origin server as multi-period manifests or single-period manifests. If your origin server produces single-period manifests, set this to SINGLE_PERIOD. The default setting is MULTI_PERIOD. For multi-period manifests, omit this setting or set it to MULTI_PERIOD. "
                          }
                        ]
                      }
                    }
                  },
                  "LivePreRollConfiguration": {
                    "description": "The configuration for pre-roll ad insertion.",
                    "type": "object",
                    "properties": {
                      "AdDecisionServerUrl": {
                        "allOf": [
                          { "$ref": "#/components/schemas/__string" },
                          {
                            "description": "The URL for the ad decision server (ADS) for pre-roll ads. This includes the specification of static parameters and placeholders for dynamic parameters. AWS Elemental MediaTailor substitutes player-specific and session-specific parameters as needed when calling the ADS. Alternately, for testing, you can provide a static VAST URL. The maximum length is 25,000 characters."
                          }
                        ]
                      },
                      "MaxDurationSeconds": {
                        "allOf": [
                          { "$ref": "#/components/schemas/__integer" },
                          {
                            "description": "The maximum allowed duration for the pre-roll ad avail. AWS Elemental MediaTailor won't play pre-roll ads to exceed this duration, regardless of the total duration of ads that the ADS returns."
                          }
                        ]
                      }
                    }
                  },
                  "Name": {
                    "description": "The identifier for the playback configuration.",
                    "type": "string"
                  },
                  "PersonalizationThresholdSeconds": {
                    "description": "The maximum duration of underfilled ad time (in seconds) allowed in an ad break.",
                    "type": "integer",
                    "minimum": 1
                  },
                  "SlateAdUrl": {
                    "description": "The URL for a high-quality video asset to transcode and use to fill in time that's not used by ads. AWS Elemental MediaTailor shows the slate to fill in gaps in media content. Configuring the slate is optional for non-VPAID configurations. For VPAID, the slate is required because MediaTailor provides it in the slots that are designated for dynamic ad content. The slate must be a high-quality asset that contains both audio and video. ",
                    "type": "string"
                  },
                  "tags": {
                    "description": "The tags to assign to the playback configuration. ",
                    "type": "object",
                    "additionalProperties": {
                      "$ref": "#/components/schemas/__string"
                    }
                  },
                  "TranscodeProfileName": {
                    "description": "The name that is used to associate this playback configuration with a custom transcode profile. This overrides the dynamic transcoding defaults of MediaTailor. Use this only if you have already set up custom profiles with the help of AWS Support.",
                    "type": "string"
                  },
                  "VideoContentSourceUrl": {
                    "description": "The URL prefix for the master playlist for the stream, minus the asset ID. The maximum length is 512 characters.",
                    "type": "string"
                  },
                  "ManifestProcessingRules": {
                    "description": "The configuration for manifest processing rules. Manifest processing rules enable customization of the personalized manifests created by MediaTailor.",
                    "type": "object",
                    "properties": {
                      "AdMarkerPassthrough": {
                        "$ref": "#/components/schemas/AdMarkerPassthrough",
                        "allOf": [
                          {
                            "$ref": "#/components/schemas/AdMarkerPassthrough"
                          },
                          {
                            "description": "<p>For HLS, when set to true, MediaTailor passes through EXT-X-CUE-IN, EXT-X-CUE-OUT, and EXT-X-SPLICEPOINT-SCTE35 ad markers from the origin manifest to the MediaTailor personalized manifest.</p> <p>No logic is applied to these ad markers. For example, if EXT-X-CUE-OUT has a value of 60, but no ads are filled for that ad break, MediaTailor will not set the value to 0.</p>"
                          }
                        ]
                      }
                    }
                  },
                  "ConfigurationAliases": {
                    "description": "The predefined aliases for dynamic variables.",
                    "type": "object",
                    "additionalProperties": {
                      "$ref": "#/components/schemas/__mapOf__string"
                    }
                  }
                }
              }
            }
          }
        },
        "summary": "Put Playback Configuration"
      },
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
        { "$ref": "#/components/parameters/X-Amz-Date" },
        { "$ref": "#/components/parameters/X-Amz-Algorithm" },
        { "$ref": "#/components/parameters/X-Amz-Credential" },
        { "$ref": "#/components/parameters/X-Amz-Security-Token" },
        { "$ref": "#/components/parameters/X-Amz-Signature" },
        { "$ref": "#/components/parameters/X-Amz-SignedHeaders" }
      ]
    },
    "/tags/{ResourceArn}#tagKeys": {
      "delete": {
        "operationId": "UntagResource",
        "description": "Removes tags from the specified playback configuration resource. You can specify one or more tags to remove. ",
        "responses": {
          "204": { "description": "Success" },
          "480": {
            "description": "BadRequestException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/BadRequestException" }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "ResourceArn",
            "in": "path",
            "required": true,
            "description": "The Amazon Resource Name (ARN) for the playback configuration. You can get this from the response to any playback configuration request. ",
            "schema": { "type": "string" }
          },
          {
            "name": "tagKeys",
            "in": "query",
            "required": true,
            "description": "A comma-separated list of the tag keys to remove from the playback configuration. ",
            "schema": {
              "type": "array",
              "items": { "$ref": "#/components/schemas/__string" }
            }
          }
        ],
        "summary": "Untag Resource"
      },
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
        { "$ref": "#/components/parameters/X-Amz-Date" },
        { "$ref": "#/components/parameters/X-Amz-Algorithm" },
        { "$ref": "#/components/parameters/X-Amz-Credential" },
        { "$ref": "#/components/parameters/X-Amz-Security-Token" },
        { "$ref": "#/components/parameters/X-Amz-Signature" },
        { "$ref": "#/components/parameters/X-Amz-SignedHeaders" }
      ]
    },
    "/channel/{channelName}": {
      "post": {
        "operationId": "CreateChannel",
        "description": "Creates a channel.",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/CreateChannelResponse"
                }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "channelName",
            "in": "path",
            "required": true,
            "description": "The identifier for the channel you are working on.",
            "schema": { "type": "string" }
          }
        ],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": ["Outputs", "PlaybackMode"],
                "properties": {
                  "FillerSlate": {
                    "description": "Slate VOD source configuration.",
                    "type": "object",
                    "properties": {
                      "SourceLocationName": {
                        "allOf": [
                          { "$ref": "#/components/schemas/__string" },
                          {
                            "description": "The name of the source location where the slate VOD source is stored."
                          }
                        ]
                      },
                      "VodSourceName": {
                        "allOf": [
                          { "$ref": "#/components/schemas/__string" },
                          {
                            "description": "The slate VOD source name. The VOD source must already exist in a source location before it can be used for slate."
                          }
                        ]
                      }
                    }
                  },
                  "Outputs": {
                    "description": "An object that represents an object in the CreateChannel request.",
                    "type": "array",
                    "items": {
                      "$ref": "#/components/schemas/RequestOutputItem"
                    }
                  },
                  "PlaybackMode": {
                    "description": "<p>The type of playback mode to use for this channel.</p> <p>LINEAR - The programs in the schedule play once back-to-back in the schedule.</p> <p>LOOP - The programs in the schedule play back-to-back in an endless loop. When the last program in the schedule stops playing, playback loops back to the first program in the schedule.</p>",
                    "enum": ["LOOP", "LINEAR"],
                    "type": "string"
                  },
                  "tags": {
                    "description": "The tags to assign to the channel.",
                    "type": "object",
                    "additionalProperties": {
                      "$ref": "#/components/schemas/__string"
                    }
                  }
                }
              }
            }
          }
        },
        "summary": "Create Channel"
      },
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
        { "$ref": "#/components/parameters/X-Amz-Date" },
        { "$ref": "#/components/parameters/X-Amz-Algorithm" },
        { "$ref": "#/components/parameters/X-Amz-Credential" },
        { "$ref": "#/components/parameters/X-Amz-Security-Token" },
        { "$ref": "#/components/parameters/X-Amz-Signature" },
        { "$ref": "#/components/parameters/X-Amz-SignedHeaders" }
      ],
      "delete": {
        "operationId": "DeleteChannel",
        "description": "Deletes a channel. You must stop the channel before it can be deleted.",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/DeleteChannelResponse"
                }
              }
            }
          }
        },
