{
  "openapi": "3.0.0",
  "info": {
    "title": "Telstra Programmable Network API",
    "description": "Telstra Programmable Network is a self-provisioning platform that allows its users to create on-demand connectivity services between multiple end-points and add various network functions to those services. Programmable Network enables to connectivity to a global ecosystem of networking services as well as public and private cloud services. Once you are connected to the platform on one or more POPs (points of presence), you can start creating those services based on the use case that you want to accomplish. The Programmable Network API is available to all customers who have registered to use the Programmable Network.\nTo register, please contact your account representative.",
    "version": "2.5.1",
    "contact": {
      "name": "Telstra Corporation - Programmable Network API",
      "url": "https://dev.telstra.com/content/programmable-network-api",
      "email": "pnapi-support@team.telstra.com"
    }
  },
  "servers": [
    {
      "url": "https://penapi.pacnetconnect.com",
      "description": "Production Public API"
    }
  ],
  "components": {
    "securitySchemes": {
      "oAuth2": {
        "type": "oauth2",
        "description": "OAuth 2 with password grant flow",
        "flows": {
          "password": {
            "tokenUrl": "https://penapi.pacnetconnect.com/1.0.0/auth/generatetoken",
            "scopes": {}
          }
        }
      }
    },
    "schemas": {
      "contractresponse-fragment": {
        "type": "object",
        "properties": {
          "params": {
            "type": "object",
            "properties": {
              "duration": {
                "$ref": "#/components/schemas/duration.mins"
              },
              "bandwidth": {
                "$ref": "#/components/schemas/bandwidth.kbs"
              },
              "linkid": {
                "$ref": "#/components/schemas/linkid"
              },
              "contractid": {
                "$ref": "#/components/schemas/contractid"
              },
              "currencyId": {
                "$ref": "#/components/schemas/currencyID"
              },
              "price": {
                "$ref": "#/components/schemas/price"
              },
              "renewal-option": {
                "$ref": "#/components/schemas/renewal-option"
              }
            }
          }
        }
      },
      "createcustomerrequest": {
        "required": [
          "companyname",
          "parentcompanyuuid",
          "address",
          "country",
          "city",
          "customeraccountid",
          "customertype",
          "currency",
          "billingaccountid",
          "parentcustomeraccountid"
        ],
        "type": "object",
        "properties": {
          "companyname": {
            "type": "string",
            "description": "Name of the company/division/group this customer tenancy represents",
            "example": "Engineering Division"
          },
          "parentcompanyuuid": {
            "type": "string",
            "description": "Unique identifier of the parent customer tenancy"
          },
          "address": {
            "type": "string",
            "description": "Primary address for the company/division/group this customer tenancy represents"
          },
          "country": {
            "type": "string",
            "description": "Three-letter country code of the location of the company/division/group this customer tenancy represents",
            "example": "AUS"
          },
          "city": {
            "type": "string",
            "description": "The city for the location of the company/division/group this customer tenancy represents",
            "example": "Melbourne"
          },
          "customeraccountid": {
            "type": "string"
          },
          "customertype": {
            "type": "string",
            "example": "External Paying"
          },
          "currency": {
            "type": "string",
            "description": "Currency in which resources created in this customer tenancy will be billed",
            "example": "AUD"
          },
          "billingaccountid": {
            "type": "string",
            "example": "3488394955"
          },
          "parentcustomeraccountid": {
            "type": "string"
          },
          "phone": {
            "type": "string",
            "description": "Phone number to be associated with the company/division/group this customer tenancy represents"
          },
          "fax": {
            "type": "string",
            "description": "Fax number to be associated with the company/division/group this customer tenancy represents"
          },
          "postalcode": {
            "type": "string",
            "description": "Postal code of the location of the company/division/group this customer tenancy represents"
          },
          "website": {
            "type": "string",
            "description": "Web URL to be associated with the company/division/group this customer tenancy represents"
          }
        }
      },
      "customerdetails": {
        "type": "object",
        "properties": {
          "phone": {
            "type": "string",
            "description": "Phone number associated with this customer tenancy (used for display/reference purposes only)",
            "example": "null"
          },
          "city": {
            "type": "string",
            "description": "City associated with this customer tenancy (used for display/reference purposes only)",
            "example": "Melbourne"
          },
          "fax": {
            "type": "string",
            "description": "Fax number associated with this customer tenancy (used for display/reference purposes only)",
            "example": "null"
          },
          "country": {
            "type": "string",
            "description": "Country code associated with this customer tenancy (used for display/reference purposes only)",
            "example": "AUS"
          },
          "address": {
            "type": "string",
            "description": "Address associated with this customer tenancy (used for display/reference purposes only)",
            "example": "150 Lonsdale St."
          },
          "website": {
            "type": "string",
            "description": "Web site associated with this customer tenancy (used for display/reference purposes only)",
            "example": "http://www.example.com"
          },
          "billingaccountuuid": {
            "type": "string",
            "example": "3008ef0b-7f5e-44c0-9ab5-13d07a4acee8",
            "description": "Unique identifier for the billing account used by this customer tenancy"
          },
          "enforce2FA": {
            "type": "boolean",
            "description": "Flag indicating whether two-factor authentication is enforced for the users of this customer tenancy",
            "example": false
          },
          "currency": {
            "type": "string",
            "description": "Currency in which billing will be calculated for this customer tenancy",
            "example": "AUD"
          },
          "companyuuid": {
            "type": "string",
            "description": "Unique identifier for this customer tenancy",
            "example": "9bbfc3a8-f32c-4658-8a81-513274ae25f0"
          },
          "companyname": {
            "type": "string",
            "description": "Company Name associated with this customer tenancy (used for display/reference purposes only)",
            "example": "Engineering"
          },
          "countryname": {
            "type": "string",
            "description": "Country Name associated with this customer tenancy",
            "example": "Australia"
          },
          "postalcode": {
            "type": "string",
            "description": "Postal Code associated with this customer tenancy",
            "example": "3000"
          },
          "customertype": {
            "type": "string",
            "description": "Internal use",
            "example": "External paying"
          },
          "status": {
            "type": "string",
            "description": "Internal use",
            "example": "1"
          },
          "customeraccountid": {
            "type": "string",
            "description": "Account identifier used for billing purposes",
            "example": "4099099282"
          },
          "parentcompanyuuid": {
            "type": "string",
            "description": "Unique identifier for the parent of this customer tenancy",
            "example": "c2d7358d-d46f-4e2b-8472-0882d6edda1c"
          },
          "parentcustomeraccountid": {
            "type": "string",
            "description": "Account identifier for the parent customer of this customer tenancy, used for billing purposes",
            "example": "1234"
          },
          "domainid": {
            "type": "string",
            "description": "The domain identifier for this customer tenancy, used for authenticating users to this customer tenancy",
            "example": "714635132656"
          },
          "parentcompanyname": {
            "type": "string",
            "description": "Company Name associated with the parent customer tenancy for this customer tenancy (provided for convenience and used for display/reference purposes only)",
            "example": "TPN Demo"
          },
          "billingaccountid": {
            "type": "string",
            "description": "Billing account identifier for the parent customer of this customer tenancy, used for billing purposes",
            "example": "4099099282"
          },
          "idp_type": {
            "type": "string",
            "description": "\"Indicates the Identity Provider type. Currently, the possible values are: 'Default' for the Programmable Network Identity Provider, 'saml' for an external Identity Provider using the SAML standard, and 'oauth' for an external Identity Provider using the OAuth standard\"",
            "example": "Default"
          },
          "createdon": {
            "example": 1535956880000,
            "type": "integer"
          },
          "nonconsenting": {
            "type": "string",
            "description": "Internal use",
            "example": "null"
          }
        }
      },
      "topotagupdaterequest": {
        "type": "object",
        "properties": {
          "name": {
            "type": "string",
            "description": "A short human-friendly name which is visible in the UI to select the topology",
            "example": "Production WAN Updated Name"
          },
          "description": {
            "description": "A longer human-friendly description which is visible in the UI when looking at the topology detail.",
            "type": "string",
            "example": "Updated topology for WAN"
          }
        }
      },
      "backuprequest": {
        "required": [
          "vnf_uuid",
          "backup_name"
        ],
        "type": "object",
        "properties": {
          "vnf_uuid": {
            "type": "string",
            "example": "303c6928-f9de-4e4c-a563-4772775f2316"
          },
          "backup_name": {
            "type": "string",
            "example": "prior to production network reconfiguration"
          },
          "replace_backup_uuid": {
            "type": "string",
            "example": "736848ab-d4dc-4f94-a255-35f9984f8a42"
          }
        }
      },
      "topotagcreaterequest": {
        "type": "object",
        "properties": {
          "name": {
            "description": "A short human-friendly name which is visible in the UI to select the topology",
            "type": "string",
            "example": "Production WAN"
          },
          "description": {
            "description": "A longer human-friendly description which is visible in the UI when looking at the topology detail.",
            "type": "string",
            "example": "Main topology for WAN"
          }
        }
      },
      "rebootrequest": {
        "type": "object",
        "properties": {
          "reboot_type": {
            "type": "string",
            "example": "warm"
          }
        }
      },
      "regvportrequest": {
        "type": "object",
        "properties": {
          "vporttype": {
            "type": "string",
            "example": "vlan"
          },
          "endpointuuid": {
            "type": "string",
            "example": "9a04a7ff-0a82-4259-9f2b-c384b6d720ab"
          },
          "vportvalue": {
            "type": "object",
            "properties": {
              "vlan": {
                "$ref": "#/components/schemas/vlan"
              }
            }
          }
        }
      },
      "vportrequest": {
        "type": "object",
        "properties": {
          "customeruuid": {
            "type": "string",
            "example": "e58af39b-4a89-43f2-b1ed-1816b6d1f8e9",
            "description": "Unique identifier representing the customer who owns the VNF"
          },
          "vnfuuid": {
            "type": "string",
            "example": "9da748fb-9044-4909-95c2-4245d7592bec"
          },
          "management_port": {
            "type": "boolean"
          }
        }
      },
      "createcontractrequest": {
        "type": "object",
        "properties": {
          "duration": {
            "$ref": "#/components/schemas/duration.hrs"
          },
          "bandwidth": {
            "$ref": "#/components/schemas/bandwidth.mbs"
          },
          "renewal-option": {
            "$ref": "#/components/schemas/renewal-option"
          }
        }
      },
      "createlinkrequest": {
        "type": "object",
        "properties": {
          "billing-id": {
            "type": "string",
            "example": "684903a3-78b0-4005-9bfe-eece54ad42f3"
          },
          "description": {
            "description": "A human-friendly description which is visible in the UI when looking at the link detail.",
            "type": "string",
            "example": "\"Replication link between Equinix SYD and NextDC MEL\""
          },
          "connections": {
            "$ref": "#/components/schemas/connections"
          },
          "tag": {
            "type": "string",
            "example": "prod"
          },
          "latency": {
            "$ref": "#/components/schemas/latency"
          },
          "duration": {
            "$ref": "#/components/schemas/duration.mins"
          },
          "bandwidth": {
            "$ref": "#/components/schemas/bandwidth.kbs"
          },
          "renewal-option": {
            "$ref": "#/components/schemas/renewal-option"
          },
          "link-type": {
            "description": "1=VLAN, 2=VNF",
            "type": "integer",
            "example": 1
          },
          "topology_tag_uuid": {
            "type": "string",
            "example": "684903a3-78b0-4005-9bfe-eece54ad42f3"
          }
        }
      },
      "endpointupdaterequest": {
        "type": "object",
        "properties": {
          "name": {
            "description": "A short human-friendly name which is visible in the UI under the endpoint icon",
            "type": "string",
            "example": "My Endpoint"
          }
        }
      },
      "assigntopotagrequest": {
        "type": "object",
        "properties": {
          "topology_tag": {
            "type": "string",
            "example": "38d17228-0179-491f-9e84-36ddd078b8e6"
          }
        }
      },
      "regendpointrequest": {
        "type": "object",
        "properties": {
          "customeruuid": {
            "type": "string",
            "example": "6a607f5f-25c9-45a1-a960-a34245509208",
            "description": "Unique identifier representing the customer who will own the endpoint"
          },
          "datacenteruuid": {
            "type": "string",
            "example": "dc8f4a57-4621-4b17-87bd-e5a7e508bf7a",
            "description": "UUID of the data centre which the port is to be allocated from"
          },
          "interfacetype": {
            "type": "string",
            "example": "1000Base-TX"
          }
        }
      },
      "vnfendpointrequest": {
        "required": [
          "customeruuid",
          "imageuuid",
          "flavoruuid",
          "datacenteruuid",
          "config"
        ],
        "type": "object",
        "properties": {
          "customeruuid": {
            "type": "string",
            "example": "e66546d2-eb85-442f-a4d4-a4f6f4cee6bc",
            "description": "Unique identifier representing the customer who will own the endpoint"
          },
          "imageuuid": {
            "type": "integer",
            "example": 21
          },
          "flavoruuid": {
            "type": "integer",
            "example": 22
          },
          "datacenteruuid": {
            "type": "string",
            "example": "2e5ff2d1-da1b-4e5d-81a8-58f1780b1031",
            "description": "UUID of the data centre which the VNF is to be deployed in"
          },
          "config": {
            "description": "Boot configuration for VNF, base64 encoded",
            "type": "string"
          },
          "topology_tag_uuid": {
            "type": "string",
            "example": "f6c14b31-f29f-4be2-919b-5b7f463e4a3c"
          }
        }
      },
      "Error": {
        "type": "object",
        "properties": {
          "ns": {
            "type": "string",
            "xml": {
              "name": "xmlns:ams",
              "attribute": true
            }
          },
          "error-code": {
            "type": "integer",
            "example": 1010099,
            "xml": {
              "name": "ams:error-code"
            }
          },
          "error-message": {
            "type": "string",
            "xml": {
              "name": "ams:error-message"
            }
          },
          "error-auxiliary": {
            "type": "string",
            "xml": {
              "name": "ams:error-auxiliary"
            }
          }
        },
        "xml": {
          "name": "ams:error"
        }
      },
      "success-fragment": {
        "type": "object",
        "properties": {
          "success-auxiliary": {
            "type": "string",
            "example": "success"
          },
          "success-code": {
            "type": "integer",
            "example": 20000
          },
          "success-message": {
            "type": "string",
            "example": "vport created successfully"
          }
        }
      },
      "endpoint-port": {
        "type": "object",
        "properties": {
          "cabletype": {
            "description": "Physical cable medium required to connect to this port",
            "type": "string",
            "example": "SMF"
          },
          "connectortype": {
            "description": "Type of phyisical connector required to connect to this port",
            "type": "string",
            "example": "LC"
          },
          "cfa": {
            "description": "Customer Facility Assignment, or the reference ID for the physical port within the DC for interconnect",
            "type": "string",
            "example": "10GBase-LR SMF/LC"
          },
          "endpointuuid": {
            "description": "Unique identifier for this endpoint",
            "type": "string",
            "example": "8843fd24-4ef6-4a7d-8241-aed3778d6f07"
          },
          "interfacespeed": {
            "description": "Speed at which the physical interface operates",
            "type": "string",
            "example": "100"
          },
          "interfacetype": {
            "description": "Common name for the standard used on this port",
            "type": "string",
            "example": "10GBase-LR"
          },
          "vport": {
            "description": "List of Virtual Ports (or VLANs) which exist on this endpoint",
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/vport"
            }
          }
        }
      },
      "vport": {
        "type": "object",
        "properties": {
          "status": {
            "type": "string",
            "example": "deployed"
          },
          "bandwidth": {
            "type": "integer",
            "example": 0
          },
          "interfacesequence": {
            "type": "integer",
            "example": 0
          },
          "vporttype": {
            "type": "string",
            "example": "vlan"
          },
          "vportuuid": {
            "type": "string",
            "example": "e62716f5-cc68-40e7-b97e-f1e3d1bda9a8"
          },
          "vportvalue": {
            "type": "object",
            "properties": {
              "vlan": {
                "$ref": "#/components/schemas/vlan"
              }
            }
          }
        }
      },
      "connections": {
        "type": "array",
        "minItems": 2,
        "maxItems": 2,
        "example": [
          "5134d2ba-ce81-4b1c-9242-d2f30896c3a0",
          "e2f182ee-8c06-410d-b0fe-e21c9e7ee7bd"
        ],
        "items": {
          "type": "string",
          "description": "two vport uuids identified the endpoints of the link"
        }
      },
      "billing": {
        "type": "object",
        "properties": {
          "billingid": {
            "type": "string",
            "example": "dae2cb75-89f2-4b5b-be99-5cf3ae3faa20"
          },
          "billing-account": {
            "type": "string",
            "example": "60012345"
          }
        }
      },
      "role": {
        "type": "object",
        "properties": {
          "role-name": {
            "type": "string",
            "example": "customer-admin"
          },
          "permissions": {
            "type": "array",
            "items": {
              "type": "string",
              "example": "delete-user"
            }
          }
        }
      },
      "vnf-tag": {
        "type": "object",
        "properties": {
          "description": {
            "type": "string",
            "example": "UNIX"
          },
          "id": {
            "type": "integer",
            "example": 2
          },
          "name": {
            "type": "string",
            "example": "UNIX"
          }
        }
      },
      "vlan": {
        "required": [
          "id",
          "mac"
        ],
        "type": "object",
        "properties": {
          "id": {
            "type": "string",
            "example": "101"
          },
          "mac": {
            "type": "string",
            "example": ""
          }
        }
      },
      "topology": {
        "type": "object",
        "properties": {
          "uuid": {
            "type": "string",
            "example": "2056dbc6-fcad-4374-9dda-3bde3507b3b3"
          },
          "name": {
            "type": "string",
            "description": "A short human-friendly name which is visible in the UI to select the topology",
            "example": "Production WAN"
          },
          "description": {
            "type": "string",
            "description": "A longer human-friendly description which is visible in the UI when looking at the topology detail.",
            "example": "Main topology for WAN"
          },
          "status": {
            "type": "string",
            "example": "active"
          },
          "customer_uuid": {
            "type": "string",
            "example": "536b8cca-6594-46b9-8e62-1638e42deb6e"
          },
          "nsd_uuid": {
            "description": "no longer used",
            "type": "string",
            "example": ""
          },
          "guid_topology_id": {
            "type": "string",
            "example": ""
          },
          "created_by": {
            "type": "string",
            "example": "7a53ecf9-644a-4c92-8c63-eabc1ffe131f"
          },
          "creation_date": {
            "type": "integer",
            "example": 1508307028000
          },
          "deletion_date": {
            "type": "integer",
            "example": 0
          }
        }
      },
      "link": {
        "type": "object",
        "properties": {
          "description": {
            "type": "string",
            "example": "Main link from Melbourne to Sydney"
          },
          "latency": {
            "$ref": "#/components/schemas/latency"
          },
          "linkid": {
            "$ref": "#/components/schemas/linkid"
          },
          "contracts": {
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/contract"
            }
          },
          "tag": {
            "type": "string",
            "example": "prod"
          },
          "connections": {
            "$ref": "#/components/schemas/connections"
          },
          "type": {
            "type": "string",
            "example": "0"
          },
          "linkStatus": {
            "type": "integer",
            "example": 1
          },
          "billing-id": {
            "type": "string",
            "example": "b41c4ec8-1825-404b-b740-feaeba96681d"
          }
        }
      },
      "duration.mins": {
        "description": "Duration of contract in minutes",
        "type": "integer",
        "example": 3600,
        "minimum": 3600
      },
      "duration.hrs": {
        "description": "Duration of contract in hours (not minutes)",
        "type": "integer",
        "example": 1,
        "minimum": 1
      },
      "bandwidth.kbs": {
        "description": "Bandwidth in kB/s",
        "type": "integer",
        "example": 1000,
        "minimum": 1000,
        "maximum": 10000000
      },
      "bandwidth.mbs": {
        "description": "Bandwidth in MB/s (not kB/s)",
        "type": "integer",
        "example": 1,
        "minimum": 1,
        "maximum": 10000
      },
      "renewal-option": {
        "type": "integer",
        "description": "\"Renewal Option: 0=Auto Disconnect, 1=Auto Renew, 2=Pay per hour\"",
        "example": 0
      },
      "linkid": {
        "type": "string",
        "description": "Identifier of a link",
        "example": "f070660600c1c45f"
      },
      "contractid": {
        "type": "string",
        "description": "Identifier of a contract",
        "example": "f070660600c1c45f.2"
      },
      "latency": {
        "description": "Latency: 0=Low, 1=Standard, 2=Best Effort",
        "type": "integer",
        "example": 0,
        "pattern": "[012]"
      },
      "price": {
        "type": "integer",
        "example": 259
      },
      "contractStatus": {
        "type": "integer",
        "example": 8
      },
      "version": {
        "type": "integer",
        "example": 20431
      },
      "deletedtimestamp": {
        "type": "integer",
        "example": 0
      },
      "currencyCode": {
        "type": "string",
